vec3 solveQuadratic(in float a, in float b, in float c) {
    if (abs(a) < 1e-6) return vec3(-c / b, 0, 1);
    float h = b * b - 4.0 * a * c;
    if (h < 0.0) return vec3(0.0);
    return vec3((vec2(-1.0, 1.0) * sqrt(h) - b) * 0.5 / a, 2.0);
}

vec4 solveCubic(in float a, in float b, in float c, in float d) {
    if (abs(a) < 1e-9) return vec4(solveQuadratic(b, c, d), 0.0).xywz;
    float u = b / (3.0 * a);

    float p = (c - b * u) / a;
    float q = (d - (c - 2.0 * b * b / (9.0 * a)) * u) / a;
    if (abs(p) < 1e-3) return vec4(-sign(q) * pow(abs(q), 1.0 / 3.0) - u, 0, 0, 1);

    if (4.0 * p * p * p + 27.0 * q * q > 0.0) {
        float ip = 3.0 / abs(p), sp = sqrt(ip), k = 0.5 * q * ip * sp, x;
        if (p > 0.0) x = -2.0 / sp * sinh(asinh(k) / 3.0) - u;
        else x = -2.0 / sp * cosh(acosh(abs(k)) / 3.0) * sign(q) - u;
        return vec4(x, 0, 0, 1);
    }

    float m = sqrt(-p / 3.0);
    float x1 = -2.0 * m * sin(asin(1.5 * q / (p * m)) / 3.0);

    float h = sqrt(-3.0 * x1 * x1 - 4.0 * p);
    float x2 = 0.5 * (h - x1), x3 = 0.5 * (-h - x1);

    return vec4(x1 - u, x2 - u, x3 - u, 3);
}

float evalDist(in vec2 p, in float t, in float tmin, in float tmax, in bool limit, in vec2 a, in vec2 b, in vec2 c, in vec2 d) {
    if (limit) t = clamp(t, tmin, tmax);
    vec2 toCurve = p - (((a * t + b) * t + c) * t + d);
    return max(abs(toCurve.x), abs(toCurve.y));
}

float sdCubic(in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d, in float tmin, in float tmax, in bool limit) {
    // Solving for the global minimum of max(|a.x*t^3 + b.x*t^2 + c.x*t + d.x - p.x|, |a.y*t^3 + b.y*t^2 + c.y*t + d.y - p.y|)
    // Minima and maxima are either intersections of the two with different combinations of signs where the derivative jumps from one to the other
    // or the minima and maxima of one of the cubics (sign flip due to abs() does not change them)
    vec2 dp = d - p;

    // |a.x*t^3 + b.x*t^2 + c.x*t + d.x - p.x| = |a.y*t^3 + b.y*t^2 + c.y*t + d.y - p.y|
    // All combinations of signs on both sides degenerate into either +/+ or +/-
    vec4 minMax1 = solveCubic(a.x - a.y, b.x - b.y, c.x - c.y, dp.x - dp.y);
    vec4 minMax2 = solveCubic(a.x + a.y, b.x + b.y, c.x + c.y, dp.x + dp.y);

    // Minima and maxima of both cubics
    // Remember that they CAN have a global minimum because of abs() forcing them
    // to bounce back up when they go below the x axis
    vec3 minMax3 = solveQuadratic(3.0 * a.x, 2.0 * b.x, c.x);
    vec3 minMax4 = solveQuadratic(3.0 * a.y, 2.0 * b.y, c.y);

    // Find minimum
    float dist = evalDist(p, minMax1[0], tmin, tmax, limit, a, b, c, d);
    for (int n=1; n < int(minMax1.w); n++) dist = min(dist, evalDist(p, minMax1[n], tmin, tmax, limit, a, b, c, d));
    for (int n=0; n < int(minMax2.w); n++) dist = min(dist, evalDist(p, minMax2[n], tmin, tmax, limit, a, b, c, d));
    for (int n=0; n < int(minMax3.z); n++) dist = min(dist, evalDist(p, minMax3[n], tmin, tmax, limit, a, b, c, d));
    for (int n=0; n < int(minMax4.z); n++) dist = min(dist, evalDist(p, minMax4[n], tmin, tmax, limit, a, b, c, d));

    return dist;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    float time = iTime * 0.5;

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(2.0, 2.0 * sin(time)) * cos(time);

    float t1 = iTime * 0.5, t2 = iTime, t3 = iTime * 1.25;

    float c1 = cos(t1), s1 = sin(t1);
    float c2 = cos(t2), s2 = sin(t2);
    float c3 = cos(t3), s3 = sin(t3);

    vec2 v1 = vec2(c2, s3 * 1.5 - 0.25);
    vec2 v2 = vec2(-2.0, 0.5);
    vec2 v3 = vec2(2.0, 0.5);
    vec2 v4 = vec2(s2, c3 * 1.5 - 0.25);

    // To power basis
    vec2 a = v4 + 3.0 * (v2 - v3) - v1;
    vec2 b = 3.0 * (v1 - 2.0 * v2 + v3);
    vec2 c = 3.0 * (v2 - v1);
    vec2 d = v1;

    float dist = sdCubic(uv, a, b, c, d, 0.0, 1.0, true);

    // Nice colors from iq
    vec3 color = 1.0 - sign(dist) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(dist));
	color *= 0.8 + 0.2 * cos(140.0 * dist);
	color = mix(color, vec3(1.0), smoothstep(unit, 0.0, abs(dist)));

    // Control points
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - v1) - 0.05));
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - v4) - 0.05));

    float mDist = sdCubic(mouse, a, b, c, d, 0.0, 1.0, true);
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - mouse) - 0.03));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, abs(max(abs(uv.x - mouse.x), abs(uv.y - mouse.y)) - mDist) - 0.005));

    fragColor = vec4(color, 1.0);
}

//a bicomplex mandelbrot

vec4 square(vec4 B){
    //Bicomplex square
    return vec4(
         B.x* B.x- B.y* B.y- B.z* B.z+ B.w* B.w
        ,2.* B.x* B.y-2.* B.z* B.w
        ,2.* B.x* B.z-2.* B.y* B.w
        ,2.* B.x* B.w+2.* B.y* B.z
     );
}
float biComplexMB(in vec4 c, int itr){
    vec4 z=vec4(0);
    //ye old z=z^2+c
    for (int a=0;a<itr;a++){
        z=square(z);
        z=z+c;
        float sqrd = dot(z,z);
        if (sqrd>4.){
            return float(a)/float(itr);
        }

    }
    return 0.;

}
