#define STEPS 250.0
#define MDIST 550.0
#define pi 3.1415926

#define pmod(p,x) (mod(p,x) - (x)*0.5)

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float smin(float a, float b, float k){
  float h = max(0.0,k-abs(a-b));
  return min(a,b)-h*h*0.25/k;
}
float anim(){
  return sin(iTime); 
}
float rand(vec2 a){
  return fract(sin(dot(a,vec2(43.234,21.4343)))*94544.3434343)-0.5;
}
float ssRemap(float t, float s1, float s2, float c){
  return 0.5*(s2-s1)*(t-asin(cos(t*pi)/sqrt(c*c+1.0))/pi)*s1*t;
}
float wave(vec3 p, float t){
  float dist = length(p.xz)-fract(t+0.5)*50.0;
  dist = min(dist,0.0);
  float wave = sin(dist)*exp(-abs(length(dist*0.2)));
  wave*=max(0.,1.0-fract(t-0.5)*2.0);
  return wave;
}
float ball(vec3 p, float t){
  float mag = 80.0;
  p.y += (fract(t)-0.5)*mag;
  float a = length(p)-3.0;
  return a;
}

vec2 map(vec3 p){
  vec3 po = p;

  float t= mod(iTime,300.0)*0.6;
  vec2 a = vec2(1);
  vec2 b = vec2(2);

  //Bending
  p.y = pmod(p.y,60.0);

  float th = atan(p.x,p.z)/(2.0*pi)+0.5;
  th*=300.0;
  float r = length(p.xz)-60.0;
  p.x = r;
  //p.z = th;
  p.xy*=rot(sin(t));
  //p.xy*=rot(p.z*0.01*sin(t));
  p.xy = abs(p.xy)-5.0;
  p.xy*=rot(-pi/4.0);
  p.y=mix(p.y,ssRemap(p.y,0.01,0.4,0.3),0.5+0.5*anim());

  vec3 po2 = p;
  float count = 6.0;
  float wav =0.0;
  for(float i =0.0; i < count; i++){
    p = po2;
    t+=1.0/count;
    float mag = 10.0;
    p.x+=rand(vec2(floor(t),i))*mag;
    p.z+=(rand(vec2(floor(t),i*1.5)))*mag*30.0;
    a.x = smin(a.x,ball(p,t),0.5);
    wav +=wave(p,t)*1.5;
  }
  p = po2;
  t = mod(iTime,300.0)*4.0;
  wav+=sin(p.z+t)*0.1+sin(p.x+t)*0.1;
  wav+=sin(p.z*0.5-t)*0.2+sin(p.x*0.5-t)*0.2;

  //a.x = ball(p,t);
  b.x = p.y-wav;


  a=(a.x<b.x)?a:b;

  return a;
}

vec3 norm(vec3 p){
  vec2 e = vec2(0.01,0);
  return normalize(map(p).x-vec3(
  map(p-e.xyy).x,
  map(p-e.yxy).x,
  map(p-e.yyx).x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

  float t= mod(iTime,300.0);
  vec3 col = vec3(0.1);
  vec3 ro = vec3(0,70,-150);

  float drop = 0.0;
  drop = 0.0;
  ro.y-=drop;

  //ro.z*=sin(t);
  //ro.y += sin(t)*20.0;
  ro.xz*=rot(t*0.3);
  vec3 lk = vec3(0,0,0);

  lk.y-=drop;
  vec3 f = normalize(lk-ro);
  vec3 r = normalize(cross(vec3(0,1,0),f));
  vec3 rd = f*1.0+uv.x*r+uv.y*cross(f,r);

  vec3 p = ro;
  float dO, shad;
  vec2 d;

  for(float i = 0.0; i<STEPS; i++){
    p = ro+rd*dO;
    d = map(p);
    dO+=d.x*0.9;

    if(abs(d).x<0.01){
      shad = i/STEPS;
      break;
    }
    if(dO>MDIST){
      dO = MDIST;
      p = ro+rd*dO;
      d.y=0.0;
      break;
    }
  }
  shad = 1.0-shad;
  vec3 n = norm(p);
  vec3 ld = normalize(vec3(1,1,0));
  vec3 h = normalize(ld - rd);
  float spec = pow(max(dot(n,h),0.0),20.0);

  vec3 al = vec3(0);
  if(d.y==1.0) al = vec3(0,0.8,0.2);
  if(d.y==2.0) al = vec3(0,0,0.9)*1.5;
  p.y-=5.0;
  vec3 back = mix(vec3(0.1,0.1,0.5),vec3(0.0,0.4,0.6),clamp(p.y*0.05,0.0,1.0))*0.9;
  //back = vec3(0);

  col = shad*al;
  col+=spec*0.5;

  col = mix(col,back*2.0,(dO/MDIST));

  fragColor = vec4(col,0);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1


float noise(in vec2 uv)
{
    return sin(uv.x)+cos(uv.y);
}

#define OCTAVES 8
float fbm(in vec2 uv)
{
    //this function generates the terrain height
    uv /= 1.0;
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.8;

    for (int i = 0; i < OCTAVES; i++)
    {
        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = max(value,value+(.25-(noise(uv * freq)) * amplitude));

        amplitude *= .3;

        freq *= 1.3+value/2.;

        uv = uv.yx;
    }

    return (value-2.8)/4.0;
}



struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 3.0;
    return fbm(uv/factor)*factor;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 0.05;
    #endif

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

#define pi 3.141592653589793

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = 2.*(fragCoord-iResolution.xy*.5)/iResolution.y;
    vec3 col, col1, col2;

        col1 = vec3(cos(pi*uv.x*8.-iTime)); //Initial waves
        vec2 a = vec2(uv.x,uv.y-0.5);
        vec2 b = vec2(uv.x,uv.y+0.5);
        float c = dot(a,a);
        float d = dot(b,b);
        col2 = vec3(cos(pi*sqrt(c)*8.-iTime)*exp(-c*.4)+cos(pi*sqrt(d)*8.-iTime)*exp(-d*.4))*.5; //Interference

    col = mix(col1,col2,smoothstep(0.,1.,uv.x*15.)); //Interpolate
    col *= min(smoothstep(0.,1.,abs(uv.x)*16.)/smoothstep(0.,1.,abs(abs(uv.y)-0.5)*20.),1.); //Barrier

    col = col*col; //PDF

    col = vec3((-cos(pi*(col.r))/2.+.5),col.r,(1.-(acos(2.*col.r-1.))/pi)); //color

    fragColor = vec4(col,1.0);
}

