// 1D hash for 1D input by David Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash(in float p) {
    p = fract(p * 0.011);
    p *= p + 7.5;
    p *= p + p;
    return fract(p);
}

// 1D hash for 2D input by David Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash21(in vec2 p) {
    vec3 p3 = fract(vec3(p.xyx) * 0.13);
    p3 += dot(p3, p3.yzx + 3.333);
    return fract((p3.x + p3.y) * p3.z);
}

// 2D value noise by Morgan McGuire
// https://www.shadertoy.com/view/4dS3Wd
float vnoise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    // Four corners in 2D of a tile
    float a = hash21(i);
    float b = hash21(i + vec2(1.0, 0.0));
    float c = hash21(i + vec2(0.0, 1.0));
    float d = hash21(i + vec2(1.0, 1.0));

    // Smooth interpolation (smoothstep without clamping)
    vec2 u = f*f * (3.0 - 2.0*f);

    // Mix 4 coorners
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

// 2D Fractional Brownian motion based on value noise by Morgan McGuire
// https://www.shadertoy.com/view/4dS3Wd
float vfbm(in vec2 p) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.5));
    const int NOCTAVES = 5;
    for (int i = 0; i < NOCTAVES; ++i) {
        v += a * vnoise(p);
        p = rot * p * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

float sdOrientedBox(in vec2 p, in vec2 a, in vec2 b, float th) {
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

float time() {
    return 1.5 * iTime;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = fragCoord.xy / iResolution.xy;
    float ar = iResolution.x / iResolution.y;
    p.x *= ar;

    vec3 col = vec3(0.9);

    const int N = 40;
    const float INV_N = 1.0 / float(N);
    for (int i = 0; i < N; ++i) {
        // Stripe fraction
        float fi = float(i) * INV_N;

        // Stripe-dependent random numbers
        float r1 = hash(13.0 * float(i));
        float r2 = hash(37.0 * float(i));

        // Stripe-dependent random direction
        float dir = sign(r1 - 0.5);

        // Modulate horizontal offset per stripe
        float off = 4.0 * fi;

        // Modulate sine amplitude per stripe (stronger emphasis near the bottom)
        float amp = 0.025 * pow(1.0 - fi, 3.0);

        // Modulate stripe using offset and amplitude
        vec2 q = vec2(p.x + off, p.y + amp * sin(10.0 * p.x + 3.5));

        // Stripe start and end points
        vec2 start = vec2(off, -INV_N + (1.0 + INV_N) * fi);
        vec2 end = vec2(off + ar, -INV_N + (1.0 + INV_N) * fi);

        // Animated stripe thickness with random left/right direction
        float th = 0.025 + 0.0075 * sin(10.0 * q.x - 11.0 + dir * time());

        // Stripe SDF as oriented box
        float d = sdOrientedBox(q, start, end, th);

        // Modulate stripe color (gradually darker from horizon to top/bottom)
        const float HORIZ = 0.6;
        float low = clamp(
            (fi > HORIZ) ? 0.15 + pow(1.0 - fi, 0.2) : pow(0.3 + fi, 3.0),
            0.0, 1.0);
        float c = low + 0.8 * (1.0 - low) * r2;

        // Add noise (gradually stronger near bottom)
        float inten = 0.3 + pow(1.0 - fi, 1.5);
        c += -0.5 * inten + inten * vfbm(750.0 * q + dir * time());
        c = clamp(c, 0.0, 1.0);

        // Mix in stripe color
        col = mix(col, vec3(c), smoothstep(fwidth(d), 0.0, d));
    }

    fragColor = vec4(col, 1.0);
}

