// Try clicking with the mouse to see the weights interpolate it
// back to its original position (purple is the original position,
// green is the interpolated position, both should align)

// Hue to RGB function from Fabrice's shadertoyunofficial blog:
#define hue2rgb(hue) (0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0)))

// Number of vertices
#define N 6

// Trigless version
void polyLerpWeightsTrigless(in vec2 p, in vec2[N] poly, out float[N] weights) {
    float norm = 0.0;
    for(int n = 0; n < N; n++) {
        vec2 a = poly[(n - 1 + N) % N] - p;
        vec2 b = poly[n] - p;
        vec2 c = poly[(n + 1) % N] - p;

        float d = length(b);
        float ab = dot(a, b), bc = dot(b, c), ad = length(a) * d, dc = d * length(c);
        float u = sqrt(max(0.0, (ad - ab) / (ad + ab))) * sign(a.x * b.y - a.y * b.x);
        float v = sqrt(max(0.0, (dc - bc) / (dc + bc))) * sign(b.x * c.y - b.y * c.x);

        weights[n] = (u + v) / d;
        norm += weights[n];
    }

    for(int n = 0; n < N; n++) weights[n] /= norm;
}

// Original (slightly modified to match the interface of the trigless one)
float cr2(vec2 A, vec2 B) {
    return A.x*B.y-A.y*B.x;
}

float getAngle(vec2 A, vec2 B) {
    return atan(cr2(A, B), dot(A, B));
}

void polyLerpWeightsOriginal(in vec2 p, in vec2[N] poly, out float[N] weights) {
    vec2 vi, vi_min1, vi_plus1;
    float sumweights = 0.0;
    float ang1, ang2;
    float t1, t2;
    vec2 vi_p;

    for(int i = 0; i < N; i++) {
        vi = poly[i];
        vi_min1 = poly[(i-1 + N) % N];
        vi_plus1 = poly[(i+1) % N];
        vi_p = normalize(vi-p);


        //ang1 = acos(dot(normalize(vi_min1-p), vi_p));
        //ang2 = acos(dot(normalize(vi_plus1-p), vi_p));
        ang1 = getAngle(normalize(vi_min1-p), vi_p);
        ang2 = getAngle(vi_p, normalize(vi_plus1-p));


        t1 = tan(ang1*0.5);
        t2 = tan(ang2*0.5);
        weights[i] = (t1 + t2) / distance(vi, p);
        sumweights += weights[i];
    }

    //normalize
    float recp = 1.0/sumweights;
    for(int i = 0; i < N; i++) {
        weights[i] *= recp;
    }
}

// Simple point in polygon test extracted from @iq's polygon
// distance shader: https://www.shadertoy.com/view/wdBXRW
bool pointInPolygon(in vec2 p, in vec2[N] v) {
    bool res = false;
    for (int i=0, j=N - 1; i < N; j = i, i++) {
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];

        // Winding number from http://geomalgorithms.com/a03-_inclusion.html
        bvec3 c = bvec3(p.y >= v[i].y, p.y < v[j].y, e.x * w.y > e.y * w.x);
        if (all(c) || all(not(c))) res = !res;  
    }

    return res;
}

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    float time = 0.5 * iTime;
    vec3 color = vec3(1.0);

    // Create polygon
    float t1 = 0.5 * time, t2 = 1.25 * time, t3 = 1.5 * time;
    float c1 = 0.4 * cos(t1), s1 = 0.4 * sin(t1);
    float c2 = 0.4 * cos(t2), s2 = 0.4 * sin(t2);
    float c3 = 0.4 * cos(t3), s3 = 0.4 * sin(t3);

    vec2[N] poly;
    poly[0] = vec2(1.0, 0.0);
    poly[1] = vec2(0.5, sqrt(0.75));
    poly[2] = vec2(-0.5, poly[1].y);
    poly[3] = vec2(-1.0, 0.0);
    poly[4] = vec2(-0.5, -poly[1].y);
    poly[5] = vec2(0.5, -poly[1].y);

    // Distort the vertices
    for (int n=0; n < N; n++) {
        float id = float(n + 3);
        float freq = Hash11(id * 393.84) * 10.0 - 5.0;
        float phase = Hash11(id * 183.37);
        float amp = Hash11(id * 275.35) * 2.0;
        float ang = iTime * freq + phase;
        poly[n] += vec2(cos(ang), sin(ang)) * amp;
        poly[n] *= 0.25;
    }

    if (pointInPolygon(uv, poly)) {
        // Compute interpolation weights
        float[N] weights1, weights2;
        polyLerpWeightsOriginal(uv, poly, weights1);
        polyLerpWeightsTrigless(uv, poly, weights2);

        // Interpolate colors from vertices
        vec3 polyLerp = vec3(0.0);
        float side = step(0.0, uv.x); // Which side of the screen (left: 0, right: 1)
        for (int n=0; n < N; n++) polyLerp += hue2rgb(float(n) / float(N)) * mix(weights1[n], weights2[n], side);
        color = polyLerp;
    }

    // Get distance to edges
    float d = 1000000.0;
    for (int n=0; n < N; n++) d = min(d, sdLine(uv, poly[n], poly[(n + 1) % N]));

    color = mix(color, vec3(0.0), smoothstep(unit, 0.0, d));

    if (iMouse.z > 0.0) {
        // Compute weights for the mouse position
        float[N] weights;
        polyLerpWeightsTrigless(mouse, poly, weights);

        // Exact mouse position (to test)
        float distMouse = length(uv - mouse) - 0.015;
        color = mix(color, vec3(0.6, 0.0, 0.8), smoothstep(unit, 0.0, distMouse));
        color = mix(color, vec3(0.0), smoothstep(unit, 0.0, abs(distMouse))); // Outline

        // Test to see if it interpolates back to its original position
        vec2 mouseLerp = vec2(0.0);
        for (int n=0; n < N; n++) mouseLerp += poly[n] * weights[n];

        float distMouseLerp = length(uv - mouseLerp) - 0.015;
        color = mix(color, vec3(0.0, 0.8, 0.6), smoothstep(unit, 0.0, distMouseLerp));
        color = mix(color, vec3(0.0), smoothstep(unit, 0.0, abs(distMouseLerp))); // Outline
    }

    fragColor = vec4(color, 1.0);
}

#define MAX_STEPS 100.0
#define EPS 0.001

mat2 rot(float a){
	float s=sin(a);
	float c=cos(a);
	return mat2(c,s,-s,c);
}

float sdSphere(vec3 p){
	return length(p)-1.0;
}

float sdCube(vec3 p){
	float r=0.7;
	vec3 q=abs(p)-r;
	return length(max(q,0.0))+min(max(q.x,max(q.y,q.z)),0.0);
}

float mainDist(vec3 p){

	float s=sdSphere(p);
	float c=sdCube(p);
	return mix(s,c,cos(iTime*2.0)*0.5+0.5);
}

vec3 GetRayDir(vec2 uv,vec3 ro){
	vec3 f=normalize(vec3(0.0)-ro),
	r=normalize(cross(vec3(0.0,1.0,0.0),f)),
	u=cross(f,r),
	c=ro+f,
	i=c+uv.x*r+uv.y*u,
	rd=normalize(i-ro);
	return rd;
}

vec3 WorldToCube(vec3 p){
	vec3 ap=abs(p);
	vec3 sp=sign(p);
	float m=max(ap.x,max(ap.y,ap.z));
	vec3 st;
	if(m==ap.x)
		st=vec3(p.zy,1.0*sp.x);
	else if(m==ap.y)
		st=vec3(p.zx,2.0*sp.y);
	else
		st=vec3(p.xy,3.0*sp.z);
	st.x/=m;
	st.xy*=(1.45109572583-0.451095725826*abs(st.xy));
	return st;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

	vec3 ro=vec3(0.0,0.0,-2.5);
	vec3 rd=GetRayDir(uv,ro);

	float dO=0.0,dS;
	vec3 p;

	for(float i=0.0;i<MAX_STEPS;i++){

		p=ro+rd*dO;
		dS=mainDist(p);
		dS=min(dS,-(length(p)-4.0));
		if(dS<EPS)break;
		dO+=dS;
	}

	vec3 col=WorldToCube(p);


	fragColor=vec4(col,1.0);
}

