// https://twitter.com/kamoshika_vrc/status/1362976717350924290

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 rayPos = vec3(0, 0, 2);
