}



// a Light is defined by a location and a color
struct Light {
    vec3 location;
    vec3 color;
    float radius;
};

// Sphere is defined by a center and radius and material: color
struct Sphere {
	float radius;
	vec3 center;
    vec3 color;
};

// Ray is define by an origin point and a direction vector
struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Intersection {
    int obj;
    float t;
};


const int NUM_LIGHTS = 1;
