const int special_sphere = NUM_SPHERE - 1;
const int light_sphere = NUM_SPHERE;

// Intersection code for Ray-Sphere    
float raySphereIntersect(in Ray ray, in Sphere sphere) {

    vec3 rayToSphere = ray.origin - sphere.center;
    float b = dot(rayToSphere, ray.direction);
    float c = dot(rayToSphere, rayToSphere) - (sphere.radius * sphere.radius);
	float disc = b*b - c;
    float t;
    if (disc > 0.0) {
        t = -b - sqrt(disc);
        if (t > 0.00001) {
            return t;
        }
        t = -b + sqrt(disc);
        if (t > 0.00001) {
            return t;
        }  
    }
    return MAXX;
}


// Checks if the Ray origin is inside the sphere, and returns time when it intersects boundary.
float raySpecialSphereIntersect(in Ray ray, in Sphere sphere) {
    vec3 rayToSphere = ray.origin - sphere.center;

    float dist_from_center = dot(rayToSphere, rayToSphere);
    float radius_squared = sphere.radius * sphere.radius;

    if(dist_from_center <= radius_squared)
        return sqrt( dist_from_center + radius_squared);
    else
        return MAXX;
}

// Traverses the entire scene and 
// returns the objectID and the intersection point
Intersection intersectAllObjects(Ray ray, bool shadow) {
    float minT = MAXX;
    int iSphere = -1;

    for (int i=0; i < NUM_SPHERE+NUM_LIGHTS; i++) {
       Sphere sphere = spheres[i];


       float t=MAXX;
       if(i==special_sphere)
           {
            if(!shadow)
               t = raySpecialSphereIntersect(ray, sphere);
           }
       else {
           if(!shadow || (i != light_sphere) )
               t = raySphereIntersect(ray, sphere);
       }
       if (t < minT && t >= 0.001) {
           // keep track of the closest sphere and intersection
           iSphere = i;
           minT = t;
       }
   }

   return Intersection(iSphere, minT);
}

