// samples per pixel
const int numSamples=10;

// convert directionToLight and directionToView to tangent space
//
void convertToTangentSpace(vec3 toLight, vec3 toView, vec3 hitPoint, out vec3 toLightTS, out vec3 toViewTS, out vec3 nTS)
{
    // use a matrix to convert
    vec3 t = normalize(dFdx(hitPoint));
    vec3 b = normalize(dFdy(hitPoint));
    vec3 n = normalize(cross(t, b));
    mat3 xformMatrix = transpose(mat3(t, b, n));

    toLightTS = xformMatrix * toLight;
    toViewTS = xformMatrix * toView;
    nTS = n;
}

float checkLightVisibility(in Light light, vec3 hitPoint, vec3 hitNormal) {
    float visible = 0.;
    vec3 lightDir = normalize(light.location - hitPoint);
    Ray ray;
    ray.origin = hitPoint+lightDir*0.01;
    ray.direction = lightDir;
    Intersection intersection = intersectAllObjects(ray, true);
    int iSphere = intersection.obj;
    if (iSphere == -1 || iSphere == light_sphere) { 
        // no object was hit, light is visible
        visible = 1.0;
    } 
    return visible;
}

/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

   float screenDepth = -2.0;

   float width = iResolution.x;
   float height = iResolution.y; 

   vec3 mouse_input = vec3( iMouse.z > 0. ? 4.*(iMouse.x/width - 0.5) : 0.,
                            0.,
                            iMouse.z > 0. ? 3.*(iMouse.y/height - 0.5) : 0.);
   makeScene(iFrame, mouse_input);





   vec3 samp = vec3(0, 0, 0);
   seed = 0.0;
   for (int i=0; i<numSamples; i++) {
       float x = fragCoord.x + random() - 0.5;
       float y = fragCoord.y + random() - 0.5;


       // map (0.5, w-0.5) to (-1, 1)
       // and (0.5, h-0.5) to (-1, 1)
       x = (x/width)*2.0 - 1.0;
       y = (y/height)*2.0 - 1.0;

       // account for the non-square window
       float aspectRatio = width/height;
       y = y/aspectRatio;


       vec3 rayOrigin = vec3(0.0, 0.0, 0.0);
       // normalized ray direction
       vec3 rayDirection = normalize(vec3(x, y, screenDepth));

       Ray ray = Ray(rayOrigin, rayDirection);

       // traverse the scene (all spheres) and find the 
       // closest intersected object and intersection point
       bool check_for_shadows = false;
       Intersection intersection = intersectAllObjects(ray, check_for_shadows);

       int iSphere = intersection.obj;
       float minT = intersection.t;

       if(iSphere == -1){
           samp += bgColor(ray.direction);
           continue;
       }

       Sphere sphere = spheres[iSphere];


       vec3 hit = ray.origin + minT*ray.direction;
       vec3 hitPointNormal = normalize(hit-sphere.center);
       if(iSphere == special_sphere) hitPointNormal *= -1.;


       //If we hit the special sphere, we keep bouncing;
       int NUM_BOUNCES = 100;
       //To have correct "shadows" in the outer sphere, we have to remember the first ray hit
       bool first_hit_special_sphere = iSphere == special_sphere;
       vec3 first_hit = hit;
       vec3 first_normal = hitPointNormal;

       if(iSphere == special_sphere){
           for(int k=0; k<NUM_BOUNCES; ++k){
               vec3 reflDir = ray.direction - 2.0*hitPointNormal*dot(ray.direction, hitPointNormal);

               ray.origin = hit;
               ray.direction = reflDir;

               intersection = intersectAllObjects(ray, false);
               iSphere = intersection.obj;
               if(iSphere == -1) break;

               minT = intersection.t;
               sphere = spheres[iSphere];

               hit = ray.origin + minT*ray.direction;
               hitPointNormal = normalize(hit-sphere.center);

               if(iSphere != special_sphere) break;
               else hitPointNormal *= -1.;
       }}

       if (iSphere > -1 && iSphere < NUM_SPHERE) { // if there is an intersection

           // to get around iSphere not being constant
           /* for (int i=0; i<NUM_SPHERE+NUM_LIGHTS; i++) {
               if (i==iSphere) {
                   sphere = spheres[i];
                   break;
               }
           }
           */


           // vector from intersection to light
           vec3 hitPointToLight = normalize(lights[0].location-hit);
           vec3 hitPointToView = ray.origin-hit;

           vec3 toViewTS;
           vec3 toLightTS;
           vec3 nTS;
           convertToTangentSpace(hitPointToLight, hitPointToView, hit, toLightTS, toViewTS, nTS); 
           vec3 h_ts 		= normalize(toLightTS + toViewTS);
           float dot_nl 	= clamp(toLightTS.z, 0.0, 1.0);
           float dot_nv 	= clamp(toViewTS.z, 0.0, 1.0);
           float dot_nh	= clamp(h_ts.z, 0.0, 1.0);
           float dot_lh 	= clamp(dot(toLightTS, h_ts), 0.0, 1.0); // same as dot(v_ts, h_ts)
           float dot_ht	= h_ts.x;
           float dot_hb	= h_ts.y;
           Material m = materials[iSphere];
           vec3 wi;
           float lightPdf;

           float lightVisible;
           if(!first_hit_special_sphere)
               lightVisible = checkLightVisibility(lights[0], hit, hitPointNormal);
           else
               lightVisible = checkLightVisibility(lights[0], first_hit, first_normal);

           vec3 brdf = BRDF(toLightTS, toViewTS, nTS, vec3(0.0, 0.0, 0.0), vec3(0.0, 0.0, 0.0), m);

           vec3 col = brdf*dot_nl*lightVisible;
           vec3 toneMappedColor = col * (1.0 / (col + 1.0));
           float gamma = 1.0/2.2;
           vec3 finalColor = vec3(pow(toneMappedColor.x, gamma), 
                                  pow(toneMappedColor.y, gamma), 
                                  pow(toneMappedColor.z, gamma));

           samp += finalColor; 
       } 
       else if(iSphere == light_sphere) {
           samp += lights[0].color;
       }
       else{
           samp += bgColor(ray.direction);
       }

   }
   // average all the samples per pixel
   fragColor = vec4(samp/float(numSamples), 1.0);

}

#define ITERS 12
const float scale = 2.;
const float scale1 = 1.38;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    float s1 = (uv.y+iTime/4.)/4.0;
    uv.y -= s1;
    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {
            uv= (uv.yx+vec2(uv.x/scale+uv.y/scale1,-uv.y/scale+uv.x/scale1)/scale)/scale1;
            uv *= -scale1;
            uv = fract((uv.yx+s1)/scale1);
            //uv.y /= -scale1;
        }
        col[2] = (uv.y-uv.x);
        col = (abs(col-col_prev.yzx))/1.125;
	}
    fragColor = vec4(vec3(col),1.0);

}

vec3 cam(vec3 ro, vec3 rd, vec2 uv) {
  vec3 f = normalize(rd - ro);
  vec3 l = normalize(cross(vec3(0,1,0), f));
  vec3 u = normalize(cross(f, l));

  return normalize(f + l * uv.x + u * uv.y);
}

float ni(float a){
    //return texture(texFFTIntegrated, a).x;
    return iTime;
}


void mo(inout vec2 p,vec2 d){p = abs(p)-d;if(p.y>p.x)p=p.yx;}
vec3 rep(vec3 p, vec3 c) { return mod(p + .5 * c, c) - .5 * c; }
vec3 repl(vec3 p,vec3 c,vec3 l){return p-c*clamp(round(p/c),-l,l);}
mat2 rot(float t) { return mat2(cos(t), sin(t), -sin(t), cos(t)); }
float sdBox(vec3 p, vec3 b) { vec3 q = abs(p) - b; return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.); }
float sdSphere(vec3 p, float r) { return length(p) - r; }
float sdPlane(vec3 p, vec3 n, float h) { return dot(p,n) + h; }
float rnd(float p) { p = fract(p * .131); p *= p + 333.33; return fract(2. * p * p); }
float dirlight(vec3 n, vec3 ld) {
  ld = normalize(ld);
  return clamp(max(dot(n, ld), 0.), 0., 1.);
 }
float spec(vec3 p, vec3 n, vec3 ld, float k) {
    ld = normalize(ld);
    vec3 r = reflect(-ld, n);
    vec3 v = normalize(-p);
    return pow(max(dot(r,n), 0.), k);
}
vec3 palette(float t) {
    vec3 a= vec3(1);
    vec3 b= vec3(1);
    vec3 c = vec3(2);
    vec3 d = vec3(0,.1, .2);
    return a + b * cos(6.28318 * (c * t + d));
}

vec2 ground(vec3 p) {
    float plane = sdPlane(p, vec3(0,1,0), 0.);
    float cracks = texture(iChannel0, p.xz * 0.05).r;
    float crack = 0.28;

    return vec2(
    plane + texture(iChannel1, p.xz * .7).r * 0.03
      + texture(iChannel0, p.zx * 0.1).r * 1.
      + texture(iChannel0, p.xz * 0.01).r * 4.,

       cracks > crack && cracks < crack + 0.01 ? 4 : 0
    );
}


float bid;
float tunnel(vec3 p) {
    vec3 bp = p;
    bp.xy *= rot(bp.z * 0.1);
    mo(bp.xy, vec2(0.5));
    mo(bp.zy, vec2(0.5));
    bid = rnd(round(p.z + 0.5) * 120.);
    float b = sdBox(rep(bp + vec3(-1.5, 0, 0), vec3(0, 0, 2)), vec3(0.1, 1.5, 0.1));
    return b;
}


float sid;
vec2 sphrs(vec3 p) {
  p = p + vec3(0,0,-iTime - 7.);
  p.xz *= rot(iTime);
  p.yz *= rot(iTime);
  sid = rnd(round(p.x - 0.75) * 10. + round(p.y - 0.75) * 20. +  round(p.z - 0.75) * 10.);
  p = repl(p, vec3(.5), vec3(2));
  float s = sdSphere(p + vec3(sin(iTime * 10. + p.x / 10.) * 0.05), .2 - abs(sin(sid * 60. + iTime)) * .15);
  return vec2(s, fract(sid + iTime) < 0.9 ? 2 : 3);
}

vec2 map(vec3 p) {
    float m = 999.;
    float mm = 0.;

    vec2 g = ground(p);
    if (g.x < m) { m = g.x; mm = g.y; }

    float t = tunnel(p);
    if (t < m) { m = t; mm = 1.; }

    vec2 s = sphrs(p);
    if (s.x < m) { m = s.x; mm = s.y; }

    return vec2(m, mm);
}
vec3 norm(vec3 p) {
    float E = 0.01; vec2 k = vec2(1, -1);

    return normalize(
      k.xyy * map(p + k.xyy * E).x +
      k.yyx * map(p + k.yyx * E).x +
      k.yxy * map(p + k.yxy * E).x +
      k.xxx * map(p + k.xxx * E).x 
    );
}

vec3 bg(vec2 uv) {
    float h = uv.y;
    float n = texture(iChannel0, vec2(uv.x, uv.y - iTime * 0.05)).x;
    float s = texture(iChannel0, uv * 6. + vec2(sin(iTime) + iTime, cos(iTime))).x > 0.45 ? 1. : 0.;

    return vec3(n * h*2. * palette(uv.y / 2.1));
}


vec3 ld = vec3(0, 30, 10);
vec4 tr(vec3 ro, vec3 rd, vec2 uv) {
    float td = 1.;
    vec2 h;
    vec4 c = vec4(0);
    vec4 g = vec4(0);
    int bnc = 0;
    float en = 1.;

    for (int i = 0; i < 1000; i++) {
        vec3 ip = ro + rd * td;
        h = map(ip);
        td += h.x * 0.3;

        if (h.y == 1.) {
            g += vec4(palette(bid * (ni(0.15) * 1. + iTime / 2.)) * exp(-h.x * 10.) * .07, 1.);
        }

        if (h.y == 3.) {
          g += vec4(palette(sid / 10.) * exp(-h.x * 10.) * .07, 1.);
        }
        if (h.y == 4.) {
          g += vec4(palette(0.63 + sin(ip.x / 10. + iTime * 0.2)) * exp(-h.x * 10.) * (.5 + abs(sin(ip.x + ip.z + iTime)) * .5), 1.);
        }


        if (h.x < 0.01) {
          vec3 inorm = norm(ip);

          if (h.y == 0.) {
              c += vec4(dirlight(inorm, ld) * palette(ip.y * 0.2 + 0.45) * en * 0.7 + spec(ip, inorm, ld, 20.), 1.) * en;
           }

           if (h.y == 1.) {
                c += vec4(palette(bid * (ni(0.15) * 1. + iTime / 2.)) * en, 1.);
           }

           if (h.y == 2.) {
             c += vec4(0.2) * en;
             ro = ip;
             rd = reflect(rd, inorm);
             td = .1;
             bnc += 1;
             en = max(en - .7, 0.);
           }

           if (h.y == 3.) {
               c += vec4(palette(sid / 10.) * en, 1.);
            }

            if (h.y == 4.) {
                c += vec4(0.9);
            }
        }

        if (td > 200.) {
            return vec4(bg(uv) + vec3(g) * exp(-td * 0.05), td/1000.);
        }

        if (c.a >= 1. || bnc > 1 || en < 0. || td > 1000.) break;
    }

    return vec4((vec3(c) + vec3(g)) * exp(-td * 0.05), td/1000.);
}

