/*
This shader generates test bars for calibrating your monitor against the sRGB
standard and also serves as a torture-test for monitor response time.

To use, click the "Go Fullscreen" button at the bottom right of the shader and
view the monitor from 3-5 feet away.

In a "perfect" result the color blocks will appear flat and motionless.
In a "decent" result the blocks will appear to have a faint wave pattern.
In a "bad" result will have very bright or dark waves.

A monitor can be perfectly color-calibrated and still display waves in this test
- the test is _very_ picky about the monitor is displaying _exactly_ what the
GPU is sending it.

The following things will all cause the waves to be more visible:

- Non-native resolution
- Display scaling not at 100%
- Non-sRGB color space
- Compressed color signals like YUV422, YUV420 YCbCr422, YCbCr420, etcetera.
- "Limited" dynamic range color output
- Too-high or too-low contrast, brightness, or gamma
- Very high refresh rates
- "Fastest" monitor overdrive modes
- GSYNC or FreeSync turned on (in some cases, not all)
- Sharpening filters
- Black stabilizer filters
- Inter-pixel interference inside the panel itself (mostly on VA panels)

*/

float gamma(float x) {
  x = clamp(x, 0.0, 1.0);
  // srgb standard
  x = (x < 0.0031308) ? 12.92 * x : (1.0 + 0.055) * pow(x, 1.0 / 2.4) - 0.055;

  // gamma 2.2
  //x = pow(x, 1.0 / 2.2);
  return x;
}

float bit(float x, int bit) {
    x = fract(x * exp2(float(-bit - 1)));
    return x < 0.5 ? 0.0 : 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Divide the screen into 7 vertical stripes and make each one a different color.
    float barX = floor(7.0 * fragCoord.x / iResolution.x) + 1.0;
    float r = bit(barX, 0);
    float g = bit(barX, 1);
    float b = bit(barX, 2);
    vec4 barColor = vec4(r, g, b, 1.0);

    // Make each horizontal region 1/2 the brightness of the one above it.
    int barY = int(8.0 * fragCoord.y / iResolution.y);
    barColor *= exp2(float(barY - 8));

    // Make a moving horizontal wave pattern with a bit of a flat top and bottom.
    float waveX = sin((iTime * 2.7 + 15.0 * fragCoord.x / iResolution.x) * 2.0 * 3.14159);
    waveX *= 1.05;
    waveX = clamp(waveX * 0.5 + 0.5, 0.0, 1.0);

    // Interpolate between the pure color and a "checkerboard" version of the color
    // based on the wave pattern.
    float checker = (bit(fragCoord.x, 0) + bit(fragCoord.y, 0)) == 1.0 ? 0.0 : 1.0;

    // Invert the checkerboard every other frame - this will cause the monitor's
    // response time to have a huge effect on the wave visibility.
    if ((iFrame & 1) == 0) checker = 1.0 - checker;
    checker = checker * 2.0 - 1.0;

    fragColor = mix(barColor, barColor + barColor * float(checker), waveX);

    // Convert from linear color to inverse-gamma-corrected color.
    // When displayed on a calibrated monitor, this will turn back into linear color.
    fragColor.r = gamma(fragColor.r);
    fragColor.g = gamma(fragColor.g);
    fragColor.b = gamma(fragColor.b);

}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash21(in vec2 p) {
	vec3 p3 = fract(p.xyx * vec3(1.12031, 1.103, 1.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy).x;
}

vec2 sdTruchet(in vec2 p) {
    vec2 cId = floor(p);
    vec2 cUv = fract(p) - 0.6;

    cUv.x *= sign(Hash21(cId) - 0.5);
    cUv -= cUv.x < -cUv.y ? -0.5 : 0.5;

    float t = atan(cUv.y, cUv.x);
    t *= sign(mod(cId.x + cId.y, 1.0) - 0.5);

    return vec2(abs(length(cUv) - 0.9), t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.7 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0 + 0.25 * iTime;

    vec2 truchet = sdTruchet(uv);
    float d = truchet.x - 0.05;

    // Nice colors from me
    vec3 color = 12.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 0.5 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.3 * cos(140.0 * d);
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d) * 0.6));

    if (d < 0.02) {
        truchet.y -= 0.6 * iTime;
        float fade = 0.5 + 0.5 * sin(iTime);
        float vertStripes = abs(mod(d + 0.0333, 0.0666) - 0.0333);
        float horiStripes = abs(mod(truchet.y + 0.1, 0.2) - 0.1) * 0.5;
        color += fade * smoothstep(0.033, 0.0, min(vertStripes, horiStripes));
    }

    fragColor = vec4(color, 1.0);
}

