// Distance to a capsule in the L-infinity metric space. Compare
// to euclidean: https://www.shadertoy.com/view/3tdSDj
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH

// -------------------------------
// THIS CODE IS VERY INNEFICIENT, RIGHT NOW IT'S DOING A BRUTE
// FORCE COMPUTATION. SOME OTHER DAY I'll GIVE IT A TRY
// -------------------------------


float sdCapsule( in vec2 p, vec2 va, vec2 vb, float rb )
{
    // recenter primitive
    p -= (vb+va)*0.5;
    const float k = sqrt(0.5);
    float l = length(vb-va);
    vec2 u = (vb-va)/l;
    vec2 v = vec2(-u.y,u.x);
    l *= 0.5;
    vec2 a = u*l;
    vec2 w = v*rb;

    // distance to body sides
    float ss = (u.x*u.y>0.0)?1.0:-1.0;
    float de = 1.0/(a.y+ss*a.x);
    vec2 w1 = p-a-w;
    vec2 w2 = p-a+w;
    vec2 q1 = abs(w1+a*clamp(-(w1.y+ss*w1.x)*de, 0.0, 2.0));
    vec2 q2 = abs(w2+a*clamp(-(w2.y+ss*w2.x)*de, 0.0, 2.0));
    float d1 = max(q1.x,q1.y);
    float d2 = max(q2.x,q2.y);
    float d = min(d1,d2);

    // inside circular caps
    vec2 pa = p - a;
    vec2 pb = p + a;
    float da = dot(pa,pa);
    float db = dot(pb,pb);
    if( min(da,db)<rb*rb )
    {
        float s = 1.0; if( db<da ) {pa=pb;s=-1.0;}
        float b1 = 0.5*(pa.x+pa.y);
        float b2 = 0.5*(pa.x-pa.y);
        float c = dot(pa,pa) - rb*rb;
        vec2  t1 = vec2(-b1,b1)+sqrt(b1*b1-c*0.5);
        vec2  t2 = vec2(-b2,b2)+sqrt(b2*b2-c*0.5);
        // 4 solution. Up to 3 can be valid
        if( s*dot(pa+vec2( 1, 1)*t1.x,-u)<0.0 ) d=min(d,t1.x);
        if( s*dot(pa+vec2(-1,-1)*t1.y,-u)<0.0 ) d=min(d,t1.y);
        if( s*dot(pa+vec2( 1,-1)*t2.x,-u)<0.0 ) d=min(d,t2.x);
        if( s*dot(pa+vec2(-1, 1)*t2.y,-u)<0.0 ) d=min(d,t2.y);
        d = -d;
    }
    // outside circular caps
    else 
    {
        vec2  qa = abs(mat2(u.x,-u.y,u.y,u.x)*p) - vec2(l,rb);
        float di = max(qa.x, qa.y);
        // outside circular caps AND outside of body
        if( di>0.0 )
        {
            float dc;

            // first cap
            pa = abs(pa);
            if( abs(pa.y-pa.x)<rb )
            {
                float b = 0.5*(pa.x+pa.y);
                float c = dot(pa,pa) - rb*rb;
                dc = b - sqrt(b*b-c*0.5);
            }
            else
            {
                dc = max(pa.x,pa.y)-rb;
            }
            d = min(d,dc);

            // sadly, we still need to test the second cap
            pb = abs(pb);
            if( abs(pb.y-pb.x)<rb )
            {
                float b = 0.5*(pb.x+pb.y);
                float c = dot(pb,pb) - rb*rb;
                dc = b - sqrt(b*b-c*0.5);
            }
            else
            {
                dc = max(pb.x,pb.y)-rb;
            }            
            d = min(d,dc);
        }
        d *= sign(di);
    }

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    vec2 m = (2.0*iMouse.xy - iResolution.xy) / iResolution.y;
    p *= 1.9;
    m *= 1.9;

    if( iMouse.x<0.01 ) m=sin(-0.17*iTime*vec2(1.1,1.3)+vec2(0,2));

    vec2  v1 = cos( iTime*0.5 + vec2(0.0,1.00) + 0.0 );
	vec2  v2 = cos( iTime*0.5 + vec2(0.0,3.00) + 1.5 );
    float ra = 0.3*(0.5+0.5*cos(iTime*1.1+1.0));

    float d = sdCapsule(p, v1, v2, ra);

    vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(80.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    d = sdCapsule(m, v1, v2, ra);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, abs(max(abs(p.x-m.x), abs(p.y-m.y)) - abs(d))));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, length(p-m)-0.015));

	fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float one;
    one  = cos(iTime * 1.000 + distance(uv, vec2(0.0, 0.0)) * 8.0) * 0.5 + 0.5;
    one += cos(iTime * 0.666 + distance(uv, vec2(1.0, 0.0)) * 8.0) * 0.5 + 0.5;
    one += cos(iTime * 0.777 + distance(uv, vec2(0.5, 1.3)) * 8.0) * 0.5 + 0.5;
    one = one / 3.0;

    // Output to screen
    fragColor = vec4(one, one, one, 1.0);
}

#define ITERS 12
const float scale = .95;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    float s1 = (uv.y+iTime/16.)/8.;
    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {
            uv = fract(uv.yx);
            uv = (uv+vec2(s1-uv.x+uv.y)/scale);
            //uv.x = scale;
        }
        col[2] = abs(uv.y-uv.x);
        col = ((col+col_prev.yzx))/1.75;
	}
    fragColor = vec4(vec3(col),1.0);

}

#define MAXX 10000000.0

float seed = 0.0;

mat3 zRotation(float angle) {
    return mat3(cos(angle),  sin(angle), 0,
                -sin(angle), cos(angle), 0,
                0,           0,          1);
}

mat3 rotAxis(vec3 axis, float angle) {
//http://www.neilmendoza.com/glsl-rotation-about-an-arbitrary-axis/
    axis = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float oc = 1.0 - c;

    return mat3(oc * axis.x * axis.x + c,           oc * axis.x * axis.y - axis.z * s,  oc * axis.z * axis.x + axis.y * s,
                oc * axis.x * axis.y + axis.z * s,  oc * axis.y * axis.y + c,           oc * axis.y * axis.z - axis.x * s,
                oc * axis.z * axis.x - axis.y * s,  oc * axis.y * axis.z + axis.x * s,  oc * axis.z * axis.z + c);
}

vec3 tranformAroundCenter(mat3 transform, vec3 center, vec3 point) {
    return center + transform * (point - center);
}

vec3 rotAxisCenter(vec3 axis, float angle, vec3 center, vec3 point) {
    return tranformAroundCenter(rotAxis(axis,angle),center, point);
}


vec3 bgColor(vec3 rayDir) {
    float u =  0.5*(1.0 + rayDir[1]);
    //return vec3(0.05, 0.05, 0.2);
    return u*vec3(0.7, 0.8, 0.9) + (1.0-u)*vec3(0.05, 0.05, 0.2);
}

// random number between 0 and 1
float random() {
    return fract(sin(seed++)*43758.5453123);
}

// a Light is defined by a location and a color
struct Light {
    vec3 location;
    vec3 color;
};

// Sphere is defined by a center and radius and material: color
struct Sphere {
	float radius;
	vec3 center;
    vec3 color;
};

// Ray is define by an origin point and a direction vector
struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Intersection {
    int obj;
    float t;
};

const int NUM_SPHERE = 4;
Sphere spheres[NUM_SPHERE];
Light  lights[1];

// Intersection code for Ray-Sphere    
float raySphereIntersect(in Ray ray, in Sphere sphere) {

    vec3 rayToSphere = ray.origin - sphere.center;
    float b = dot(rayToSphere, ray.direction);
    float c = dot(rayToSphere, rayToSphere) - (sphere.radius * sphere.radius);
	float disc = b*b - c;
    float t;
    if (disc > 0.0) {
        t = -b - sqrt(disc);
        if (t > 0.00001) {
            return t;
        }
        t = -b + sqrt(disc);
        if (t > 0.00001) {
            return t;
        }  
    }
    return MAXX;
}

// Traverses the entire scene and 
// returns the objectID and the intersection point
Intersection intersectAllObjects(Ray ray) {
    float minT = MAXX;
    int iSphere = -1;

    for (int i=0; i < NUM_SPHERE; i++) {
       Sphere sphere = spheres[i];

       float t = raySphereIntersect(ray, sphere);

       if (t < minT && t >= 0.001) {
           // keep track of the closest sphere and intersection
           iSphere = i;
           minT = t;
       }
   }

   return Intersection(iSphere, minT);
}


// create 4 spheres at different locations in different colors
void makeScene(int iFrame) {
    spheres[0] = Sphere(1.0, vec3(0.2, 0, -7), vec3(1, 1, 0));
    spheres[1] = Sphere(0.3, vec3(-0.4, -0.5, -2.5), vec3(1, 0, 0));
    spheres[2] = Sphere(0.2, vec3(0, 0, -2), vec3(0, 1, 0));
    spheres[3] = Sphere(0.8, vec3(1., 1, -5.5), vec3(0, 0, 1));
    lights[0] = Light(vec3(0.0, 0.0, 0.0), vec3(1, 1, 1));

    float f = float(iFrame);
    vec3 axis=vec3(0,0,1);
    vec3 axis3=vec3(0,0,1);
    float w1=0.01;
    float w2=w1*1.1;
    float w3=w1*1.3;
    spheres[1].center = rotAxisCenter(axis, w1*f, spheres[0].center, spheres[1].center);
    spheres[2].center = rotAxisCenter(axis, w2*f, spheres[0].center, spheres[2].center);
    spheres[3].center = rotAxisCenter(axis3, w3*f, spheres[2].center, spheres[3].center);
}

// samples per pixel
const int numSamples=4;



/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

   // fragCoord ranges from 
   //   in x: 0.5 to iResolution.x-0.5
   //   in y: 0.5 to iResolution.y-0.5
   // pixel (0,0) is at the bottom left corner

   makeScene(iFrame);

   //vec3 rayOrigin = vec3(0.0, 0.0, 0.0);

   float screenDepth = -2.0;

   float width = iResolution.x;
   float height = iResolution.y; 

   vec3 samp = vec3(0, 0, 0);
   seed = 0.0;
   for (int i=0; i<1*numSamples; i++) {
       float x = fragCoord.x + random() - 0.5;
       float y = fragCoord.y + random() - 0.5;


       // map (0.5, w-0.5) to (-1, 1)
       // and (0.5, h-0.5) to (-1, 1)
       x = (x/width)*2.0 - 1.0;
       y = (y/height)*2.0 - 1.0;

       // account for the non-square window
       float aspectRatio = width/height;
       y = y/aspectRatio;

       // ray Origin for is at (0, 0, 0)
       vec3 rayOrigin = vec3(0.0, 0.0, 0.0);
       // normalized ray direction
       vec3 rayDirection = normalize(vec3(x, y, screenDepth));

       Ray ray = Ray(rayOrigin, rayDirection);
       vec3 col = vec3(1.0, 1.0, 1.0);
       for (int k=0; k<100; k++) { // 100 bounces

       // traverse the scene (all spheres) and find the 
       // closest intersected object and intersection point
       Intersection intersection = intersectAllObjects(ray);

       int iSphere = intersection.obj;
       float minT = intersection.t;

       Sphere sphere;

       if (iSphere > -1) { 
           // to get around iSphere not being constant
           for (int i=0; i<NUM_SPHERE; i++) {
               if (i==iSphere) {
                   sphere = spheres[i];
                   break;
               }
           }

           // hit coordinates
           vec3 hit = ray.origin + minT*ray.direction;
           // normal at the point of ray-sphere intersection
           vec3 hitPointNormal = normalize(hit-sphere.center);
           vec3 xnorm = hitPointNormal;
           vec3 reflDir = ray.direction - 2.0*xnorm*dot(ray.direction, xnorm);
           ray.origin = hit;
           ray.direction = reflDir;
           /****
           // vector from eye to the intersection point
           vec3 hitPointToEye = normalize(vec3(0, 0, 0)-hit);
           // cosine of the angle between ray and nornal
           float angle = dot(hitPointNormal, hitPointToEye);
           // use the cosine of the angle to modulate color for 
           // a simple diffuse shading effect
           samp = samp + angle*sphere.color;
           ***/
           //samp = sphere.color;
           col = col*(0.4*sphere.color+0.9*vec3(1,1,1)); 
       } 
       else {
           samp = samp + col*bgColor(ray.direction);
           break;
       }
   }
   }
   // average all the samples per pixel
   fragColor = vec4(samp/float(numSamples), 1.0);

}

