#define lofi(i,j) (floor((i)/(j))*(j))
#define fs(i) (fract(sin((i)*114.514)*1919.810))

const float PI=3.14159265;
const float EPSILON=1E-3;
const float TAU=PI*2.;
const float FAR=1E2;

float time;
float seed;

float random(){
  seed++;
  return fs(seed);
}

mat3 orthBas(vec3 z){
  z=normalize(z);
  vec3 up=abs(z.y)>.99?vec3(0,0,1):vec3(0,1,0);
  vec3 x=normalize(cross(up,z));
  return mat3(x,cross(z,x),z);
}

vec3 randomSphere(){
  float phi=TAU*random();
  float theta=acos(2.0*random()-1.);
  return vec3(cos(phi)*sin(theta),sin(phi)*sin(theta),cos(theta));
}

vec3 randomLambert(vec3 n){
  float phi=TAU*random();
  float cosTheta=sqrt(random());
  float sinTheta=sqrt(1.0-cosTheta*cosTheta);
  return orthBas(n)*vec3(cos(phi)*sinTheta,sin(phi)*sinTheta,cosTheta);
}

float quadTree(vec2 p){
  float mul=0.5;
  for(int i=0;i<4;i++){
    vec2 pt=lofi(p,mul);
    float s=dot(vec2(11.2,22.3),pt);
    s=fs(s)-0.1;
    if(s>mul){
      return mul;
    }
    mul*=0.5;
  }
  return mul;
}

float isectPlane(vec3 ro,vec3 rd,vec3 n){
  float d=dot(ro,n)/dot(-rd,n);
  return d<0.?FAR:d;
}

float drawPattern(vec2 p){
  float hcell=lofi(p.x,0.5);
  float phase=time+hcell;
  p.y+=(cos(phase)+phase)/PI*cos(hcell*PI*2.0);
  float q=quadTree(p);
  vec2 cell=lofi(p,q);

  p/=q;
  p=fract(p);
  p-=0.5;
  p/=1.0-0.01/q;

  float r1=fs(dot(cell,vec2(12.3,44.5)));
  float r2=fs(dot(cell,vec2(33.3,55.5)));

  float sub=0.002/q;

  float ptn=1.0;

  if(r1<.3){
    ptn*=smoothstep(0.5,0.5-sub,length(p));
    ptn*=smoothstep(0.5-3.*sub,0.5-2.*sub,length(p));
  }else if(r1<.6){
    p=abs(p);
    ptn*=smoothstep(0.5,0.5-sub,max(p.x,p.y));
    ptn*=smoothstep(0.5-3.*sub,0.5-2.*sub,max(p.x,p.y));
  }else if(r1<.8){
    p=abs(p);
    ptn*=smoothstep(0.5,0.5-sub,max(p.x,p.y));
    ptn*=smoothstep(2.1*sub,1.4*sub,abs(p.x-p.y));
  }else{
    ptn*=0.0;
  }

  float haha=0.5+0.5*sin(cell.x/4.0-2.0*time+4.0*r2);
  ptn*=0.01+0.99*pow(haha,3.);

  return ptn;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv=fragCoord.xy/iResolution.xy;
  vec2 p=uv*2.-1.;
  p.x*=iResolution.x/iResolution.y;

  time=iTime;

  seed=texture(iChannel0,8.0*uv).x;
  seed+=fract(time);

  vec3 col=vec3(0);
  vec3 colRem=vec3(1.);
  vec3 ro0=vec3(0,1,2);

  vec3 rd0=orthBas(vec3(0.05*sin(time)+0.5*sin(time/7.),0.05*cos(time),1.))*normalize(vec3(p,-1));

  for(int iS=0;iS<100;iS++){
    colRem=vec3(1.);
    vec3 ro=ro0;
    vec3 rd=rd0;
    rd.xy+=(vec2(random(),random())-.5)/iResolution.y*2.0;
    vec3 fp=ro+mix(2.0,4.0,smoothstep(-0.5,0.5,sin(time/1.2)))*rd;
    ro.xy+=0.05*randomSphere().xy;
    rd=normalize(fp-ro);

    for(int iR=0;iR<4;iR++){
      float dWall=isectPlane(ro,rd,vec3(0,0,1));
      float dFloor=isectPlane(ro,rd,vec3(0,1,0));
      float d=min(dWall,dFloor);

      if(d<FAR){
        if(dWall<dFloor){
          vec3 n=vec3(0,0,1);
          ro=ro+rd*d+n*EPSILON;
          rd=randomLambert(n);
          float pattern=drawPattern(ro.xy);
          col+=colRem*10.0*pattern;
        }else{
          vec3 n=vec3(0,1,0);
          ro=ro+rd*d+n*EPSILON;
          if(random()<0.04){
            rd=reflect(rd,normalize(n+0.05*random()*randomSphere()));
          }else{
            rd=randomLambert(n);
          }
        }
      }
    }
  }

  col/=100.;
  col=pow(col,vec3(.4545));
  col*=1.0-0.2*length(p);
  col=vec3(
    smoothstep(0.2,0.8,col.x),
    smoothstep(0.0,1.0,col.y),
    smoothstep(-0.2,1.2,col.z)
  );

  fragColor = vec4(col,1);
}

#define SC (250.0)

vec2 noise(in vec2 uv)
{
    return vec2(sin(uv.x),cos(uv.y));
}

#define OCTAVES 8
float fbm(in vec2 uv)
{
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.8;

    for (int i = 0; i < OCTAVES; i++)
    {
        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        vec2 n1 = noise(uv * freq);
        value += (.25-abs(n1.x+n1.y-.3) * amplitude);

        amplitude *= .37;

        freq *= 2.05;

        uv += uv.yx/16.0;
        uv += (n1.yx)/freq;
        uv = uv.yx;
    }

    return value;
}

float f(in vec3 p)
{

    float h = fbm(p.xz);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
}

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(-.8, .15, -.3));

    vec3 camStep = vec3(lightDir.x, 0., lightDir.z) * iTime;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;
    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));
    rd.yx *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
    rd.xz *= rot(3.14-iMouse.x/iResolution.x*3.14*2.0);
    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

float DistancePointToLine(vec2 p, vec2 A, vec2 B)
{
        float l2 = distance(A, B);
        l2 *= l2;
        if (l2 == 0.0f) 
            return distance(p, A);  

        float t = max(0.0f, min(1.0f, dot(p - A, B - A) / l2));
        vec2 projection = A + t * (B - A); 
        return distance(p, projection);
}

