    vec3 col;
    float dx = 0.1;
    for (float x = -1.5f-dx; x<=1.5f+dx; x+=dx){
        vec2 lineA_1 = vec2(x, cos((x+iTime)*4.0f)/5.0f);
        vec2 lineB_1 = vec2(x+dx, sin((x+iTime+dx)*3.0f)/5.0f);
        float p = DistancePointToLine(uv, lineA_1, lineB_1);

        col += vec3(0.01f/p)*vec3(abs((cos(iTime)+1.0)/5.0f),0.2f,0.7f);
    }


    fragColor = vec4(col,1.0);
}

