     p.xz = mat2(cs, si, -si, cs)*p.xz;

     th = -0.6;
     cs = cos(th), si = sin(th);
     p.xy = mat2(cs, si, -si, cs)*p.xy;

     th = 1.5;
     cs = cos(th), si = sin(th);
     p.yz = mat2(cs, si, -si, cs)*p.yz;

     // an is like how long the torus is out of pi = 100% , or something        
     an = 2.;
     //c is how that is applied to the torus 
     c = vec2(sin(an),cos(an));
     // make torus (position, length, outer radius, inner radius
     tmp = sdCappedTorus(p, c, .29, 0.06);
     //add it in
     alle = min(alle, tmp);


     //Tail (h)whip
     //translate pos
     p=pos+vec3(-0.35,1.1,.3);
     th = 3.8;
     cs = cos(th), si = sin(th);
     p.xz = mat2(cs, si, -si, cs)*p.xz;

     th = 0.;
