     c = vec2(sin(an),cos(an));
     // make torus (position, length, outer radius, inner radius
     tmp = sdCappedTorus(p, c, .25, 0.03);
     //add it in
     alle = min(alle, tmp);

     pos.y+=0.05;
     //Upper Body
     //translate pos
     p=pos+vec3(-0.5,0.3,.27);
     th = 1.2;
     cs = cos(th), si = sin(th);
     p.xz = mat2(cs, si, -si, cs)*p.xz;

     th = 3.;
     cs = cos(th), si = sin(th);
     p.xy = mat2(cs, si, -si, cs)*p.xy;

     th = -0.1;
     cs = cos(th), si = sin(th);
     p.yz = mat2(cs, si, -si, cs)*p.yz;

     // an is like how long the torus is out of pi = 100% , or something        
     an = 2.4;
     //c is how that is applied to the torus 
     c = vec2(sin(an),cos(an));
     // make torus (position, length, outer radius, inner radius
     tmp = sdCappedTorus(p, c, .19, 0.1);
     //add it in
     alle = min(alle, tmp);



     //Neck Head
     //translate pos
     p=pos+vec3(-0.5, -0.03, .53);
     th = 1.6;
     cs = cos(th), si = sin(th);
     p.xz = mat2(cs, si, -si, cs)*p.xz;

     th = -.7;
     cs = cos(th), si = sin(th);
     p.xy = mat2(cs, si, -si, cs)*p.xy;

     th = .3;
     cs = cos(th), si = sin(th);
     p.yz = mat2(cs, si, -si, cs)*p.yz;

     // an is like how long the torus is out of pi = 100% , or something        
     an = 1.2;
     //c is how that is applied to the torus 
     c = vec2(sin(an),cos(an));
     // make torus (position, length, outer radius, inner radius
     tmp = sdCappedTorus(p, c, .2, 0.12);
     //add it in
     alle = smin(alle, tmp, 0.07);


     pos*=0.9;
     th = .0;
     cs = cos(th), si = sin(th);
     pos.yz = mat2(cs, si, -si, cs)*pos.yz;

     //shere for head
     p=pos+vec3(-0.47, -0.17, .52);

     th = -.2;
     cs = cos(th), si = sin(th);
     p.xz = mat2(cs, si, -si, cs)*p.xz;
     tmp = length(p)-0.25;
     alle = smin(alle, tmp,0.03);


     //shaping the head with minus sheres
     p=pos+vec3(-0.5, -0.1, .59);

     th = -.1;
     cs = cos(th), si = sin(th);
     p.xz = mat2(cs, si, -si, cs)*p.xz;
     p.x = abs(p.x)-0.13;
     tmp = length(p)-0.218;
     alle = smax(alle, -tmp, 0.05);

     p=pos+vec3(-0.5, -0.28, .7);
     tmp = length(p)-0.03;
     alle = smin(alle, tmp, 0.05);


     //shaping the head with minus sheres
     p=pos+vec3(-0.5, -0.1, .5);

     th = -.1;
     cs = cos(th), si = sin(th);
     p.xz = mat2(cs, si, -si, cs)*p.xz;
     tmp = length(p)-0.2;
     alle = smax(alle, -tmp, 0.05);

    //jaw
    p=pos;
    th = -.14;
    p.y+=sin(p.z*30.-.6)/40.;
    cs = cos(th), si = sin(th);
    p.xz = mat2(cs, si, -si, cs)*p.xz;
    tmp = knochen(p, vec3(0.4, 0.26, -.65), vec3(0.08, 0.01, 0.14), 
                -1.2, -0.35, -0., 0., 0.0);
    alle = smin(alle, tmp, 0.1);

    //teeth / zhane
    //upper
    p=pos;
    th = -.17;
    cs = cos(th), si = sin(th);
    p.xz = mat2(cs, si, -si, cs)*p.xz;

    tmp = knochen(p, vec3(0.38, 0.22, -.78), vec3(0.005, 0.04, 0.005), 
                -0., -0., -0., 0.04, 0.0);
    alle = smin(alle, tmp, 0.01);
    //lower
    p=pos;
    th = -.17;
    cs = cos(th), si = sin(th);
    p.xz = mat2(cs, si, -si, cs)*p.xz;
    tmp = knochen(p, vec3(0.38, 0.22, -.7), vec3(0.005, 0.05, 0.005), 
                -0., -0., -0., 0.04, 0.0);
    alle = smin(alle, tmp, 0.01);

    return alle;
}
