void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
   // p: position on the ray
   // d: direction of the ray
   vec3 p = vec3(0.,0.,2.);
   vec3 d = vec3((gl_FragCoord.xy/(0.5*iResolution.xy)-1.)*vec2(iResolution.x/iResolution.y,1.0), 0.) - p;
   d = normalize(d); 
   vec2 uv = (gl_FragCoord.xy-iResolution.xy*0.5)/iResolution.y;
   // ld, td: local, total density 
   // w: weighting factor
   float ld=0., td=0.;
   float w=0.;

   // total color
   vec3 tc = vec3(0.);

   // i: 0 <= i <= 1.
   // r: length of the ray
   // l: distance function
   float r=0., l=0., b=0.;

   // rm loop
   for (float i=0.; (i<1.); i+=1./64.) {
       //break if you either
       //get through all the loop calls, the total density is 95%  or more Y
       // or both
       //don't break only if both conditions are not met Y
	   if(!((i<1.) && (td < .95)))
		   break;
      // evaluate distance function
       //l for length to point

       //why take only 50% of the length?Y
      l = map(p) * 0.55;


      const float h = .025;

       // check whether we are close enough (step)
      // compute local density and weighting factor
       //if distance to point "l" is less than h, (step(h, l) would be the opposite Y
       //h - l meaning difference between threshold and length
       //that equals the "local density"
       // otherwise local density = 0
       //local density is relative to the threshhold 0.05.
       //l will always be less than h, based on the step condition,

      ld = (h - l) * step(l, h);

       //total density
       //density will be like how dark solid(colored in) the cloud is Y
       //td will always be less than 0.95 based on the break condition
      w = (1. - td) * ld;   

      //accumulate color and density
      tc += w; // * hsv(w, 1., 1.); // * hsv(w*3.-0.5, 1.-w*20., 1.); 
      td += w;

      td += 1./200.;

      // enforce minimum stepsize
      l = max(l, 0.003);

      // step forward
      p += l*d;
      r += l;
   }  


   vec3 lookAt = vec3(.0, -1.0, .0);

    vec3 campos = lookAt + vec3(0., .0, -4.);



   // lookAt.xy += path(lookAt.z);
   // campos.xy += path(campos.z);

   // light1.xy += path(light1.z);
	//light2.xy += path(light2.z);
    //I've ommitted adding path to the lights because it seems redundant
    //although it does make a difference somehow.

    float FOV = 3.14/3.;//is a bit more than one so multiplying this by right and up in ray
    //direction increases those vector components (x and y) so it kinda stretches the screen
    //so you can see more.
    vec3 forward = normalize(lookAt - campos);
    vec3 right = normalize(vec3(forward.z, 0.0, 0.0));//don't really need -forward.x?
    							//pretty cool using forward.z as the x component in this vector
    vec3 up = cross(forward, right);


    vec3 rd = normalize(forward + FOV*right*uv.x + FOV*up*uv.y);
  //  rd.xy*=rot(iTime);
    //rd.yz*=rot(-1.8);


    float t = 0.0, dt;
    for(int i = 0;i<128; i++)
    {
        dt = augenMap(campos + rd*t,td);
    if(dt<0.005 || t > 150.){break;}

    t += dt*0.75;
    }

   #ifdef SCATTERING
   // simple scattering approximation
   tc *= 1. / exp( ld * 0.4 ) * 1.5;
   #endif


   vec2 st = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
   vec3 sky = vec3(.0);
   sky = mix(vec3(0.,0.1,0.0),sky, uv.y);
   sky += vec3(step(0.99,hash(fragCoord/10.0)));

   st.x/=2.;
   st.x+=iTime/16.;
   vec3 skyGrad = mix(vec3(0., 0., 0.2),vec3(0.2,0.7,0.3),uv.y*2.);
   float cloud = 0.04+fbm(st*4.+fbm(st*2.)/2.)*(noise(st));
   sky = mix(sky, vec3(6.0)+skyGrad, clamp(cloud,0., 1. ));


   //sky += fpn(uv.xyx+iTime*10.);
   vec3 col  = mix(tc*vec3(0.3,0.7,0.37)+0.2 + glow/800. + glow2, sky, 1.0-tc.x);
   //gamma

   col = pow(col,vec3(1.5));

   uv.x*=0.95;
   uv.y*=1.2;
   float baumen = 1.0-smoothstep(0.8, 0.85, length(uv)-fbm(vec2(atan(uv.y/uv.x)*9.,length(uv))+sin(iTime/5.))/5.);
   col *= baumen;
   //vignette
    uv = fragCoord/iResolution.xy;
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * 15.0; // multiply with sth for intensity

    vig = pow(vig, 0.15); // change pow for modifying the extend of the  vignette

    fragColor = vec4(col*vig,1.0);
}

#define SC (250.0)

float noise(in vec2 uv)
{
    uv.x -= iTime/2.0;
    return sin(uv.x);
}

#define OCTAVES 4
float fbm(in vec2 uv)
{
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.8;

    for (int i = 0; i < OCTAVES; i++)
    {
        // value += noise(uv * freq) * amplitude;

        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value += (.25-abs(noise(uv * freq)-.3) * amplitude);

        amplitude *= .37;

        freq *= 3.+1./3.;

        uv += uv.yx/10.0;
        //uv = uv.yx;
    }

    return value;
}

float f(in vec3 p)
{

    float h = fbm(p.xz);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(-.8, .15, -.3));

    vec3 camStep = vec3(lightDir.x, 0., lightDir.z);
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;
    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));

    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

