vec3 ponerBorde(vec3 pColObj, vec3 colOut, float distObj )
{
  colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj),0.0));
  return colOut;
}

float f(vec2 x)                                 
{
    float r = length(x);
    float a = atan(x.y,x.x);
    return r - 1.0 + 0.5*sin(3.0*a+2.0*r*r);
}                                                   

vec2 grad( vec2 x )												 
{
    float r = length(x);
    float a = atan(x.y,x.x);
    vec2 da = vec2(x.y,-x.x)/(r*r);
    return (x/r) + (1.5*da+2.0*x)*cos(3.0*a+2.0*r*r);
}


float colorIsolinea( in vec2 x )
{
    float v = f( x );
    vec2  g = grad( x );
    float de = abs(v)/length(g);
    float eps =1.0; /*size_of_a_pixel*/;
    return smoothstep( 1.0*eps, 2.0*eps, de );
}

