    vec3 rd = normalize(vec3(uv.x,uv.y,1));
    float d = RayMarch(ro,rd); // Distance
    d/= 10.;
    vec3 color = vec3(d);

    // Set the output color
    fragColor = vec4(color,1.0);
}

#define MAX_STEPS 100

float max_distance = 30.0;
float epsilon = 0.015;

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float sdSphere (vec3 p, float r) {
    return length(p)-r;
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float molar(vec3 p, vec3 pos) {
    p = p-pos;

    float main = sdSphere(p, 1.25);

    float cusp1 = sdEllipsoid(p-vec3(.39, .39, .69), vec3(.6, .9, .53));
    float cusp2 = sdEllipsoid(p-vec3(-.59, .39, -.69), vec3(.6, .9, .53));
    float cusp3 = sdEllipsoid(p-vec3(-.59, .39, .69), vec3(.6, .9, .53));
    float cusp4 = sdEllipsoid(p-vec3(.19, .39, .69), vec3(.6, .9, .53));

    float d = main;
    d = smin(d, cusp1, 0.2);
    d = smin(d, cusp2, 0.2);
    d = smin(d, cusp3, 0.2);
    d = smin(d, cusp4, 0.2);
    return d;
}

float de(vec3 p)
{
    return molar(p, vec3(0.));
}

float march(vec3 start_pos, vec3 ray_dir)
{
	float d = 0.0;

	for (int i = 0; i < MAX_STEPS; i++)
	{
		vec3 ray_pos = start_pos + ray_dir*d;
		float s = de(ray_pos);

		d += s;
		if (s < epsilon)
			return d;	
	}

	return max_distance;
}

vec3 getNormal(vec3 point)
{
  float d0 = de(point);
  float dX = de(point - vec3(epsilon, 0.0, 0.0));
  float dY = de(point - vec3(0.0, epsilon, 0.0));
  float dZ = de(point - vec3(0.0, 0.0, epsilon));

  vec3 r = vec3(dX-d0, dY-d0, dZ-d0);
  return normalize(r);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
