    float f = 20.;
    float h = .8;

    angle = -(k/f)*cos(f*iTime)+h*iTime;

    float ca = cos(angle);
    float sa = sin(angle);

    mat2 rot = mat2(ca, -sa, sa, ca);

	vec3 eye_pos = vec3(0.0, 0.0, 3.75);
    eye_pos.xz = rot*eye_pos.xz;

	vec3 ray_dir = vec3(uv, -1.);
