	uv=spin(uv,iTime);

    vec3 color = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

	float s=0.1;
	for(int i=0;i<5;i++){
		uv=abs(uv)-s;
		uv=spin(uv,cos(iTime));
	}

    vec3 col = vec3(0.);

    col += vec3(0.03/abs(uv.x) + abs(uv.y));
    col *= color;

    fragColor = vec4(col,1.0);
}

vec2 field(vec2 uv) {
    float y = uv.y;
    float x = uv.x;
    float v1 = pow(y, 3.) - 9.*y; // x component of vector field
    float v2 = pow(x, 3.) - 9.*x; // y component of vector field
    return vec2(v1, v2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col;


    // BACKGROUND
    vec2 v = field(uv);

    // -- Take derivatives with respect to x and y
    float dv1 = dFdy(v.x);
    float dv2 = dFdx(v.y);

    // -- calculate '2d curl'
    float c = dv2 - dv1;

    // -- build background
    // Positive '2d-curl' in our case signifies flow in the counter clockwise direction
    // Green represents where we find positive 2d-curl.
    // Red represents where we find negative 2d-curl.

    float pos = sign(c);
    if(pos > 0.) {
        col = vec3(c, 1., c); // green background
    } else {
        col = vec3(1., abs(c), abs(c)); // red background
    }

    // FLOW
    vec2 p = uv*10.+0.5; // create grid
    vec2 fp = fract(p)-0.5; // uv coords WITHIN each box 0.5 <> 0.5
    vec2 id = floor(p); // coords OF each box (x,y)


    // This was the result of playing around until something looked nice
    // The janky animation was fixed by just speeding everything up.
    for(int y=-10;y<=10;y++){
        for(int x=-10; x<=10;x++) {
            vec2 offs = vec2(x, y);
            vec2 w = field(id);//id+offs);
            vec2 nw = normalize(w);

            float d = length(fp-offs-nw*1.*mod(iTime, .1)/0.1);
            float m = smoothstep(1., 0.1, d);
            col -= m*0.8;
        }
    }

    // grid lines to make the bad animation be less in your face.
    if(fp.x > .43 || fp.y > .43) {
        col = vec3(.2);
    }

    col = pow( col, vec3(1.0/2.2) );
    fragColor = vec4(col, 1.0);
}

const float PI = 3.1415;

void mainImage(out vec4 o, vec2 i) {
    float a = 1.0 / min(iResolution.x, iResolution.y);
    a *= 10.0;
    vec2 uv = (i - (0.5 * iResolution.xy)) * a;

    vec2 d = floor(uv);
    vec2 f = uv - d;
    f = abs(f - 0.5);

    float value = cos((d.x + 0.5) * PI * 0.25) * sin((d.y + 0.5 + (iTime * 4.0)) * PI * 0.25);

    float size = (value * 0.25) + 0.25;

    float r = smoothstep(size, size - a, f.x) * smoothstep(size, size - a, f.y);

    o = vec4(r, r, r, 1.0);
}

// Adapted from: https://timcoster.com/2020/02/11/raymarching-shader-pt1-glsl/


// Constants
#define PI 3.1415925359
#define TWO_PI 6.2831852
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURFACE_DIST .001

float GetDist(vec3 p) 
{
    vec4 s = vec4(0,1,6. + sin(iTime)*3.,1); //Sphere xyz is position w is radius
    float sphereDist = length(p-s.xyz) - s.w;
    float planeDist  = p.y;
    float d = min(sphereDist,planeDist);

    return d;
}

/*

Distance field
The ray march loop marches from the origin/camera into the direction of the scene. 
After each iteration it checks if the distance to the scene is smaller than the minimum distance(SURACE_DIST). 
If so, it returns the distance to the object. If the distance is greater than the max distance, 
meaning the ray hasn’t hit an object it also breaks out of the loop.
*/ 

float RayMarch(vec3 ro, vec3 rd) 
{
    float dO = 0.; //Distane Origin
    for(int i=0;i<MAX_STEPS;i++)
    {
        vec3 p = ro + rd * dO;
        float ds = GetDist(p); // ds is Distance Scene
        dO += ds;
        if(dO > MAX_DIST || ds < SURFACE_DIST) break;
    }
    return dO;
}

/*

Normals
To get the normals the point where the raymarch hits is used and a small amount (Epsilon) 
is added to the point in the right, up and forward direction. This new offset point is 
then normalized to turn it into a unit vector/direction.
*/

vec3 GetNormal(vec3 p)
{ 
    float d = GetDist(p); // Distance
    vec2 e = vec2(.01,0); // Epsilon
    vec3 n = d - vec3(
    GetDist(p-e.xyy),  
    GetDist(p-e.yxy),
    GetDist(p-e.yyx));

    return normalize(n);
}

/*
Diffuse light (Directional)
To calculate diffuse light, the angle between the surface normal and the light vector 
from a raymarched point is used. To get the angle, the Dot product is used which returns 
the angle in a -1 <> 1 range. So when the light vector is perpendicular to the normal 
vector the Dot product is 1 and if its parralel to the normal vector it is 0.
*/

float GetLight(vec3 p)
{ 
    // Light (directional diffuse)
    vec3 lightPos = vec3(5.*sin(iTime),5.,5.0*cos(iTime)); // Light Position
    vec3 l = normalize(lightPos-p); // Light Vector
    vec3 n = GetNormal(p); // Normal Vector

    float dif = dot(n,l); // Diffuse light
    dif = clamp(dif,0.,1.); // Clamp so it doesnt go below 0

    return dif;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,1,0); // Ray Origin/Camera
    vec3 rd = normalize(vec3(uv.x,uv.y,1)); // Ray Direction

    float d = RayMarch(ro,rd); // Distance

    vec3 p = ro + rd * d;
    float dif = GetLight(p); // Diffuse lighting
    d *= .2;
    vec3 color = vec3(0);
    color = vec3(dif);

    // Set the output color
    fragColor = vec4(color,1.0);
}

