float map(in vec3 pos)
{
    float d = 1e10;


    float an = sin(iTime) * 2.5;

    // opSmoothUnion
    {
    vec3 q = pos - vec3(0.0,0.0,1.0);
    float d1 = sdSphere( q-vec3(0.0,2.5+0.3*an,0.0), 2.5 );
    float d2 = sdRoundBox(q - vec3(0.0,-4.0,0.0), vec3(4.5,4.0,1.0), 0.1 ); 
    float dt = opSmoothSubtraction(d1,d2, 3.0) - 0.2;
    d = min( d, dt );
    }

    return d;
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
	float res = 7777777.7777;
    float t = tmin;
    for( int i=0; i<256; i++ )
    {
		float h = map( ro + rd*t );
        res = min( res * 0.777, k*h/t );
        t += clamp( h, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}


#define AA 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

        vec3 ro = vec3(0.0,4.0,8.0);
        vec3 rd = normalize(vec3(p-vec2(0.0,1.8),-3.5));

        float t = 7.0;
        for( int i=0; i<64; i++ )
        {
            vec3 p = ro + t*rd;
            float h = map(p);
            if( abs(h)<0.001 || t>11.0 ) break;
            t += h;
        }

        vec3 col = vec3(0.0);

        if( t<11.0 )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            vec3  lig = normalize(vec3(1.0,0.8,-0.2));
            float dif = clamp(dot(nor,lig),0.0,1.0);
            float sha = calcSoftshadow( pos, lig, 0.1, 4.0, 32.0 );
            float amb = 0.5 + 0.5*nor.y;
            col = vec3(0.5,0.0,0.25)*amb + 
                  vec3(1.00,0.9,0.80)*dif*sha;
        }

        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    tot += tot * 0.25;
    tot *= tot * tot * tot * tot * tot;

	fragColor = vec4( tot * 2.0, 1.0 );
}

//---By Matt - regular raymarching shader

#define ITER 64
#define MAXD 64.0
#define SURD 0.005
#define BGC vec3(0.45,0.24,0.1) * 4.

struct ray
{
    vec3 o,d;
    float l;
};

//Simplest rotation matrix
mat2 rot(float a)
{
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

//Iq's smooth opu
vec4 opu(vec4 a, vec4 b, float s)
{
    float k = s;
    float d1 = a.w;
    float d2 = b.w;
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return vec4(mix(b.rgb,a.rgb,h),mix( d2, d1, h ) - k*h*(1.0-h));
}

//Iq's smooth ops
vec4 ops(vec4 a, vec4 b, float s)
{
    float k = s;
    float d1 = a.w;
    float d2 = b.w;
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return vec4(mix(b.rgb,a.rgb,h),mix( d2, -d1, h ) + k*h*(1.0-h));
}

//Custom noise method
float noise(float p, float a, float s, float d)
{
	return cos(-p * a + iTime * s) * d;
}

//Full geometry generation
vec4 geo(vec3 p)
{
    p.x += noise(p.x, 39.,6.0,0.003);
    p.y = p.y + sin(iTime * 1.5) * 0.05;
    vec3 objCenter = vec3(0.,0.,1.0);
    //---head
    vec3 headCenter = objCenter + vec3(0.,0.32,-0.03);
    vec4 s0 = vec4(vec3(.45,.34,.1), length(p - headCenter) - 0.1);
    //mouth
    vec4 b0 = vec4(vec3(.45,.14,.1)*0.5, length(max(abs(vec3(p.xy * rot(25.), p.z) - headCenter - vec3(0.15, -.03, -.1)) - vec3(0.12,cos(iTime * 1.5) * 0.015,.08),0.0))-0.02);
    vec4 s0b0 = ops(b0,s0,0.05);

    //---body
    //center
    vec4 b1 = vec4(vec3(.45,.26,.1), length(max(abs(p - objCenter - vec3(0., .0, .0)) - vec3(0.15,.2,.1),0.0))-0.01);
    //r-l subtracted curves
    vec4 b2 = vec4(vec3(-0.2), length(max(abs(vec3(abs(p.x),p.yz) - objCenter - vec3(0.6, -0.3, 0.0)) - vec3(0.2,.2,.1),0.0))-0.25);
    vec4 b1b2 = ops(b2,b1,0.15) + noise(p.y, -38.,8.0,0.001);
    //lHand
    vec4 b3 = vec4(vec3(.45,.34,.1), length(max(abs(vec3(p.xy * rot(-35.),p.z) - objCenter - vec3(-0.215, .01, -.02)) - vec3(0.001,.1,.001),0.0))-0.05);
    vec4 b4 = vec4(vec3(.45,.29,.1)*0.9, length(max(abs(vec3(p.xy * rot(-94.1),p.z) - objCenter - vec3(0.25, -.12, -.02)) - vec3(0.001,.07,.001),0.0))-0.03);
    //rHand
    vec4 b5 = vec4(vec3(.45,.34,.1), length(max(abs(vec3(p.xy * rot(-30.7),p.z) - objCenter - vec3(-0.24, -.08, -.02)) - vec3(0.001,.1,.001),0.0))-0.05);
    vec4 b6 = vec4(vec3(.45,.29,.1)*0.9, length(max(abs(vec3(p.xy * rot(30.5),p.z) - objCenter - vec3(-0.18, .1, -.04)) - vec3(0.001,.07,.001),0.0))-0.03);

    //---legs
    vec4 b7 = vec4(mix(vec3(.85,.54,.0), vec3(0.25,0.14,0.1), smoothstep(p.x+0.1 ,-0.25,0.01)), length(max(abs(p - objCenter - vec3(0.06, -.38, .0)) - vec3(0.015,.05,.05),0.0))-0.005) + 
    noise(p.y, -38.8,12.5,0.008);
    vec4 b8 = vec4(vec3(.85,.1,.1)*2., length(max(abs(p - objCenter - vec3(-0.06, -.38, .0)) - vec3(0.015,.05,.05),0.0))-0.005) + 
    noise(p.y, -32.8,12.5,0.008);

    //---body joints
    vec4 Lhand = opu(b4,b3,.05);
    vec4 Rhand = opu(b5,b6,.05);
    vec4 legs = opu(b7,b8,0.2);

    //---body connection
    vec4 hands = opu(Rhand,Lhand,.05);
    vec4 body = opu(b1b2,hands,.05);
    vec4 full = opu(body, legs,.2);

    return opu(s0b0,full,0.05);
}

//Normals generation
vec3 normal(vec3 p)
{
    vec2 o = vec2(0.001,0.);//slight offset
    float c = geo(p).w;
    return normalize(c - vec3(geo(p - o.xyy).w, geo(p - o.yxy).w, geo(p - o.yyx).w));
}

//Simples lighting + BlinnPhong specs
float light(vec3 p)
{
    vec3 LP = vec3(0.2,0.5,0.5);
    vec3 lp = normalize(LP - p);
    vec3 n = normal(p);

    float l = clamp(dot(n,lp),0.25,1.);
    float s = pow(max(dot(n,lp),0.),80.)*3.0;

    return pow(l + s,0.25);
}

//Raymarching core
vec4 march(ray r)
{
    vec3 c = vec3(0.);
    for(int i = 0; i < ITER; i++)
    { 
        if(r.l > MAXD ) 
        {
            c = BGC * 2.;
            break;
        }
        vec3 p = r.o + r.d * r.l;
        vec4 g = geo(p);
        if(g.w < SURD) 
        {
            c = g.rgb * 2.5;
            break;
        }
        r.l += g.w;
    }
    return vec4(c, r.l);
}

//Output img
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    ray r = ray(vec3(0.),vec3(uv,1.0),0.);
    vec4 m = march(r);
    vec3 p = r.o + r.d * m.w;
    vec3 col = vec3(m.rgb) * light(p);

    fragColor = vec4(col,1.0) + 0.1;
}

// Made by Darko Supe (omegasbk)
// Playing with SDF to show how parallax effects can be achieved 
// with ray marching.
// 25.6.2021.

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .005

#define PI 3.14159265359

const vec3 lightPosition = vec3(0, 3, 6);
const vec3 lightColor    = vec3(0.1, 0.2, 0.2) * 2.;

vec2 getSphereCoords(in vec3 normal)
{
    vec2 coords = vec2((atan(normal.z, normal.x) / PI + 0.5) / 2.,
                       (asin(normal.y) / PI + 0.5));  

    return coords;  
}

float getDepth(in vec3 normal)
{                              
    vec2 depthCoords = getSphereCoords(normal);
    //depthCoords.x = clamp(depthCoords.x, 0.1, 0.9);
    return texture(iChannel1, fract(depthCoords)).r / 10.;   
}

float getDist(in vec3 p) 
{
    float time = iTime * 3.;

    vec4 s = vec4(0, 0, 0, 1.);

    vec3 normal = normalize(p - vec3(0, 0, 0));
    s.w += getDepth(normal);         

    float sphereDist = length(p - s.xyz) - s.w;    
    return sphereDist;
}

vec3 getNormal(in vec3 p) 
{
    float d = getDist(p);

    vec2 e = vec2(.01, 0); 

    vec3 n = d - vec3(
        getDist(p - e.xyy), 
        getDist(p - e.yxy), 
        getDist(p - e.yyx));

    return normalize(n);
}

float rayMarch(in vec3 ro, in vec3 rd)
{
    float dO = 0.;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + rd * dO;

        float dS = getDist(p);
        dO += dS;

        if (dO > MAX_DIST || dS < SURF_DIST) 
            break;
    }

    return dO;
}

vec3 getLight(in vec3 p, in vec3 rd)
{    
	vec3 normal = getNormal(p);
    vec3 lightDir = normalize(p - lightPosition);

    float cosa = pow(0.5+0.5*dot(normal, -lightDir), 3.0);
    float cosr = max(dot(-rd, reflect(lightDir, normal)), 0.0);

    vec3 ambiant = vec3(0.02);
    vec3 diffuse = vec3(0.7 * cosa);
    vec3 phong = vec3(0.3 * pow(cosr, 16.0));

    return lightColor * (ambiant + diffuse + phong);
}

