vec2 RaySphere(vec3 ro, vec3 rd, vec3 sc, float r)
{
    vec3 offset = ro - sc;
    float b = 2. * dot(offset, rd);
    float c = dot(offset, offset) - r * r;

    float discriminant = b * b - 4. * c;

    if (discriminant > 0.)
    {
        float s = sqrt(discriminant);
        float dstToSphereNear = max(0., (-b - s) / 2.);
        float dstToSphereFar = (-b + s) / 2.;

        if (dstToSphereFar > 0.)
        {
            return vec2(dstToSphereNear, dstToSphereFar - dstToSphereNear);
        }
    }
    return vec2(999999999999999999., 0);
}


float RayPlane(vec3 ro, vec3 dir, vec3 p, vec2 s)
{
    // front or back
    float depth = (p.z - ro.z) / dir.z;

    if (depth < 0.) depth = 99999999.;
    else
    {
        vec2 cp = ro.xy + dir.xy * depth;
        if (!(cp.y < p.y + s.y && cp.y > p.y - s.y && cp.x < p.x + s.x && cp.x > p.x - s.x))
        {
            depth = 99999999.;
        }
    }

    return depth;
