float AreaLight(vec3 p, float r, int samples, vec3 ro)
{
    // fidning the goldan ratio
    const float pi = 3.14159;
    const float phi = pi * (3. - sqrt(5.));

    float x;
    float y;
    float z;
    float yR;
    float theta;

    float unHit = 0.;
    // generating the points on the sphere
    for (int i = 0; i < samples; i++)
    {
        y = 1. - (float(i) / float(samples - 1)) * 2.;
        yR = sqrt(1. - y * y);
        theta = phi * float(i);

        x = cos(theta) * yR;
        z = sin(theta) * yR;
        // adding the points to the list
        vec3 pos = vec3(x, y, z) * r + p;
        vec3 dif = pos - ro;
        float lp = length(dif);
        vec3 dir = dif / lp;
        float rs1 = RaySphere(ro, dir, vec3(0, 0, 6), 2.).x;
        float rs2 = RaySphere(ro, dir, vec3(0, -20, 0), 20.).x;
        float rp3 = RayPlane(ro, dir, vec3(1.6, 0.25, 0), vec2(1., 1.));
        unHit += min(min(rs1, rs2), rp3) > lp ? 1. : 0.;
    }
    return unHit / float(samples);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
