    vec3 ro = vec3(-1, 2, -6);

    vec3 col = vec3(0.3, 0.3, rd.y * 0.5 + 0.5);

    vec2 rs1 = RaySphere(ro, rd, vec3(0, 0, 6), 2.);
    vec2 rs2 = RaySphere(ro, rd, vec3(0, -20, 0), 20.);
    float rp3 = RayPlane(ro, rd, vec3(1.6, 0.25, 0), vec2(1., 1.));

    vec2 rs;
    if (rs1.x < rs2.x && rs1.x < rp3) rs = rs1;
    else if (rp3 < rs2.x) rs = vec2(rp3, 0.);
    else rs = rs2;

    if (rs.x < 500.)
    {
        col = vec3(0.8, 0.2, 0.2);
        float shadow = AreaLight(vec3(sin(iTime * 6.), 4, cos(iTime * 6.) + 6.), sin(iTime) + 1., 750, ro + rd * rs.x - rd * 0.01);
        col *= shadow * 0.7 + 0.3;
    }

    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    col =  clamp((col*(a*col+b))/(col*(c*col+d)+e), 0., 1.);

    fragColor = vec4(col,1.0);
}

