// samples per pixel
const int numSamples=16;

// convert directionToLight and directionToView to tangent space
//
void convertToTangentSpace(vec3 toLight, vec3 toView, vec3 hitPoint, out vec3 toLightTS, out vec3 toViewTS, out vec3 nTS)
{
    // use a matrix to convert
    vec3 t = normalize(dFdx(hitPoint));
    vec3 b = normalize(dFdy(hitPoint));
    vec3 n = normalize(cross(t, b));
    mat3 xformMatrix = transpose(mat3(t, b, n));

    toLightTS = xformMatrix * toLight;
    toViewTS = xformMatrix * toView;
    nTS = n;
}

float checkLightVisibility(in Light light, vec3 wi, vec3 hitPoint, vec3 hitNormal) {
    float visible = 0.0;
    vec3 lightDir = wi; 
    Ray ray;
    ray.origin = hitPoint+lightDir*0.01;
    ray.direction = lightDir;
    Intersection intersection = intersectAllObjects(ray);
    int iSphere = intersection.obj;
    if (iSphere == 10) { 
        // light was hit, light is visible
        visible = 1.0;
    }
    return visible;
}

void
makeOrthonormalBasis(vec3 normal, out vec3 tangent, out vec3 binormal) {
    if (abs(normal.x) > abs(normal.y)) {
        tangent = normalize(vec3(0., normal.z, -normal.y));
    }
    else {
        tangent = normalize(vec3(-normal.z, 0., normal.x));
    }

    binormal = cross(normal, tangent);
}



vec3 sampleCone(vec2 u12, float cosThetaMax, vec3 xbasis, vec3 ybasis, vec3 zbasis) {
    float cosTheta = (1. - u12.x) + u12.x * cosThetaMax;
    float sinTheta = sqrt(1. - cosTheta * cosTheta);
    float phi = u12.y * 2.0*PI;
    vec3 sphericald = vec3(sinTheta * cos(phi), sinTheta * sin(phi), cosTheta);
    return sphericald.x * xbasis + sphericald.y * ybasis + sphericald.z * zbasis;
}


vec3 sampleLight(Light light, vec3 hitPoint, vec3 hitNormal, out vec3 wi, out float lightPdf) {
    vec2 u = vec2(random(), random());

    vec3 tangent = vec3(0.);
    vec3 binormal = vec3(0.);
    vec3 lightDir = normalize(light.location - hitPoint);
    makeOrthonormalBasis(lightDir, tangent, binormal);

    float d = length(light.location-hitPoint);
    float sinThetaMax2 = light.radius * light.radius / (d*d);
    float cosThetaMax = sqrt(max(0.00001, 1. - sinThetaMax2));
    wi = sampleCone(u, cosThetaMax, tangent, binormal, lightDir);

    if (dot(wi, hitNormal) > 0.) {
        lightPdf = 1. / (2.0*PI * (1. - cosThetaMax));
    }

	return light.color;
}


/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

   // fragCoord ranges from 
   //   in x: 0.5 to iResolution.x-0.5
   //   in y: 0.5 to iResolution.y-0.5
   // pixel (0,0) is at the bottom left corner

   makeScene(iFrame);
   //materials[0].roughness = iMouse.x/iResolution.x;
   //materials[0].metallic = iMouse.y/iResolution.y;
   vec3 rayOrigin = vec3(0.0, 0.0, 0.0);

   float screenDepth = -2.0;

   float width = iResolution.x;
   float height = iResolution.y; 

   vec3 samp = vec3(0, 0, 0);
   seed = 0.0;
   for (int i=0; i<1*numSamples; i++) {
       float x = fragCoord.x + random() - 0.5;
       float y = fragCoord.y + random() - 0.5;


       // map (0.5, w-0.5) to (-1, 1)
       // and (0.5, h-0.5) to (-1, 1)
       x = (x/width)*2.0 - 1.0;
       y = (y/height)*2.0 - 1.0;

       // account for the non-square window
       float aspectRatio = width/height;
       y = y/aspectRatio;

       seed = float(iFrame) +  hash12(vec2(x,y));
       // normalized ray direction
       vec3 rayDirection = normalize(vec3(x, y, screenDepth));

       Ray ray = Ray(rayOrigin, rayDirection);

       vec3 L = vec3(0.0, 0.0, 0.0);
       for (int bounces=0; bounces<2; bounces++) {

       // traverse the scene (all spheres) and find the 
       // closest intersected object and intersection point
       Intersection intersection = intersectAllObjects(ray);

       int iSphere = intersection.obj;
       float minT = intersection.t;

       Sphere sphere;

       if (iSphere > -1) { // if there is an intersection

           // to get around iSphere not being constant
           for (int i=0; i<NUM_SPHERE; i++) {
               if (i==iSphere) {
                   sphere = spheres[i];
                   break;
               }
           }

           if (iSphere == 10 && bounces==0) {
               // direct hit to the light from view
               L = lights[0].color; // 'show' the light
               break;
           }
           if (iSphere==10) break; // otherwise ignore it


           Material m = materials[iSphere];
           vec3 wi;
           float lightPdf = 0.0;
           vec3 Ld = vec3(0.0, 0.0, 0.0);

           // hit coordinates
           vec3 hit = ray.origin + minT*ray.direction;
           // normal at the point of ray-sphere intersection
           vec3 hitPointNormal = normalize(hit-sphere.center);         
           // vector from intersection to light
           vec3 hitPointToLight = normalize(lights[0].location-hit);
           vec3 hitPointToView = ray.origin-hit;
           // calculate tangent and binormal at hit point
           vec3 tangent = cross(hitPointNormal, vec3(1.,0.,1.));
           vec3 binormal = normalize(cross(hitPointNormal, tangent));
           tangent = normalize(cross(hitPointNormal,binormal));


           // sample the light (get new direction and pdf)
           vec3 Li = sampleLight(lights[0], hit, hitPointNormal, wi, lightPdf);
           //wi = hitPointToLight;
           float lightVisible = checkLightVisibility(lights[0], wi, hit, hitPointNormal);
           Li = Li * lightVisible;

           // compute the brdf at intersection
           vec3 f = BRDF(wi, hitPointToView, hitPointNormal, tangent, binormal, m);
           f = f * abs(dot(wi, hitPointNormal));

           if (lightVisible > 0. && lightPdf > 0.0001)
               Ld = Li * f  / lightPdf;  // direct light contribution


           L = L + Ld; // accumulate direct light
           // the new ray
           ray.direction = wi;
           ray.origin = hit + .01 * ray.direction;


       } 
       else {
           L = bgColor(ray.direction); // nothing was hit

       }

       } // end of bounces
       samp = samp + L;
   } // end of spp
   // average all the samples per pixel
   samp = samp/float(numSamples);
   // tone map
   vec3 toneMappedColor = samp * (1.0 / (samp + 1.0));
   // gamma correct
   float gamma = 1.0/2.2;
   vec3 finalColor = vec3(pow(toneMappedColor.x, gamma), 
                          pow(toneMappedColor.y, gamma), 
                          pow(toneMappedColor.z, gamma));

   fragColor = vec4(finalColor, 1.0);

}

#define PI 3.14159265359

/*** math heavy part for spherical harmonics ***/

#define SQRT2PI 2.506628274631

// factorial
float fac(int n) {
    float res = 1.0;
    for (int i = n; i > 1; i--)
        res *= float(i);
    return res;
}

// double factorial
float dfac(int n) {
    float res = 1.0;
    for (int i = n; i > 1; i-=2)
        res *= float(i);
    return res;
}

// fac(l-m)/fac(l+m) but more stable
float fac2(int l, int m) {
    int am = abs(m);
    if (am > l)
        return 0.0;
    float res = 1.0;
    for (int i = max(l-am+1,2); i <= l+am; i++)
        res *= float(i);
    if (m < 0)
        return res;
    return 1.0 / res;
}

// complex exponential
vec2 cexp(vec2 c) {
    return exp(c.x)*vec2(cos(c.y), sin(c.y));
}

// complex multiplication
vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

// complex conjugation
vec2 conj(vec2 c) { return vec2(c.x, -c.y); }

// complex/real magnitude squared
float sqr(float x) { return x*x; }
float sqr(vec2 x) { return dot(x,x); }

// associated legendre polynomials
float legendre_poly(float x, int l, int m) {
    if (l < abs(m))
        return 0.0;
    if (l == 0)
        return 1.0;
    float mul = m >= 0 ? 1.0 : float((~m&1)*2-1)*fac2(l,m);
    m = abs(m);
    // recursive calculation of legendre polynomial
    float lp1 = 0.0;
    float lp2 = float((~m&1)*2-1)*dfac(2*m-1)*pow(1.0-x*x, float(m)/2.0);
    for (int i = m+1; i <= l; i++) {
        float lp = (x*float(2*i-1)*lp2 - float(i+m-1)*lp1)/float(i-m);
        lp1 = lp2; lp2 = lp;
    }
    return lp2 / mul;
}

// spherical harmonics function
vec2 sphere_harm(float theta, float phi, int l, int m) {
    float abs_value = 1.0/SQRT2PI*sqrt(float(2*l+1)/2.0*fac2(l,m))
                        *legendre_poly(cos(theta), l, m);
    return cexp(vec2(0.0,float(m)*phi))*abs_value;
}

/*** Now the rendering ***/

float map(float t, float a, float b, float A, float B) {
    if (A - B == 0. || a - b == 0.)
        return (A+B)*0.5;
    return A + (t - a) * (B - A) / (b - a);
}

vec3 rotateX(vec3 pos, float angle) {
    return vec3(pos.x, cmul(pos.yz, cexp(vec2(0.,-angle))));
}

vec3 rotateZ(vec3 pos, float angle) {
    return vec3(cmul(pos.xy, cexp(vec2(0.,-angle))), pos.z);
}

float sphereSdf(vec3 pos, float r) {
    return length(pos)-r;
}

#define SELECT_GRID 7.0
void get_lm(out int l, out int m, in vec2 fragCoord) {
    vec2 mouse = iMouse.xy/iResolution.xy;
    int t = int(iTime);

    if (mouse.x + mouse.y > 0.0) {
        vec2 coord = iMouse.z > 0.5 ? fragCoord : iMouse.xy;
        ivec2 cell = ivec2(coord/iResolution.y*SELECT_GRID);
        t = (int(SELECT_GRID) - cell.y - 1) + cell.x * int(SELECT_GRID);
        l = cell.x;
        m = cell.y;
        return;
    }

    l = int(floor(sqrt(0.25 + float(2*t)) - 0.5));
    m = int(floor(float(t) - 0.5*float(l + l*l)));
}

float globalSdf(vec3 pos, out vec3 color, in vec2 fragCoord) {
    // get spherical coordinates
    float sin_theta = length(pos.xy);
    float ball_phi = sin_theta > 0.0 ? atan(pos.x, pos.y) : 0.0;
    float ball_theta = atan(sin_theta, pos.z);

    int m, l;
    get_lm(l, m, fragCoord);

    // evaluate spherical harmonics
    float off = m == 0 ? 0.0 : iTime/float(m);
    vec2 sph_harm = sphere_harm(ball_theta, ball_phi + off, l, m);

    // normalization for visual purposes
    sph_harm *= SQRT2PI;
    float occ = abs(sph_harm.x);

    float crit = float(1+max(abs(m), l-abs(m)));
    float crit2 = 1.0 / (3.0 + crit);
    float max_abs_sd = (length(pos)+0.01)*PI*2.0/crit;

    float sdx = sphereSdf(pos, max(sph_harm.x, 0.))*crit2;
    float sdy = sphereSdf(pos, -min(sph_harm.x, 0.))*crit2;
    color = vec3(float(sdx < sdy),occ,0);
    float sd = min(sdx, sdy);
    return min(max_abs_sd, abs(sd)) * sign(sd);
}

vec3 calculate_normal(in vec3 world_point, float sd, in vec2 fragCoord) {
    const vec3 small_step = vec3(0.001, 0.0, 0.0);
    vec3 col;
    float gradient_x = globalSdf(world_point + small_step.xyy, col, fragCoord) - sd;
    float gradient_y = globalSdf(world_point + small_step.yxy, col, fragCoord) - sd;
    float gradient_z = globalSdf(world_point + small_step.yyx, col, fragCoord) - sd;
    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);
    return normalize(normal);
}

vec4 lighting(vec3 cp, vec3 color, vec3 normal, vec3 rdir) {
    // from https://www.shadertoy.com/view/ts3XDj
    // geometry
    vec3 ref = reflect( rdir, normal );

    // material		
    vec3 mate = 0.5*mix( vec3(1.0,0.6,0.15), vec3(0.2,0.4,0.5), color.r );

    float occ = min(color.g, 1.0);//clamp(2.0*tmat.z, 0.0, 1.0);
    float sss = 0.0;//pow(clamp(1.0 + dot(normal, rdir), 0.0, 1.0), 1.0);

    // lights
    vec3 lin  = 2.5*occ*vec3(1.0)*(0.6 + 0.4*normal.y);
         lin += 1.0*sss*vec3(1.0,0.95,0.70)*occ;		

    // surface-light interacion
    vec3 col = mate.xyz * lin;
    return vec4(col, 1.0);
}

#define NUMBER_OF_STEPS 128
#define MINIMUM_HIT_DISTANCE 0.005
#define MAXIMUM_TRACE_DISTANCE 6.0
vec4 raymarch(in vec3 rpos, in vec3 rdir, in vec2 fragCoord) {
    float t = 0.0;
    float closest_t = 0.0;
    float closest_t_r = MAXIMUM_TRACE_DISTANCE;
    float closest_t_r2 = MAXIMUM_TRACE_DISTANCE;
    float closest_t_r3 = MAXIMUM_TRACE_DISTANCE;
    vec4 col = vec4(0,0,0,0);
    for (int i = 0; i < NUMBER_OF_STEPS; i++) {
        vec3 cp = rpos + t * rdir;

        vec3 color = vec3(0.0);
		float sd = globalSdf(cp, color, fragCoord);

        if (abs(sd) < 0.7*MINIMUM_HIT_DISTANCE) {
            vec3 normal = calculate_normal(cp, sd, fragCoord);
            col = lighting(cp, color, normal, rdir);
            break;
        }

        closest_t_r3 = closest_t_r2;
        closest_t_r2 = closest_t_r;
        if (sd < closest_t_r) {
            closest_t = t;
            closest_t_r = sd;
        }

        if (t > MAXIMUM_TRACE_DISTANCE)
            break;

        t += sd;
    }
    if (abs(closest_t_r3) > MINIMUM_HIT_DISTANCE) {
        return col;
    }
    vec3 cp = rpos + closest_t * rdir;
    vec3 color = vec3(0.0);
    float sd = globalSdf(cp, color, fragCoord);
    vec3 normal = calculate_normal(cp, sd, fragCoord);
    float a = 1.0-abs(closest_t_r3)/MINIMUM_HIT_DISTANCE;
    vec4 col2 = lighting(cp, color, normal, rdir);
    col2.a = a;
    return mix(col, col2, a);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    float rot = 0.5*sin(iTime*0.5) * PI/2.1;

    if (iMouse.z > 0.5) {
        // selection on click
        uv = fract(fragCoord/iResolution.y*SELECT_GRID)*2.0-1.0;
        rot = -0.5;
    }

     // camera movement
	vec3 cam_pos = 3.0 * rotateX(vec3(0,1,0), rot);
    vec3 look_at = vec3(0);   
    vec3 look_up = vec3(0,0,1);
    // camera matrix
    vec3 ww = normalize(look_at - cam_pos);
    vec3 uu = normalize(cross(ww, look_up));
    vec3 vv = normalize(cross(uu, ww));
	// create perspective view ray
    vec3 rpos = cam_pos;
	vec3 rdir = normalize( uv.x*uu + uv.y*vv + 2.0*ww );

    vec4 col = raymarch(rpos, rdir, fragCoord);
    vec3 bg = vec3(0.3) * clamp(1.0-2.6*length(fragCoord/iResolution.xy-0.5)*0.5,0.0,1.0);
    col = vec4(mix(bg, col.rgb, col.a), 1.0);
    col = vec4(pow(clamp(col.rgb,0.0,1.0), vec3(0.4545)), 1.0);

    if (iMouse.z > 0.5) {
        // selection on click
        ivec2 select = abs(ivec2(fragCoord/iResolution.y*SELECT_GRID)-ivec2(iMouse.xy/iResolution.y*SELECT_GRID));
        if (select.x + select.y == 0) {
            // draw selection box
            vec2 absuv = abs(uv);
            vec2 cmp = min(absuv, vec2(0.9));
            float d = length(absuv - cmp);
            float fac = max(0.05 - abs(d - 0.05), 0.0)/0.025;
            ivec2 checkers = ivec2(round(uv * 3.5));
            fac *= float((checkers.x + checkers.y + 1)&1);
            col = mix(col, vec4(1.0), fac);
        }
    }

    fragColor = col;
}

#define MAX_ITER 256.0 
