#define MAX_Y  1.5

precision highp float;

//  Function from Iñigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                            6.0)-3.0)-1.0,
                    0.0,
                    1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}


// https://gist.github.com/companje/29408948f1e8be54dd5733a74ca49bb9
float map(float value, float min1, float max1, float min2, float max2) {
    return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

float crosshair(vec2 st, vec2 m) {
    return (step(st.x-0.001, m.x) - step(st.x+0.001, m.x)) + (step(st.y-0.002, m.y) - step(st.y+0.002, m.y));
}

//map 0-1 to mandelbrot space
vec2 normalToMandelCoord(vec2 norm, vec2 center, float zoom) {
    return center + vec2(
        map(norm.x, 0.0, 1.0, MIN_X, MAX_X),
        map(norm.y, 0.0, 1.0, MIN_Y, MAX_Y)
     ) / zoom;
}

//map mandelbrot space to 0-1
vec2 mandelToNormalCoord(vec2 norm, float zoom) {
    return vec2(
        map(norm.x, MIN_X/zoom, MAX_X/zoom, 0.0, 1.0),
        map(norm.y, MIN_Y/zoom, MAX_Y/zoom, 0.0, 1.0)
     );
}


