float doCircle(vec2 uv, float x, float y, float size) {
    return floor(length(uv+vec2(x, y)) - size + 0.988);
}

float doRing(vec2 uv, float x, float y, float size, float thicc) {
    float ring = length(uv+vec2(x, y)) - size;
    ring = abs(ring) * thicc;
    ring = floor(ring);
    return ring;
}

float doBrm(vec2 uv, float x, float y, float size) {
    float ringSize = 0.425;
    float detailThicc = 80.0;

    uv += vec2(x, y);
    uv *= size;

    float ring = doRing(uv, 0.0, 0.0, ringSize, 40.0);

    float small = doRing(uv, 0.0, 0.0, ringSize - 0.1, detailThicc);

    small = max(small, 1.0 - doCircle(uv, 0.0, -0.33, 0.07));
    small = min(small, doRing(uv, 0.0, -0.33, 0.07, detailThicc));

    small = max(small, 1.0 - doCircle(uv, 0.25, 0.21, 0.07));
    small = min(small, doRing(uv, 0.25, 0.21, 0.07, detailThicc));

    small = max(small, 1.0 - doCircle(uv, -0.25, 0.21, 0.07));
    small = min(small, doRing(uv, -0.25, 0.21, 0.07, detailThicc));

    small = max(small, doCircle(uv, 0.0, 0.0, ringSize - 0.1));

    vec2 boxV = uv * 1.0;
    boxV = abs(boxV) * 1.0;
    boxV -= 0.04;
    boxV.x -= 0.04;
    boxV = abs(boxV) * 80.0;
    boxV = floor(boxV);
    boxV.x = max(boxV.x, floor(abs(uv.y)*19.0));
    boxV.y = max(boxV.y, floor(abs(uv.x)*12.0));
    float box = min(boxV.x, boxV.y);

    float put = min(ring, small);
    put = min(put, box);
    put = min(put, doRing(uv, 0.0, -0.16, 0.035, detailThicc));
    put = min(put, doRing(uv, 0.15, -0.16, 0.035, detailThicc));
    put = min(put, doRing(uv, -0.15, -0.16, 0.035, detailThicc));

    put = min(put, doRing(uv, 0.20, 0.05, 0.035, detailThicc));
    put = min(put, doRing(uv, -0.20, 0.05, 0.035, detailThicc));

    put = min(put, doRing(uv, 0.08, 0.20, 0.035, detailThicc));
    put = min(put, doRing(uv, -0.08, 0.20, 0.035, detailThicc));

    return put;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float var = (sin(iTime*20.0)+1.0)*0.01 + 1.0;

    float put = doBrm(uv, 0.0, 0.0, var);

    put = 1.0 - put;
    fragColor = vec4(put, put, put, 1.0);
}

float R21 (vec2 p) {
    return fract(sin(dot(p.xy, vec2(2.3245,5.234)))*12.5632145);
}

float NoiseValue (vec2 uv) {
    vec2 gv = fract(uv);
    vec2 id = floor(uv);

    gv = gv * gv * (3. - 2. * gv);

    float a = R21(id);
    float b = R21(id + vec2(1., 0.));
    float c = R21(id + vec2(0., 1.));
    float d = R21(id + vec2(1., 1.));

    return mix(a, b, gv.x) + (c - a)* gv.y * (1. - gv.x) + (d - b) * gv.x * gv.y;
}

float SmoothNoise (vec2 uv) {

    float value = 0.;
    float amplitude = .1;

    for (int i = 0; i < 10; i++) {
        value += NoiseValue(uv) * amplitude;
        uv *= 2.;
        amplitude *= .5;
    }

    return value;
}

float Circle(vec2 uv, vec2 p) {
    float dist = length(uv-p);
    float mask = smoothstep(.03, .29, dist);
    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    vec2 rn = vec2(0.);
    rn.x = SmoothNoise(uv + 1.2 * vec2(1.) + vec2(2.)+ 0.15*iTime );
    rn.y = SmoothNoise(uv + 1. * vec2(1.) + vec2(2.)+ 0.12*iTime);

    col += SmoothNoise(uv+rn*2.5);

    col += vec3(0.4/length(uv-vec2(-1., .5)));
    col += texture(iChannel0, col.xy).rgb;
    col -= Circle(uv, vec2(-1., .5));

    fragColor = vec4(col,1.0);
}

#define SC (250.0)

float random(vec2 p)
{
    return fract(p.x*p.y*fract(p.x*0.1183099));
}

float noise(in vec2 uv)
{
    return sin(uv.x+iTime)+cos(uv.y);
}

#define OCTAVES 8
float fbm(in vec2 uv)
{
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.8;

    for (int i = 0; i < OCTAVES; i++)
    {
        // value += noise(uv * freq) * amplitude;

        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value += (.25-abs(noise(uv * freq)-.3) * amplitude);

        amplitude *= (.47+value)/2.0;

        freq *= 2.;

        uv += uv.yx/16.0;
        uv = uv.yx;
    }

    return value;
}

float f(in vec3 p)
{

    float h = fbm(p.xz);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(-.8, .15, -.3));

    vec3 camStep = vec3(lightDir.x, 0., lightDir.z) * iTime;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;
    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));

    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

