#define scale 20.0
#define PI 3.1415926535898
#define hash(x)  ( float( hashi(x) ) / float( 0xffffffffU ) )

uint hashi(uint x)
{
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

mat2 rotate(float theta)
{
    return mat2(cos(theta), sin(theta), -sin(theta), cos(theta));
}

// non-branching mod3
int mod3(int n) {
    int two_if_neg = int(n < 0)*2;
    int sg = 1-two_if_neg;
    return two_if_neg + sg*((two_if_neg + sg*n)%3);
}

// tweaked from iq's hexagonal grid traversal to be non-branching
ivec2 closestLatticeCoords(vec2 p, vec2 origin, float angle) 
{
    p = rotate(angle)*(p - origin);
	vec2 q = vec2(p.x, p.x*0.5 + p.y*0.8660254037);

    ivec2 i = ivec2(floor(q));
	vec2 f = fract(q);

	int v = mod3(i.x + i.y);
    ivec2 lc = i + v;
    lc -= int(v == 2) * (ivec2(2, 1) + int(f.x > f.y)*ivec2(-1, 1));

    return ivec2( lc.x, (2*lc.y-lc.x)/3 );
}

vec2 latticePoint(ivec2 lc, vec2 origin, float angle )
{
    vec2 pt = vec2(float(lc.x),float(lc.y)*1.732050807);
    return rotate(-angle)*pt + origin;
}

