float tSegment( in vec2 p, in vec2 a, in vec2 b, out float ud)
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float t =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    ud = length(p - (a+t*ba));
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    uv *= scale;

    float pixd = scale/iResolution.x;

    vec2 lattice1Origin = vec2(0, 0);
    float lattice1Angle = 0.0 + 0.01*iTime;
    vec2 lattice2Origin = vec2(7.5, 2.5);
    float lattice2Angle = 0.25*PI + 0.02*iTime;

    //ivec2 lc = ivec2(0, 0);
    ivec2 lc1 = closestLatticeCoords(uv, lattice1Origin, lattice1Angle);
    ivec2 lc2 = closestLatticeCoords(uv, lattice2Origin, lattice2Angle);
    //ivec2 lc = latticeCoords(vec2(0, 5));
    vec2 lp1 = latticePoint(lc1, lattice1Origin, lattice1Angle);
    vec2 lp2 = latticePoint(lc2, lattice2Origin, lattice2Angle);
    float d1 = distance(uv, lp1);
    float d2 = distance(uv, lp2);
    float f1 = hash(hashi(uint(lc1.y)) + uint(lc1.x));
    float f2 = hash(hashi(uint(lc2.y)) + uint(lc2.x));
    float rg = 1.0;
    ivec2 lc = lc1;
    vec2 lp = lp1;
    float d = d1;
    float f = f1;
    vec2 latticeOrigin = lattice1Origin;
    float latticeAngle = lattice1Angle;
    vec2 lattice3Origin = lattice2Origin;
    float lattice3Angle = lattice2Angle;
    if (d2 < d) {
        d = d2;
        lc = lc2;
        lp = lp2;
        f = f2;
        rg = 0.0;
        latticeOrigin = lattice2Origin;
        latticeAngle = lattice2Angle;
        lattice3Origin = lattice1Origin;
        lattice3Angle = lattice1Angle;
    }
    ivec2 lc3 = closestLatticeCoords(lp, lattice3Origin, lattice3Angle);
    vec2 lp3 = latticePoint(lc3, lattice3Origin, lattice3Angle);
    ivec2 lc4 = closestLatticeCoords(lp3, latticeOrigin, latticeAngle);
    float f3 = hash(hashi(uint(lc3.y)) + uint(lc3.x));
    float dSeg = 0.0;
    float tSeg = tSegment(uv, lp, lp3, dSeg);
    float b = f;
    if (lc == lc4) { //symmetric link
        d = min(d, dSeg+0.1);
        b = (1. - tSeg)*f + tSeg*f3;
        rg += (1. - 2.*rg)*tSeg;
    }
    b = .75*b + .25;
    vec4 colIn = vec4(rg*b,0.5+0.5*b,(1.0-rg)*b,1.0);
    float bgBlend = smoothstep(0., 1., (d - 0.2)/pixd);
    //float mix = smoothstep(0., 1., (d1 - 0.2)/pixd)*.5;
    fragColor = (1.0-bgBlend)*colIn + bgBlend * vec4(1,1,1,1);
    //fragColor = (1.0-mix)*vec4(1,1,1,1) + mix * vec4(f1, f1, f1, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/min(iResolution.x,iResolution.y);
    uv.y-= 0.5;
    uv.x-= iResolution.x/iResolution.y/2.;
    float PI = 3.141592553589793;

    vec3 col = vec3(0.0);
    for(float i = 0.0;i<12.0;i++){
    float angle = i*PI/6.0+iTime;
        vec2 pos = vec2(cos(angle),sin(angle))*vec2(sin(iTime+i/12.0)*0.5);
        col += vec3(0.005/(length(pos-uv)));
    }
;
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265358979323846

// Not the best way to do random, but it should be good enough here.
float random(vec3 st) {
    return fract(sin(dot(st, vec3(53.81125,6.42648,24.40232)))*73424.64639);
}

// Linear interpolation
float mid_lin(vec4 corners, vec2 location) {
    vec2 mid = corners.xz*location.x+corners.yw*(1.-location.x);
    return mid.x*location.y+mid.y*(1.-location.y);
}

// Cosine interpolation
float mid_sin(vec4 corners, vec2 location) {
    return mid_lin(corners, (cos(PI*(1.-location))+1.)/2.);
}

// Interpolate to find midpoint value.
float midlicate(vec4 corners, vec2 location) {
    return mid_sin(corners, location);
}

vec4 falsePerlin(vec2 fragCoord) {
    int depth = 12;
    float scaleCoeff = 5.;
    vec2 shaderSize = vec2(1600./350., 900./350.)/scaleCoeff;
    vec2 offset = vec2(iTime*0.75,iTime*0.5)/scaleCoeff;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*shaderSize + offset;

    float total = 0.;
    float power = 0.5;
    for (int level=0; level<depth; level++) {
        vec2 uvf = fract(uv/power);
        vec3 uvi = vec3(floor(uv/power), level);
        vec4 corners = vec4(random(uvi+vec3(1,1,0)),
                            random(uvi+vec3(0,1,0)),
                            random(uvi+vec3(1,0,0)),
                            random(uvi+vec3(0,0,0)));

        total += power*midlicate(corners,uvf);
        power /= 2.;
    }

    float totalWeight = 1.-1./float(2<<depth);
    total /= totalWeight;

    return vec4(vec3(1.-total),1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = falsePerlin(fragCoord);
    //fragColor = perlin(fragCoord);
}

// UTILS
float pong(in float x)
{
    return abs(mod(x, 2.0)-1.0);
}

// GRID
float falloff(in float x, in float limit) {
    if (x < limit) return 1.0;
    return 1.0 - pow(abs(limit-x)/limit, 2.0);
}

float size(in vec2 xy, in float t, in vec2 m)
{
    return pow(xy.y, pong(t)) * m.y + m.x;
}

vec3 color(in vec2 xy, in float t, in vec2 m)
{
    return vec3(pong(xy.x), pong(xy.y), pong(t));
}

// MAIN
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float density = 20.0;
    float radius = 0.5/density;
    float t = iTime * 0.3;

    vec2 uv = fragCoord/iResolution.y;
    vec2 m = vec2(1.0, 1.0) - iMouse.xy/iResolution.xy;
    vec2 nearest = ceil(uv * density)/density;
    float d = distance(uv, nearest);
    float limit = radius * size(nearest, t, m);
    float c = falloff(d, limit);
    vec3 col = color(uv, t, m) * c;

    // Output to screen
    fragColor = vec4(col,1.0);
}

