#define PI 3.14159265359

// define colours
const vec3 red = vec3(0.83, 0.17, 0.0);
const vec3 orange = vec3(0.99, 0.59, 0.33);
const vec3 white = vec3(1.0, 1.0, 1.0);
const vec3 pink = vec3(0.82, 0.38, 0.63);
const vec3 purple = vec3(0.63, 0.0, 0.38);

float wave(in vec2 uv) {
    return (uv.y + (0.08 * sin(iTime + (uv.x * (PI * 3.0)))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // setting each colour, starting with purple (working from the bottom up)
    vec3 colour = purple;
    // pink
    float w = step(0.2, wave(uv));
    colour = (1.0 - w) * colour + w * pink;
    // white
    w = step(0.4, wave(uv));
    colour = (1.0 - w) * colour + w * white;
    // orange
    w = step(0.6, wave(uv));
    colour = (1.0 - w) * colour + w * orange;
    // red
    w = step(0.8, wave(uv));
    colour = (1.0 - w) * colour + w * red;

    // output to screen
    fragColor = vec4(colour, 1.0);
}

vec2 getPoint(in vec2 cell) {
    return cell + 0.5 + 0.5 * vec2(
    sin((cell.x - cell.y + iTime + fract(length(cell))) * 7.777),
    sin((cell.x - fract(length(cell))) * 7.777) * sin((cell.x + cell.y * iTime) * 7.777));
}

float getId(in vec2 cell) {
    return fract(sin(cell.x * 4647.543 + cell.y * 2435.32) * 9658.65);
}

float voronoi(in vec2 pixel) {
    vec2 uv = (pixel - 0.5 * iResolution.xy) / iResolution.y * (2.0);

    uv *= distance(uv, vec2(0.0));
    uv += vec2(cos(iTime * 0.777) + 0.777);
    vec2 cell = floor(uv);

    float minDist = 1e20;
    float id = 0.0;
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            vec2 curCell = cell + (vec2(i, j));
            float curDist = length(uv - getPoint(curCell));
            if (curDist < minDist) {
                id = getId(curCell);
                minDist = curDist;
            }
        }
    }

    return id;
}

vec4 render(in vec2 px) {
    float  d1 = voronoi(px + vec2(1.0, 0.0)),
           d2 = voronoi(px - vec2(1.0, 0.0)),
           d3 = voronoi(px + vec2(0.0, 1.0)),
           d4 = voronoi(px - vec2(0.0, 1.0)),
           n = (abs(d1-d2)>0.?1.:0.) 
                +(abs(d1-d3)*abs(d2-d3)>0.?1.:0.)
                +(abs(d1-d4)*abs(d2-d4)*abs(d3-d4)>0.0?1.:0.);
    return step(vec4(2,2,2,0),vec4(n));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = (render(fragCoord));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 3.0;

    // Time varying pixel color
    vec3 col1 = 0.5 + 0.5*cos((iTime*0.5)+uv.xyx+vec3(0,2,4));
    vec3 col2 = 0.5 + 0.5*sin((iTime*0.5)+uv.yxy+vec3(2,4,0));
    vec3 col3 = 0.5 + 0.5*cos((iTime*0.5)+uv.xyx+vec3(4,0,2));

    float clamp1 = clamp(sin(iTime * 0.75) * 0.1 + 0.5, 0.0, 1.0);
    float clamp2 = clamp(cos(iTime * 0.25) * 0.1 + 0.5, 0.0, 1.0);

    // Output to screen
    fragColor = vec4(mix(mix(col1, col2, clamp1), col3, clamp2),1.0);
}

