                       60.0 - 30.0 - 30.0 - 20.0 - 8.0 + 30.0 + 50.0, 
                       -47.0 + 15.0 - 4.5 + 20.0 + 20.0 + 25.0 - 50.0);

float TOTAL_PLAY_TIME = 0.85;
float JUMP_PLAY_TIME = 10.02;
float JUMP_ON_FLY_TIME = 0.4;
float JUMP_OFF_FLY_TIME = 0.4;
float JUMP_WAIT_TIME = 0.1;

float GetTime()
{
    return iTime;
}

float GetCurrentTime()
{
    return mod(GetTime(), TOTAL_PLAY_TIME) / TOTAL_PLAY_TIME;
}

int GetCurrentCycle()
{
    return int(GetTime() / TOTAL_PLAY_TIME);
}

float GetCurrentJumpTime()
{
    return mod(GetTime(), JUMP_PLAY_TIME) / JUMP_PLAY_TIME;
}

float EaseOut(float x)
{
	return 1.0 - pow(1.0 - x, 3.0);   
}

float EaseOutQuad(float x)
{
    return 1.0 - (1.0 - x) * (1.0 - x);
}

float EaseInQuad(float x)
{
    return x * x;
}

float EaseInCubic(float x) 
{
    return x * x * x;
}

vec3 Lerp(vec3 from, vec3 to, float t) {
    vec3 res = mix(from, to, t);
    return res;
}

float LerpF(float from, float to, float t) {
    float res = mix(from, to, t);
    return res;
}

float quadSDF(vec3 p, vec3 pos, vec3 size) {
    p += pos;
    vec3 d = abs(p) - size;

    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);
    float outsideDistance = length(max(d, 0.0));
    return insideDistance + outsideDistance;
}

vec3 sceneColorBySDF(float sdf, vec3 color)
{
    return step(sdf, EPSILON) * color;
}

vec4 sceneColorBySDF4(float sdf, vec4 color)
{
    return step(sdf, EPSILON) * color;
}

vec4 building1SDF(vec3 p, vec3 pos, float tColor) {
    float b1 = quadSDF(p, pos + BUILDING_1_POS, BUILDING_1_SIZE);

    float sdf = b1;

    vec3 color = sceneColorBySDF(b1, mix(BUILDING_1_COLOR, EMPTY_COLOR, tColor));

    return vec4(color, sdf);
}

vec4 building2SDF(vec3 p, vec3 pos, float tColor) {
    float b2 = quadSDF(p, pos + BUILDING_2_POS, BUILDING_2_SIZE);

    float sdf = b2;

    vec3 color = sceneColorBySDF(b2, mix(BUILDING_2_COLOR, EMPTY_COLOR, tColor));

    return vec4(color, sdf);
}

vec4 backBuildingSDF(vec3 p, vec3 pos, float tColor) {
    float b2 = quadSDF(p, pos + BUILDING_BACK_POS, BUILDING_BACK_SIZE);

    float sdf = b2;

    vec3 color = sceneColorBySDF(b2, mix(BUILDING_BACK_COLOR, EMPTY_COLOR, tColor));

    return vec4(color, sdf);
}

vec4 signSDF(vec3 p, vec3 pos, float tColor, out float dist) {
    dist = quadSDF(p, pos + SIGN_POS, SIGN_SIZE);

    vec4 color = sceneColorBySDF4(dist, mix(SIGN_COLOR, EMPTY_COLOR_4, tColor));
    return color;
}

vec4 sign2SDF(vec3 p, vec3 pos, float tColor, out float dist) {
    dist = quadSDF(p, pos + SIGN_2_POS, SIGN_2_SIZE);

    vec4 color = sceneColorBySDF4(dist, mix(SIGN_2_COLOR, EMPTY_COLOR_4, tColor));
    return color;
}

vec4 sign3SDF(vec3 p, vec3 pos, float tColor, out float dist) {
    dist = quadSDF(p, pos + SIGN_3_POS, SIGN_3_SIZE);

    vec4 color = sceneColorBySDF4(dist, mix(SIGN_3_COLOR, EMPTY_COLOR_4, tColor));
    return color;
}

vec4 sceneSDF(vec3 samplePoint, out float dist) {
    float t = GetCurrentTime();
    float appearT = LerpF(1.0, 0.5, t);
    float appearT2 = LerpF(0.5, 0.0, EaseOut(t));

    int cycle = GetCurrentCycle();

    float tt = LerpF(0.0, 1.2, sin(t * 3.14));

    float jt = GetCurrentJumpTime();
    float jwt = (max(0.0, GetCurrentJumpTime() - JUMP_WAIT_TIME)) / (1.0 - JUMP_WAIT_TIME);

    float jtt = min(1.0, jwt / JUMP_ON_FLY_TIME);
    float jtl = 1.0 - (JUMP_ON_FLY_TIME + JUMP_OFF_FLY_TIME);
    float jtt2 = min(1.0, max(0.0, (jwt - jtl - JUMP_ON_FLY_TIME) / JUMP_OFF_FLY_TIME));

    float MOVE = 140.0;

    vec3 cameraMove = vec3(0.0, 0.0, MOVE * t) + CAMERA_OFFSET + vec3(6.0 * jtt - 6.0 * jtt2, 12.0 * sin(jt * 3.14 * 2.0), 0.0);
    vec3 dublicate_offset = vec3(0.0, 0.0, -MOVE);

    float b3 = quadSDF(samplePoint, BUILDING_3_POS + cameraMove, BUILDING_3_SIZE);
    float b4 = quadSDF(samplePoint, BUILDING_4_POS + cameraMove, BUILDING_4_SIZE);
    float b5 = quadSDF(samplePoint, BUILDING_5_POS + cameraMove, BUILDING_5_SIZE);
    float b6 = quadSDF(samplePoint, BUILDING_6_POS + cameraMove, BUILDING_6_SIZE);
    float b7 = quadSDF(samplePoint, BUILDING_7_POS + cameraMove, BUILDING_7_SIZE);
    float b8 = quadSDF(samplePoint, BUILDING_8_POS + cameraMove, BUILDING_8_SIZE);

    float b3_c = quadSDF(samplePoint, BUILDING_3_POS + cameraMove + dublicate_offset, BUILDING_3_SIZE);
    float b4_c = quadSDF(samplePoint, BUILDING_4_POS + cameraMove + dublicate_offset, BUILDING_4_SIZE);
    float b5_c = quadSDF(samplePoint, BUILDING_5_POS + cameraMove + dublicate_offset, BUILDING_5_SIZE);
    float b6_c = quadSDF(samplePoint, BUILDING_6_POS + cameraMove + dublicate_offset, BUILDING_6_SIZE);
    float b7_c = quadSDF(samplePoint, BUILDING_7_POS + cameraMove + dublicate_offset, BUILDING_7_SIZE);
    float b8_c = quadSDF(samplePoint, BUILDING_8_POS + cameraMove + dublicate_offset, BUILDING_8_SIZE);

    float b3_c_2 = quadSDF(samplePoint, BUILDING_3_POS + cameraMove + dublicate_offset * 2.0, BUILDING_3_SIZE);
    float b4_c_2 = quadSDF(samplePoint, BUILDING_4_POS + cameraMove + dublicate_offset * 2.0, BUILDING_4_SIZE);
    float b5_c_2 = quadSDF(samplePoint, BUILDING_5_POS + cameraMove + dublicate_offset * 2.0, BUILDING_5_SIZE);
    float b6_c_2 = quadSDF(samplePoint, BUILDING_6_POS + cameraMove + dublicate_offset * 2.0, BUILDING_6_SIZE);
    float b7_c_2 = quadSDF(samplePoint, BUILDING_7_POS + cameraMove + dublicate_offset * 2.0, BUILDING_7_SIZE);
    float b8_c_2 = quadSDF(samplePoint, BUILDING_8_POS + cameraMove + dublicate_offset * 2.0, BUILDING_8_SIZE);

    float f1 = quadSDF(samplePoint, FLOOR_POS + cameraMove + vec3(0.0, 0.0, -MOVE * t), FLOOR_SIZE);

    vec4 bb = backBuildingSDF(samplePoint, cameraMove + vec3(0.0, 0.0, -MOVE * t), 0.5);

    vec4 b1 = building1SDF(samplePoint, cameraMove, 0.0);
    vec4 b1_1 = building1SDF(samplePoint, cameraMove + dublicate_offset, appearT2);
    vec4 b1_2 = building1SDF(samplePoint, cameraMove + dublicate_offset * 2.0, appearT);

    vec4 b2 = building2SDF(samplePoint, cameraMove, 0.0);
    vec4 b2_1 = building2SDF(samplePoint, cameraMove + dublicate_offset, appearT2);
    vec4 b2_2 = building2SDF(samplePoint, cameraMove + dublicate_offset * 2.0, appearT);

    float sSDF = 0.0;
    float sSDF_1 = 0.0;
    float sSDF_2 = 0.0;

    float signMult_1_2 = step(0.1, mod(float(cycle + 2), 4.0)) * step(mod(float(cycle + 2), 4.0), 1.9);
    float signMult_1_1 = step(1.1, mod(float(cycle + 2), 4.0)) * step(mod(float(cycle + 2), 4.0), 2.9);
    float signMult_1 = step(2.1, mod(float(cycle + 2), 4.0)) * step(mod(float(cycle + 2), 4.0), 3.9);

    vec4 s = signSDF(samplePoint, cameraMove, 0.0, sSDF);
    vec4 s_1 = signSDF(samplePoint, cameraMove + dublicate_offset, appearT2, sSDF_1);
    vec4 s_2 = signSDF(samplePoint, cameraMove + dublicate_offset * 2.0, appearT, sSDF_2);

    float s2SDF = 0.0;
    float s2SDF_1 = 0.0;
    float s2SDF_2 = 0.0;

    float signMult2 = step(0.1, mod(float(cycle), 4.0)) * step(mod(float(cycle), 4.0), 1.9);
    float signMult1 = step(1.1, mod(float(cycle), 4.0)) * step(mod(float(cycle), 4.0), 2.9);
    float signMult = step(2.1, mod(float(cycle), 4.0)) * step(mod(float(cycle), 4.0), 3.9);

    vec4 s2 = sign2SDF(samplePoint, cameraMove, 0.0, s2SDF);
    vec4 s2_1 = sign2SDF(samplePoint, cameraMove + dublicate_offset, appearT2, s2SDF_1);
    vec4 s2_2 = sign2SDF(samplePoint, cameraMove + dublicate_offset * 2.0, appearT, s2SDF_2);

    float s3SDF = 0.0;
    float s3SDF_1 = 0.0;
    float s3SDF_2 = 0.0;

    float signMult_3_2 = step(0.1, mod(float(cycle + 1), 4.0)) * step(mod(float(cycle + 1), 4.0), 1.9);
    float signMult_3_1 = step(1.1, mod(float(cycle + 1), 4.0)) * step(mod(float(cycle + 1), 4.0), 2.9);
    float signMult_3 = step(2.1, mod(float(cycle + 1), 4.0)) * step(mod(float(cycle + 1), 4.0), 3.9);

    vec4 s3 = sign3SDF(samplePoint, cameraMove, 0.0, s3SDF);
    vec4 s3_1 = sign3SDF(samplePoint, cameraMove + dublicate_offset, appearT2, s3SDF_1);
    vec4 s3_2 = sign3SDF(samplePoint, cameraMove + dublicate_offset * 2.0, appearT, s3SDF_2);

    vec4 res = vec4(
                bb.rgb +
                b1.rgb +
                b1_1.rgb +
                b1_2.rgb +
                b2.rgb +
                b2_1.rgb +
                b2_2.rgb +

                sceneColorBySDF(b3, BUILDING_3_COLOR) + 
                sceneColorBySDF(b4, BUILDING_4_COLOR) + 
                sceneColorBySDF(b5, BUILDING_5_COLOR) + 
                sceneColorBySDF(b6, BUILDING_6_COLOR) + 
                sceneColorBySDF(b7, BUILDING_7_COLOR) + 
                sceneColorBySDF(b8, BUILDING_8_COLOR) + 

                sceneColorBySDF(b3_c, mix(BUILDING_3_COLOR, EMPTY_COLOR, appearT2)) + 
                sceneColorBySDF(b4_c, mix(BUILDING_4_COLOR, EMPTY_COLOR, appearT2)) + 
                sceneColorBySDF(b5_c, mix(BUILDING_5_COLOR, EMPTY_COLOR, appearT2)) + 
                sceneColorBySDF(b6_c, mix(BUILDING_6_COLOR, EMPTY_COLOR, appearT2)) + 
                sceneColorBySDF(b7_c, mix(BUILDING_7_COLOR, EMPTY_COLOR, appearT2)) + 
                sceneColorBySDF(b8_c, mix(BUILDING_8_COLOR, EMPTY_COLOR, appearT2)) + 

                sceneColorBySDF(b3_c_2, mix(BUILDING_3_COLOR, EMPTY_COLOR, appearT)) + 
                sceneColorBySDF(b4_c_2, mix(BUILDING_4_COLOR, EMPTY_COLOR, appearT)) + 
                sceneColorBySDF(b5_c_2, mix(BUILDING_5_COLOR, EMPTY_COLOR, appearT)) + 
                sceneColorBySDF(b6_c_2, mix(BUILDING_6_COLOR, EMPTY_COLOR, appearT)) + 
                sceneColorBySDF(b7_c_2, mix(BUILDING_7_COLOR, EMPTY_COLOR, appearT)) + 
                sceneColorBySDF(b8_c_2, mix(BUILDING_8_COLOR, EMPTY_COLOR, appearT)), 0.0);

    res = res + s * signMult_1 + s_1 * signMult_1_1 + s_2 * signMult_1_2 + s2 * signMult + s2_1 * signMult1 + s2_2 * signMult2 + s3 * signMult_3 + s3_1 * signMult_3_1 + s3_2 * signMult_3_2;

    dist = min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(min(
                min(b1.a, b2.a), b1_1.a), b1_2.a), b2_1.a), b2_2.a), b3), b4), b5), b6), b7), b8), b3_c), b4_c), b5_c), b6_c), b7_c), b8_c), 
                b3_c_2), b4_c_2), b5_c_2), b6_c_2), b7_c_2), b8_c_2), bb.a);

    if (signMult_1 > 0.0)
    {
        dist = min(dist, sSDF);
    }

    if (signMult_1_1 > 0.0)
    {
        dist = min(dist, sSDF_1);
    }

    if (signMult_1_2 > 0.0)
    {
        dist = min(dist, sSDF_2);
    }

    if (signMult > 0.0)
    {
        dist = min(dist, s2SDF);
    }

    if (signMult1 > 0.0)
    {
        dist = min(dist, s2SDF_1);
    }

    if (signMult2 > 0.0)
    {
        dist = min(dist, s2SDF_2);
    }

    if (signMult_3 > 0.0)
    {
        dist = min(dist, s3SDF);
    }

    if (signMult_3_1 > 0.0)
    {
        dist = min(dist, s3SDF_1);
    }

    if (signMult_3_2 > 0.0)
    {
        dist = min(dist, s3SDF_2);
    }

    return res;
}

vec4 shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = 0.0;
        vec4 col = sceneSDF(eye + depth * marchingDirection, dist);
        if (dist < EPSILON) {
			return col;
        }
        depth += dist;
        if (depth >= end) {
            return EMPTY_COLOR_4;
        }
    }
    return EMPTY_COLOR_4;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 0.0)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0 * 1.5, iResolution.xy, fragCoord);

    float t = GetCurrentTime();
    float tt = sin(t * 3.14 * 2.0);

    float jt = GetCurrentJumpTime();

    float jwt = (max(0.0, GetCurrentJumpTime() - JUMP_WAIT_TIME)) / (1.0 - JUMP_WAIT_TIME);


    float jtt = min(1.0, jwt / JUMP_ON_FLY_TIME);
    float jtl = 1.0 - (JUMP_ON_FLY_TIME + JUMP_OFF_FLY_TIME);
    float jtt2 = min(1.0, max(0.0, (jwt - jtl - JUMP_ON_FLY_TIME) / JUMP_OFF_FLY_TIME));

    vec3 eye = vec3(-0.25, -0.25, -4.0);

    float rotX = 0.25 * sin(jt * 3.14 * 2.0);
    mat4 viewToWorld = viewMatrix(eye, vec3(-0.1, 0.2, 0.2), vec3(rotX, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    vec4 col = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    float st = max(fragCoord.y / iResolution.y - 0.5, 0.0);
    col = mix(col, vec4(1.0, 0.0, 1.0, 0.2), st);
    fragColor = col;
}

#define TAU (2.0*3.1415926538)

vec2 rot(vec2 pos, float a) {
	float sn = sin(a);
	float cs = cos(a);
	return mat2(cs, -sn, sn, cs) * pos;
}

float map(float p, float ss, float se, float ds, float de)
{
    return ds + (p-ss)*(de-ds)/(se-ss);
}

float shade(vec2 pos, float dir) {
    pos = rot(pos, dir);
    float h = cos(pos.x);
    return map(pos.y, h, -h, 0.0, 1.0);
}

//http://gamedev.stackexchange.com/questions/59797/glsl-shader-change-hue-saturation-brightness
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


vec3 hsv2rgb_subtractive( in vec3 c ) {
    float frac = fract(c.x)*6.0;
    vec3 col = smoothstep(vec3(3,0,3),vec3(2,2,4),vec3(frac));
    col += smoothstep(vec3(4,3,4),vec3(6,4,6),vec3(frac)) * vec3(1, -1, -1);
    return mix(vec3(1), col, c.y) * c.z;
}


// CIELab to rgb conversion from https://www.shadertoy.com/view/llcXDN

float ref_X = 95.047;
float ref_Y = 100.000;
float ref_Z = 108.883;

vec3 xyz2rgb(vec3 xyz) {
    float var_X = xyz[0] * 0.01;
    float var_Y = xyz[1] * 0.01;
    float var_Z = xyz[2] * 0.01;

    float var_R = var_X * 3.2406 + var_Y * -1.5372 + var_Z * -0.4986;
    float var_G = var_X * -0.9689 + var_Y * 1.8758 + var_Z * 0.0415;
    float var_B = var_X * 0.0557 + var_Y * -0.2040 + var_Z * 1.0570;

    if (var_R > 0.0031308) var_R = 1.055 * pow(var_R, 1.0/2.4) - 0.055;
    else var_R = 12.92 * var_R;
    if (var_G > 0.0031308) var_G = 1.055 * pow(var_G, 1.0/2.4) - 0.055;
    else var_G = 12.92 * var_G;
    if (var_B > 0.0031308) var_B = 1.055 * pow(var_B, 1.0/2.4) - 0.055;
    else var_B = 12.92 * var_B;

    return vec3(var_R,var_G,var_B);
}

vec3 cielab2xyz(vec3 lab) {
    float var_Y = (lab[0] + 16.0) / 116.0;
    float var_X = lab[1] / 500.0 + var_Y;
    float var_Z = var_Y - lab[2] / 200.0;


	float py = var_Y*var_Y*var_Y;
    float px = var_X*var_X*var_X;
    float pz = var_Z*var_Z*var_Z;

    if (py > 0.008856) var_Y = py;
    else var_Y = (var_Y - 16.0 / 116.0) / 7.787;
    if (px > 0.008856) var_X = px;
    else var_X = (var_X - 16.0 / 116.0) / 7.787;
    if (pz > 0.008856) var_Z = pz;
    else var_Z = (var_Z - 16.0 / 116.0) / 7.787;

    float X = ref_X * var_X;
    float Y = ref_Y * var_Y;
    float Z = ref_Z * var_Z;
    return vec3(X, Y, Z);
}

vec3 cielab2rgb(vec3 lab) {
    return xyz2rgb(cielab2xyz(lab));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord;
    vec2 center = iResolution.xy * 0.5;

    float inside  = 0.3 * iResolution.y;
    float mid     = 0.35 * iResolution.y;
    float outside = 0.45 * iResolution.y;

    vec2 off = center - uv;

    float h = mod(iTime*0.1, 1.0);
    if(iMouse.z > 0.0){
        vec2 p = normalize(center - iMouse.xy);
        h = atan(-p.y, -p.x) / TAU;
    }

    float d = length(off);
    if(d < inside){
        off /= inside;

        float v = shade(off, 0.0/3.0 * TAU);
        float s = shade(off, -1.0/4.0 * TAU);

        fragColor = vec4(hsv2rgb(vec3(h, s, v)), 1.0);
    } else if (mid < d && d < outside) {
        off = normalize(off);
        float h = atan(-off.y, -off.x) / TAU;
        fragColor = vec4(hsv2rgb_subtractive(vec3(h, 0.85, 1.0)), 1.0);
    } else {
        fragColor = vec4(0.1, 0.13, 0.15, 1.0);
    }
}

