//Corrected by FabriceNeyret2,
vec3 ponerBorde2(vec3 pColObj, vec3 colOut, float distObj )
{
  colOut = distObj > 0. ? colOut : pColObj;
  colOut = mix(colOut,vec3(0) ,smoothstep( fwidth(distObj), 0., abs(distObj) ));
  return colOut;
}



vec3 ponerBorde(vec3 pColObj, vec3 colOut, float distObj )
{
  colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj),0.0));
  return colOut;
}



float IsoLineaFra(vec2 p)
{
    float x,y;
    float a, r;
    x=p.x; y=p.y;


    r = length(p); //sqrt(x*x+y*y);
    a = atan(y,x);

    float fra = r - 1.0 + sin(3.0*a+2.0*r*r)/2.0;
    return fra;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    uv*=1.5;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float isf1= IsoLineaFra(uv);
