void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv  = (fragCoord - iResolution.xy * .5)/iResolution.x;


    float ang      = PI * ROTATION_SPEED * iTime;
    float s        = sin(ang);
    float c        = cos(ang);
    mat2  rotation = mat2(c, s, -s, c);

    uv = rotation * uv;
    uv *= sin(iTime * .3) * .3 + 1.;

    vec2 guv = fract(uv * GRID_SIZE) - 0.5;
