    for (int x = -PROXIMITY_CHECK; x <= PROXIMITY_CHECK; x++) {
        for (int y = -PROXIMITY_CHECK; y <= PROXIMITY_CHECK; y++) {

            vec2 relativeUV = guv + vec2(x, y);

            circles += circle(vec2(0), radius, relativeUV);

        }
