float distToLine(vec2 A, vec2 B, vec2 p){

    vec2 PA = p - A;
    vec2 BA = B - A;
    float d = dot(PA,BA);
    float t = clamp(d/(length(BA)*length(BA)),0., 1.);//Vektorprodukt
    vec2 normal = PA - BA*t;
    return length(normal);

}

