void mainImage( out vec4 fragColor, in vec2 fragCoord, vec3 _ro, vec3 _rd)
{  
   // p: position on the ray
   // d: direction of the ray
   vec3 p = vec3(0.,0.,2.);
   vec3 d = vec3((fragCoord.xy/(0.5*iResolution.xy)-1.)*vec2(iResolution.x/iResolution.y,1.0), 0.) - p;
   d = normalize(d); 

   p = _ro;
   d = _rd;

   // ld, td: local, total density 
   // w: weighting factor
   float ld=0., td=0.;
   float w=0.;

   // total color
   vec3 tc = vec3(0.);

   // i: 0 <= i <= 1.
   // r: length of the ray
   // l: distance function
   float r=0., l=0., b=0.;

   // rm loop
   for (float i=0.; (i<1.); i+=1./64.) {
	   if(!((i<1.) && (l>=0.001*r) && (r < 50.)&& (td < .95)))
		   break;
      // evaluate distance function
      l = f(p) * 0.5;

      // check whether we are close enough (step)
      // compute local density and weighting factor 
      const float h = .05;
      ld = (h - l) * step(l, h);
      w = (1. - td) * ld;   

      // accumulate color and density
      tc += w; // * hsv(w, 1., 1.); // * hsv(w*3.-0.5, 1.-w*20., 1.); 
      td += w;

      td += 1./200.;

      // enforce minimum stepsize
      l = max(l, 0.03);

      // step forward
      p += l*d;
      r += l;
   }  

   #ifdef SCATTERING
   // simple scattering approximation
   tc *= 1. / exp( ld * 0.4 ) * 1.25;
   #endif

   fragColor = vec4(tc, 1.0); //vec4(tc.x+td*2., ld*3., 0, tc.x);
}

const float	sideAngle = radians(35.0);
const float	horizontalAngle = radians(14.0);
const float	cameraSize      = 8.0;
const float	aspectRatio     = 0.75;
const float quiltColumns	= 8.0;
const float quiltRows		= 6.0;
const float nearClip        = 6.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
	vec2	coord = fragCoord * vec2(quiltColumns,quiltRows) / iResolution.xy;
	vec2	fract = fract(coord);
	vec2	floor = floor(coord);
	float	imageId = floor.x + floor.y * quiltColumns;
	float	valueId = imageId / (quiltColumns * quiltRows - 1.);
	float	dist = (cameraSize / 2.) / tan(horizontalAngle / 2.);
	float	minCam = -(cameraSize / 2.) - tan(sideAngle / 2.) * dist;

	vec3	camPos = mix(vec3(minCam,0,dist),vec3(-minCam,0,dist),valueId);
	vec3	screenPos = vec3((fract.x-0.5)*cameraSize,(fract.y-0.5)*cameraSize/aspectRatio,0);

	vec4	color;
	vec3 	dirRay = normalize(screenPos-camPos);
	mainImage( color, fract*(iResolution.xy), screenPos - vec3(0, 0, 6) - nearClip * dirRay, dirRay );

    fragColor = color;
}

// Hue to RGB function from Fabrice's shadertoyunofficial blog:
#define hue2rgb(hue) (0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0)))

// Number of vertices
#define N 6

void polyLerpWeights(in vec2 p, in vec2[N] poly, out float[N] weights) {
    float norm = 0.0;
    for(int n = 0; n < N; n++) {
        vec2 a = poly[(n - 1 + N) % N] - p;
        vec2 b = poly[n] - p;
        vec2 c = poly[(n + 1) % N] - p;

        float d = length(b);
        float ab = dot(a, b), bc = dot(b, c), ad = length(a) * d, dc = d * length(c);
        float u = sqrt(max(0.0, (ad - ab) / (ad + ab))) * sign(a.x * b.y - a.y * b.x);
        float v = sqrt(max(0.0, (dc - bc) / (dc + bc))) * sign(b.x * c.y - b.y * c.x);

        weights[n] = (u + v) / d;
        norm += weights[n];
    }

    for(int n = 0; n < N; n++) weights[n] /= norm;
}

// Simple point in polygon test extracted from @iq's polygon
// distance shader: https://www.shadertoy.com/view/wdBXRW
bool pointInPolygon(in vec2 p, in vec2[N] v) {
    bool res = false;
    for (int i=0, j=N - 1; i < N; j = i, i++) {
        vec2 e = v[j] - v[i];
        vec2 w =    p - v[i];

        // Winding number from http://geomalgorithms.com/a03-_inclusion.html
        bvec3 c = bvec3(p.y >= v[i].y, p.y < v[j].y, e.x * w.y > e.y * w.x);
        if (all(c) || all(not(c))) res = !res;  
    }

    return res;
}

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    float time = 0.5 * iTime;
    vec3 color = vec3(0.0);

    // Create polygon
    float t1 = 0.5 * time, t2 = 1.25 * time, t3 = 1.5 * time;
    float c1 = 0.4 * cos(t1), s1 = 0.4 * sin(t1);
    float c2 = 0.4 * cos(t2), s2 = 0.4 * sin(t2);
    float c3 = 0.4 * cos(t3), s3 = 0.4 * sin(t3);

    vec2[N] poly;
    poly[0] = vec2(1.0, 0.0);
    poly[1] = vec2(0.5, sqrt(0.75));
    poly[2] = vec2(-poly[1].x, poly[1].y);
    poly[3] = vec2(-1.0, 0.0);
    poly[4] = -poly[1];
    poly[5] = vec2(poly[1].x, -poly[1].y);
    vec2[N] anim = poly; // Copy original positions for animating

    // Distort the vertices
    for (int n=0; n < N; n++) {
        float id = float(n + 3);
        float freq = Hash11(id * 393.84) * 2.0 - 1.0;
        float phase = Hash11(id * 183.37);
        float amp = Hash11(id * 275.35) * 1.25;
        float ang = iTime * freq + phase;
        anim[n] += vec2(cos(ang), sin(ang)) * amp;
        anim[n] *= 0.25;
    }

    // Compute interpolation weights
    float[N] weights;
    polyLerpWeights(uv, anim, weights);

    // Interpolate texture coordinates from vertices
    vec2 texUv = vec2(0.0);
    for (int n=0; n < N; n++) texUv += poly[n] * weights[n];
    color = texture(iChannel0, texUv).rgb;
    color -= smoothstep(1.25 * fwidth(texUv.x), 0.0, abs(mod(texUv.x, 0.2) - 0.1));
    color -= smoothstep(1.25 * fwidth(texUv.y), 0.0, abs(mod(texUv.y, 0.2) - 0.1));

    // Get distance to edges
    float d = 1000000.0;
    for (int n=0; n < N; n++) d = min(d, sdLine(uv, anim[n], anim[(n + 1) % N]));

    color = mix(color, vec3(1.0), smoothstep(unit, 0.0, d));

    fragColor = vec4(color, 1.0);
}

#define B 4.0
#define N 200
#define PERIOD 12.0
#define C_MUL(a,b) vec2(a.x*b.x - a.y*b.y, a.x*b.y+a.y*b.x)


// Ref:
// https://www.shadertoy.com/view/ttVSDW
// https://mathr.co.uk/blog/2018-03-12_perturbation_algebra.html

float mandelbrot_pert(vec2 c, vec2 dc) {
   vec2 z = vec2(.0);
   vec2 dz = vec2(.0);
   float n = .0;
   for(int i = 0; i < N; ++i) {
     // p(Z) = 2*Z*dz + dz^2 + c 
     dz = C_MUL((2.0*z+dz),dz) + dc;
     // f(z) = z^2 + c
     z = C_MUL(z, z) + c; 
     // check if perturbation escapes
     if( dot(dz,dz)>(B*B) ) {
         n = float(i)/float(N);
         break;
     }
   }
   return n;
}

vec3 image(vec2 fragCoord) {
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0; // remap uv to [-1, 1]
    uv.x *= iResolution.x / iResolution.y;

    // calculate and apply zoom
    float fzoom = .62 + .38*cos(iTime/PERIOD);
    float zoom = pow(fzoom, 12.0);

    vec2  c = vec2(-.745,.186);
    vec2 dc = uv*zoom;

    float f = mandelbrot_pert(c, dc)*2.0*fzoom-0.7; 

    // calculate color
    vec3 col = mix(vec3(.81,.06,.13), vec3(.80,.40,.0), f*f);

    return (f*f*(2.0 - 4.0*f))*col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	// anti-aliasing
	fragColor = vec4(image(fragCoord.xy + vec2(0,0)), 1.0f);
	fragColor.rgb += image(fragCoord.xy + vec2(.5,.0));
	fragColor.rgb += image(fragCoord.xy + vec2(.0,.5));
	fragColor.rgb += image(fragCoord.xy + vec2(.5,.5));
    fragColor.rgb += image(fragCoord.xy + vec2(.25,.25));
	fragColor.rgb += image(fragCoord.xy + vec2(.75,.25));
	fragColor.rgb += image(fragCoord.xy + vec2(.25,.75));
	fragColor.rgb += image(fragCoord.xy + vec2(.75,.75));
	fragColor.rgb /= 8.0;
}

#define ITERS 12
const float scale = 2.;
const float scale1 = 1.275;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    //uv.y += (iTime)/25.0;
    float s1 = scale1*scale+uv.y+iTime/4.0;
    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {
            uv= -fract(-uv-((vec2(uv.x/scale1-uv.y/scale1,uv.y/scale-uv.x/scale)/(scale))))/scale1;
            uv.x *= -scale1;
            uv = fract(uv.yx/s1)*s1;
            uv.y /= scale1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}

/*
This is not totally correct since it is not moving perfecty linearly
but its good enough.

It does becomes obvious with with high velocity (or low framerate) and nonlinear
turns though.

The problem consists of finding how much of the frame duration the
sample point was over the disk which involves computing the intersection
of the relative path of the sample point with the disk and computing the gap
between the two intersections.
*/

// I'm getting lazy on my derivatives :P
vec2 var2dual(in float x) { return vec2(x, 1.0); }
vec2 fMul(in vec2 a, in vec2 b) { return vec2(a.x * b.x, a.x * b.y + a.y * b.x); }
vec2 fDiv(in vec2 a, in vec2 b) { return vec2(a.x / b.x, (b.x * a.y - a.x * b.y) / (b.x * b.x)); }
vec2 fPow(in vec2 a, in vec2 b) { float q = pow(a.x, b.x); return vec2(q, q * (b.x * a.y / a.x + b.y * log(a.x))); }
vec2 fSquare(in vec2 z) { return vec2(z.x * z.x, 2.0 * z.x * z.y); }
vec2 fExp(in vec2 z, in float base) { float q = pow(base, z.x); return vec2(q, q * z.y); }
vec2 fSin(in vec2 z) { return vec2(sin(z.x), cos(z.x) * z.y); }
vec2 fCos(in vec2 z) { return vec2(cos(z.x), -sin(z.x) * z.y); }
vec2 fMin(in vec2 a, in vec2 b) { return a.x < b.x ? a : b; }
vec2 fMax(in vec2 a, in vec2 b) { return a.x > b.x ? a : b; }
vec2 fClamp(in vec2 z, in vec2 edge0, in vec2 edge1) { return fMax(edge0, fMin(edge1, z)); }
vec2 fSmoothstep(in vec2 edge0, in vec2 edge1, in vec2 z) { z = fClamp(fDiv(z - edge0, edge1 - edge0), vec2(0.0), vec2(1.0, 0.0)); vec2 sq = fSquare(z); return 3.0 * sq - 2.0 * fMul(sq, z);}
vec2 fMix(in vec2 a, in vec2 b, in vec2 t) { return a + fMul(b - a, t); }

// p: sample point
// o: disk origin
// r: disk radius
// v: disk velocity
// dt: frame duration
float movingDisk(in vec2 p, in vec2 o, in float r, in vec2 v, in float dt) {
    vec2 p0 = p - o, p1 = v * dt;

    float a = dot(p1, p1);
    float b = dot(p0, p1);
    float c = dot(p0, p0) - r * r;

    float h = b * b - a * c;
    if (h > 0.0) {
        vec2 t = clamp((vec2(-1.0, 1.0) * sqrt(h) - b) / a, 0.0, 1.0);
        return t.y - t.x;
    }

    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    vec2 diskX = 0.5 * fCos(6.0 * var2dual(iTime));
    vec2 diskY = 0.5 * fMul(fCos(6.0 * var2dual(iTime)), fSin(6.0 * var2dual(iTime)));
    color += movingDisk(uv, vec2(diskX.x, diskY.x), 0.1, vec2(diskX.y, diskY.y), iTimeDelta);

    fragColor = vec4(color, 1.0);
}

void disk(vec2 uv, vec2 center, float radius, vec3 color, inout vec3 pixel) 
{
    if( length(uv-center) < radius) 
    {
        pixel = color;
    }
}
void centerPixCoord(inout vec2 coord)
{
    //OffSet to center
    coord -= 0.5 * iResolution.xy;
    //Remap for y [-1 ; 1]  
    coord /= 0.5 * iResolution.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    centerPixCoord(uv);

    vec3 pixel = vec3(uv.x>.0,uv.y>.0,.0);

    vec2 mousePos = iMouse.xy;
    centerPixCoord(mousePos);

    vec2 lastClick = iMouse.zw;
    centerPixCoord(lastClick);

    disk(uv, lastClick, 0.2 + abs(0.2 * sin(1.0*iTime)), vec3(1.0)-vec3(uv.x>0.,uv.y>0.,.0), pixel);
    disk(uv, mousePos, 0.2 + abs(0.1 * sin(2.0*iTime)), vec3(1.0)-vec3(uv.x>0.,uv.y>0.,.0), pixel);

    // Output to screen
    fragColor = vec4(pixel,1.0);
}

float speed = 5.;

vec2 rotate(in float angle, inout vec2 st)
{
    st -= .5;
    st = mat2(cos(angle), -sin(angle),
              sin(angle), cos(angle)) * st;
    st += .5;
    return st;
}

vec4 box(in vec2 size, in vec2 st) 
{
    size = vec2(.5) - size*.5;
    vec2 uv = smoothstep(size, size+vec2(.001), st);
    uv *= smoothstep(size, size+vec2(.001), vec2(1.)-st);

    return vec4(uv.x * uv.y);
}

vec4 cross(in float size, in vec2 st)
{
    return box(vec2(size, size/4.), st) + box(vec2(size/4., size), st);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord.xy / iResolution.xy );
    uv.x *= 1.5;
    uv.x -= .25;

    uv.y += sin(iTime) * .1;
    uv.x += cos(iTime) * .1;

    uv = rotate(iTime * speed, uv);

    fragColor = cross(.25, uv);
}

/*
    Returns hash in range [0.0; 1.0].
*/
float sample3DHashUI32(uint x, uint y, uint z)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint enthropy0 = 1200u;
    const uint enthropy1 = 4500u;
    const uint enthropy2 = 6700u;
    const uint enthropy3 = 8900u;

    // Use linear offset method to mix coordinates.
    uint value = 
        z * enthropy3 * enthropy2 +
        y * enthropy2 +
        x;

    // Calculate hash.
	value += enthropy1;
	value *= 445593459u;
	value ^= enthropy0;

    // 1.0f / 4294967295.0f = 2.32830644e-10

    return float(value * value * value) * 2.32830644e-10;
}

float WorleyNoise3D(float u, float v, float w)
{
    // Fractial part.
    float fractU = u - floor(u);
    float fractV = v - floor(v);
    float fractW = w - floor(w);

    // Integer part.
    u = floor(u);
    v = floor(v);
    w = floor(w);

    float minDistance = 3.40282347e+37f; // FL_MAX = 3.40282347e+38f

    for (float z = -1.0f; z < 2.0f; z += 1.0f)
    {
        for (float y = -1.0f; y < 2.0f; y += 1.0f)
        {
            for (float x = -1.0f; x < 2.0f; x += 1.0f)
            {
                // Pseudorandom sample coordinates in corresponding cell.
                float xSample = x + sample3DHashUI32(uint(u + x), uint(v + y), uint(w + z));
                float ySample = y + sample3DHashUI32(uint(u + x), uint(v + y), uint(w + z));
                float zSample = z + sample3DHashUI32(uint(u + x), uint(v + y), uint(w + z));

                // Distance from pixel to pseudorandom sample.
                //float distance = 
                    //sqrt(
                        //(fractU - xSample) * (fractU - xSample) +
                        //(fractV - ySample) * (fractV - ySample) +
                        //(fractW - zSample) * (fractW - zSample));

                float distance = 
                        (fractU - xSample) * (fractU - xSample) +
                        (fractV - ySample) * (fractV - ySample) +
                        (fractW - zSample) * (fractW - zSample);

                // Mistance from pixel to pseudorandom sample.
                minDistance = min(minDistance, distance);
            }
        }
    }

    return minDistance;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv;

    if (fragCoord.x < iResolution.x * 0.5) { uv = fragCoord/iResolution.xy * 6.0f; }
    else { uv = fragCoord/iResolution.xy * 18.0f; }

    float _u = uv.x + iMouse.x * 0.1f;
    float _v = uv.y + iMouse.y * 0.1f;
    float _w = uv.x + iMouse.y * 0.1f + iTime * 0.2f;

    float gray = WorleyNoise3D(_u, _v, _w) * 0.5f;
    gray += WorleyNoise3D(_u * 2.054f, _v * 2.210f,  _w * 2.210f) * 0.055f;
    gray += WorleyNoise3D(_u * 4.554f, _v * 4.710f,  _w * 2.210f) * 0.0325f;

    // Output to screen
    fragColor = vec4(vec3(gray),1.0);
}

// This is just a spin off from https://www.shadertoy.com/view/XsXSWS applying some distortion and minor tweaks

#define timeScale 			iTime * 1.0
#define fireMovement 		vec2(-0.01, -0.5)
#define distortionMovement	vec2(-0.01, -0.3)
#define normalStrength		40.0
#define distortionStrength	0.1

// #define DEBUG_NORMAL

/** NOISE **/
float rand(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 hash( vec2 p ) {
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p ) {
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y)*K1 );

    vec2 a = p - i + (i.x+i.y)*K2;
    vec2 o = step(a.yx,a.xy);    
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );
}

float fbm ( in vec2 p ) {
    float f = 0.0;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    f  = 0.5000*noise(p); p = m*p;
    f += 0.2500*noise(p); p = m*p;
    f += 0.1250*noise(p); p = m*p;
    f += 0.0625*noise(p); p = m*p;
    f = 0.5 + 0.5 * f;
    return f;
}

/** DISTORTION **/
vec3 bumpMap(vec2 uv) { 
    vec2 s = 1. / iResolution.xy;
    float p =  fbm(uv);
    float h1 = fbm(uv + s * vec2(1., 0));
    float v1 = fbm(uv + s * vec2(0, 1.));

   	vec2 xy = (p - vec2(h1, v1)) * normalStrength;
    return vec3(xy + .5, 1.);
}

/** MAIN **/
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - (iResolution * 0.5).xy)/iResolution.yy;

    // Reflect Hor/vertical
    uv.x = abs(uv.x);
    uv.y= abs(uv.y);

    float angle = atan(uv.y, uv.x);
    float radius = length(uv);

    uv = vec2(radius * 1.0, angle / (2.0 * 3.141592));
    uv = vec2(angle / (2.0 * 3.141592), radius * (-0.5 + 1.0 * sin(iTime)));

    vec3 normal = bumpMap(uv * vec2(1.0, 0.3) + distortionMovement * timeScale);

    #ifdef DEBUG_NORMAL
    	fragColor = vec4(normal, 1.0);
    	return;
    #endif

    vec2 displacement = clamp((normal.xy - .5) * distortionStrength, -1., 1.);
    uv += displacement; 

    vec2 uvT = (uv * vec2(1.0, 0.5)) + timeScale * fireMovement;
    float n = pow(fbm(8.0 * uvT), 1.0);    

    float gradient = pow(1.0 - uv.y, 2.0) * 5.;
    float finalNoise = n * gradient;

    vec3 color = finalNoise * vec3(2.*n, n*n*n*n, 2.*n*n*n);
    fragColor = vec4(color, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xx;

    // Time varying pixel color

    // x sinus:
    float pop1 = sin(uv.x*10. + iTime)*.5+.5;
    // rotate sinus : 
    float pop2 = sin(10.*(uv.x*sin(iTime)-.5 + uv.y*cos(iTime)-.5))*.5+.5;
    // circles
    vec2 c =vec2(uv.x-.5+sin(iTime), uv.y-.5+cos(iTime));
    float pop3 = sin(sqrt(100.*(c.x*c.x+c.y*c.y)))*.5+.5;
    // merge waves
    float pop4 = (pop1 + pop2 + pop3);

    vec3 col = vec3(sin(pop4*3.),cos(pop4*3.),.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// "Ten Intersecting Tetrahedra" by dr2 - 2021
