#define AA  1

float PrBox2Df (vec2 p, vec2 b);
float PrTetDf (vec3 p, float d);
float PrDodecDf (vec3 p, float r);
float Maxv2 (vec2 p);
mat3 VToRMat (vec3 v, float a);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir[4], ltCol[4];
float tCur, dstFar;
int idObj;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float TetFrameDf (vec3 p, float w)
{
  vec3 q;
  q = p;
  p = abs (p);
  q = mix (q, q.yzx, step (Maxv2 (p.yz), p.x));
  q = mix (q, q.zxy, step (Maxv2 (p.zx), p.y));
  q = mix (q, vec3 (q.x, - q.yz).yxz, step (q.z, 0.)) - vec3 (-1., 1., 1.) / sqrt (3.);
  return PrBox2Df (vec2 (0.5 * (q.x + q.y), q.z), vec2 (w));
}

float ObjDf (vec3 p)
{
  mat3 m;
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  m = VToRMat (vec3 (0., sin (atan (2. / (sqrt (5.) + 1.)) + vec2 (0.5 * pi, 0.))), 2. * pi / 5.);
  // rotation matrix from "Ico-Twirl", where atan (1. / phi) = 0.55357435
  q = p;
  for (int k = 0; k < 5; k ++) {
    d = TetFrameDf (q, 0.03);
    DMIN (1);
    d = PrTetDf (q, 0.27);
    DMIN (2);
    q = m * q;
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  return PrDodecDf (p, 0.74);
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = TrObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 24; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

float SphHit (vec3 ro, vec3 rd, float rad)
{
  float b, w;
  b = dot (rd, ro);
  w = b * b + rad * rad - dot (ro, ro);
  return (w > 0.) ? - b - sqrt (w) : dstFar;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 sumD, sumS, col, colB, vn, roo;
  float dstObj, dstTrObj, nDotL, sh, s;
  roo = ro;
  s = SphHit (ro, rd, 1.2);
  dstObj = (s < dstFar) ? ObjRay (ro, rd) : dstFar;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vn = VaryNf (128. * ro, vn, 0.2);
    if (idObj == 1) col4 = vec4 (0.9, 0.9, 0.9, 0.5);
    else if (idObj == 2) col4 = vec4 (1., 1., 1., 0.2);
    sumD = vec3 (0.);
    sumS = vec3 (0.);
    for (int k = VAR_ZERO; k < 4; k ++) {
      nDotL = max (dot (vn, ltDir[k]), 0.);
      sh = ObjSShadow (ro, ltDir[k]);
      sumD += ltCol[k] * col4.rgb * sh * nDotL * nDotL *
         smoothstep (0.95, 0.98, dot (normalize (3. * ltDir[k] - ro), ltDir[k]));
      sumS += ltCol[k] * col4.a * step (0.95, sh) * pow (max (0., dot (ltDir[k],
         reflect (rd, vn))), 32.);
    }
    col = 0.05 * col4.rgb + 0.95 * sumD + sumS;
  } else {
    col = vec3 (0.1);
  }
  ro = roo;
  dstTrObj = (s < dstFar) ? TrObjRay (ro, rd) : dstFar;
  if (dstTrObj < min (dstObj, dstFar)) {
    ro += dstTrObj * rd;
    vn = TrObjNf (ro);
    colB = vec3 (0.);
    for (int k = VAR_ZERO; k < 4; k ++) {
      nDotL = max (dot (vn, ltDir[k]), 0.);
      colB += ltCol[k] * nDotL * nDotL *
         smoothstep (0.9, 0.95, dot (normalize (3. * ltDir[k] - ro), ltDir[k]));
    }
    colB = vec3 (0.05) + 0.95 * colB;
    col = mix (col, colB, 0.05 + 0.95 * pow (1. - max (- dot (rd, vn), 0.), 5.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, e;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.3 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -7.);
  zmFac = 6.;
  dstFar = 30.;
  e = vec2 (1., -1.);
  for (int k = VAR_ZERO; k < 4; k ++) {
    ltDir[k] = normalize ((k < 2) ? ((k == 0) ? e.xxx : e.xyy) : ((k == 2) ? e.yxy : e.yyx));
    ltDir[k].xy = Rot2D (ltDir[k].xy, 0.13 * pi * tCur);
    ltDir[k].xz = Rot2D (ltDir[k].xz, 0.17 * pi * tCur);
  }
  ltCol[0] = vec3 (1., 0.2, 0.2);
  ltCol[1] = ltCol[0].gbr;
  ltCol[2] = ltCol[0].brg;
  ltCol[3] = 0.8 * ltCol[0].rrg;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (Maxv2 (d), 0.) + length (max (d, 0.));
}

float PrTetDf (vec3 p, float d)
{
  vec2 e;
  e = vec2 (1., -1.) / sqrt (3.);
  return max (max (dot (p, e.yxx), dot (p, e.xyx)), max (dot (p, e.xxy), dot (p, e.yyy))) - d;
}

float PrDodecDf (vec3 p, float d)
{
  vec3 e;
  float s;
  e = vec3 ((sqrt (5.) + 1.) / 2., 1., 0.) / sqrt (5.);
  s = 0.;
  for (int k = 0; k < 4; k ++) {
    s = max (s, max (dot (p, e), max (dot (p, e.yzx), dot (p, e.zxy))));
    e.x = - e.x;
    if (k == 1) e.y = - e.y;
  }
  return s - d;
}

mat3 VToRMat (vec3 v, float a)
{
  mat3 m;
  vec3 w, b1, b2, bp, bm;
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  w = (1. - cs.x) * v * v + cs.x;
  b1 = (1. - cs.x) * v.xzy * v.yxz;
  b2 = - cs.y * v.zyx;
  bp = b1 + b2;
  bm = b1 - b2;
  m[0][0] = w.x;  m[1][1] = w.y;  m[2][2] = w.z;
  m[0][1] = bp.x;  m[1][0] = bm.x;
  m[2][0] = bp.y;  m[0][2] = bm.y;
  m[1][2] = bp.z;  m[2][1] = bm.z;
  return m;
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

// Finds the value of bottom right 
// corner of a ring with given depth
// Will also find upper left corner, 
// if negative depth is given
int calcBR(int depth)
{
    return 4 * depth * depth - 2 * depth;
}

// Finds the value of upper right 
// corner of a ring with given depth
// Will also find botton left corner,
// if negative depth is given
int calcUR(int depth)
{
    int flag = depth > 0 ? 1 : 0;
    return 4 * (depth + 1 * flag) * (depth + 1 * flag) - 4 * (depth + 1) * flag;
}	

// Given coordinates x and y, 
// returns a value from a grid 
// spiral of numbers
int calcNum(int x, int y) 
{
if(x == y)
    return calcBR(x);
else if(-x == y)
    return calcUR(x);
else if(abs(y) > abs(x))
    return calcBR(y) + abs(y) + x * (y<0?1:-1);
else
    return calcBR(x) - abs(x) - y * (x<0?1:-1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -iResolution/2 to iResolution/2)
    vec2 uv = fragCoord - iResolution.xy/2.0;//+ vec2(iResolution.y / 2.0, -iResolution.y / 2.0);
    float scale = 4.0;

    // Scales pixel coordinates by a given amount
    uv *= float(scale);

    ///CRITERIA///
    // The criteria, have fun and test out different values
    int criteria = int(uv.x*uv.y/(iTime*100.0));// + int(iTime*2.0);

    // Nested for loops make sure that no values 
    // are skipped when scaling up the resolution
    for(int i = 0; i < int(scale); i+=1)
    {
        for(int j = 0; j < int(scale); j+=1)
        {
            int num = calcNum(int(uv.x)+i, int(uv.y)+j);

            // Checks whether a number is divisible by criteria
            // if so draw it and return

            // Edit: checking whether the modulus of num divided by criteria is equal 
            // to modulus of time divided by criteria, creates a shifting effect:
            // int(mod(-iTime*50.0, float(criteria)))
            if(num%criteria == 0)
            {
                fragColor = vec4(1.0);
                return;
            }
        }
    }
    fragColor = vec4(0.0);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1

float fprand(vec2 uv, float f){
    return sin(uv.y*f)+cos(uv.x*f);
}

float noise(in vec2 uv)
{
    return sin(uv.x)+cos(uv.y);
}


#define OCTAVES 8
float fbm(in vec2 uv)
{
    //this function generates the terrain height
    uv /= 6.0;
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.8;

    for (int i = 0; i < OCTAVES; i++)
    {
        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = max(value,value+(.25-abs(noise(uv * freq)) * amplitude));

        amplitude *= .55;

        freq *= 1.75+value;

        uv = uv.yx;
    }

    return 1.-value;
}



struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 3.0;
    return fbm(uv/factor)*factor;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 0.05;
    #endif

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

#define TAU (2.0*3.1415926538)

vec2 rot(vec2 pos, float a) {
	float sn = sin(a);
	float cs = cos(a);
	return mat2(cs, -sn, sn, cs) * pos;
}

float map(float p, float ss, float se, float ds, float de)
{
    return ds + (p-ss)*(de-ds)/(se-ss);
}

float shade(vec2 pos, float dir) {
    pos = rot(pos, dir);
    float h = cos(pos.x);
    return map(pos.y, h, -h, 0.0, 1.0);
}

//http://gamedev.stackexchange.com/questions/59797/glsl-shader-change-hue-saturation-brightness
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


vec3 hsv2rgb_subtractive( in vec3 c ) {
    float frac = fract(c.x)*6.0;
    vec3 col = smoothstep(vec3(3,0,3),vec3(2,2,4),vec3(frac));
    col += smoothstep(vec3(4,3,4),vec3(6,4,6),vec3(frac)) * vec3(1, -1, -1);
    return mix(vec3(1), col, c.y) * c.z;
}

#define cbrtf(x)  ( sign(x)*pow(abs(x),1./3.) )

// linear_srgb_to_oklab
vec3 rgb2oklab(vec3 c) 
{
    float l = 0.4122214708f * c.r + 0.5363325363f * c.g + 0.0514459929f * c.b;
	float m = 0.2119034982f * c.r + 0.6806995451f * c.g + 0.1073969566f * c.b;
	float s = 0.0883024619f * c.r + 0.2817188376f * c.g + 0.6299787005f * c.b;

    float l_ = cbrtf(l);
    float m_ = cbrtf(m);
    float s_ = cbrtf(s);

    return vec3(
        0.2104542553f*l_ + 0.7936177850f*m_ - 0.0040720468f*s_,
        1.9779984951f*l_ - 2.4285922050f*m_ + 0.4505937099f*s_,
        0.0259040371f*l_ + 0.7827717662f*m_ - 0.8086757660f*s_
    );
}

// oklab_to_linear_srgb
vec3 oklab2rgb(vec3 c) 
{
    float l_ = c.x + 0.3963377774f * c.y + 0.2158037573f * c.z;
    float m_ = c.x - 0.1055613458f * c.y - 0.0638541728f * c.z;
    float s_ = c.x - 0.0894841775f * c.y - 1.2914855480f * c.z;

    float l = l_*l_*l_;
    float m = m_*m_*m_;
    float s = s_*s_*s_;

    return vec3(
		+4.0767416621f * l - 3.3077115913f * m + 0.2309699292f * s,
		-1.2684380046f * l + 2.6097574011f * m - 0.3413193965f * s,
		-0.0041960863f * l - 0.7034186147f * m + 1.7076147010f * s
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord;
    vec2 center = iResolution.xy * 0.5;

    float inside  = 0.3 * iResolution.y;
    float mid     = 0.35 * iResolution.y;
    float outside = 0.45 * iResolution.y;

    vec2 off = center - uv;

    float h = mod(iTime*0.1, 1.0);
    if(iMouse.z > 0.0){
        vec2 p = normalize(center - iMouse.xy);
        h = atan(-p.y, -p.x) / TAU;
    }

    float d = length(off);
    if(d < inside){
        off /= inside;

        float v = shade(off, 0.0/3.0 * TAU);
        float s = shade(off, -1.0/4.0 * TAU);

        //fragColor = vec4(hsv2rgb(vec3(h, s, v)), 1.0);
        fragColor = vec4(oklab2rgb(vec3(v, s, h)), 1.0);
    } else if (mid < d && d < outside) {
        off = normalize(off);
        float h = atan(-off.y, -off.x) / TAU;
        fragColor = vec4(hsv2rgb_subtractive(vec3(h, 0.9, 1.0)), 1.0);
    } else {
        fragColor = vec4(0.1, 0.13, 0.15, 1.0);
    }
}

