vec3 opU( vec3 d1, vec3 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

// worldMap defintion

vec3 worldMap( in vec3 pos )
{
    vec3 res = vec3(1e10, 0.0, 1.0);

    res = opU(res, vec3( sdPlane(pos-vec3(0, -0.5, 0)), 2.95, 0. ) ); // pos, color, reflection
    res = opU(res, vec3( sdSphere(pos-vec3(.0,-0.25, 0.0), 0.25 ), 10.2, 1.) );
    res = opU(res, vec3( sdBoundingBox(pos-vec3( -1.0, -0.25, 0.0), vec3(0.3,0.25,0.2), 0.025), 11.12, 1.0) );
    res = opU(res, vec3( sdTorus((pos-vec3(-0.3, -0.25, 1.0)).xzy, vec2(0.25,0.05) ), 25.0, 1.0) );

    res = opU(res, vec3( sdOctahedron(pos-vec3(.750,0.25, 0.0), 0.4325 ), 10.8, 1.0) );


    return res;
}

vec3 rayCast(in vec3 ro, in vec3 rd) 
{
    vec3 res = vec3(-1.0, 0.0, 0.0);

    float tmin = 1.0;
    float tmax = 20.0; // max step distance    

    int steps = 0;
    for (float tstep = tmin; tstep < tmax;) 
    {   
        vec3 h = worldMap(ro + tstep * rd);       

        if (h.x < 0.0001 )
        {
            res.x = tstep;
            res.y = h.y;
            res.z = h.z;
            return res;
        }            

        tstep += h.x;

        // just to be sure we do not end in endless loop if during development
        steps++;        
        if (steps > 1750)
            return vec3(20000.0, 1.0, 0.0);
    }

    return res;
}

// https://iquilezles.org/www/articles/rmshadows/rmshadows.htm
// https://www.shadertoy.com/view/lsKcDD
// it still suffers from some self shadow and minro light bleeding artifats however, even with  Sebastian Aaltonen's improvement
float softShadows(in vec3 ro, in vec3 lightPos) 
{
    vec3 rd = normalize(lightPos - ro);           
    const float k = 8.0;
    float tmin = 0.0090;
    float tmax = 2.0; // max step distance    

    float res = 1.0;
    float prevh = 1e20; // infinity
    for( float t = tmin; t < tmax; )
    {
        float h = worldMap(ro + rd * t).x;
        if(h < 0.001*t)
            return 0.0;            

        float y = h * h / (2.0 * prevh);
        float d = sqrt(h * h - y * y);
        res = min(res, k * d / max(0.0,t-y));
        prevh = h;
        t += h;
    }

    res = clamp(res, 0.0, 1.0);
    return res * res * (3.0 - 2.0 * res);
}

vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    // central differencing
    // line 444: https://www.shadertoy.com/view/Xds3zN
    return normalize( e.xyy * worldMap( pos + e.xyy ).x + 
					  e.yyx * worldMap( pos + e.yyx ).x + 
					  e.yxy * worldMap( pos + e.yxy ).x + 
					  e.xxx * worldMap( pos + e.xxx ).x );
}


float ambientOcclusion(in vec3 ro, in vec3 normal)
{
    float sum = 0.0;
    float pointWeight = 1.0;
    for (int i = 0; i < 5; ++i)
    {
        float normalStep = 0.001 + 0.15 * float(i) / 4.0; // we perform step along normal

        float distanceToNormalPoint = worldMap(ro + normalStep * normal).x;
        sum += (normalStep - distanceToNormalPoint) * pointWeight;
        pointWeight *= 0.95;
    }

    return clamp(1.0-1.5*sum, 0.0, 1.0);
}

vec3 colorFromMaterial(in float mat)
{
    return 0.2 + 0.2*sin(mat*2.0 + vec3(0.0,1.0,2.0));
}

// https://learnopengl.com/Advanced-Lighting/Advanced-Lighting
// interesting post pointing to this article:
// https://www.rorydriscoll.com/2009/01/25/energy-conservation-in-games/
vec4 computeBlinnPhongLighting(in vec3 depthMaterial, in vec3 ro, in vec3 rd, in vec3 lightPos) 
{    
    vec3 col = colorFromMaterial(depthMaterial.y);

    vec3 surfacePoint = ro + rd * (depthMaterial.x);

    vec3 normal = calcNormal(surfacePoint);

    vec3 lightDir = normalize(lightPos - surfacePoint);

    float diffuse = clamp(dot(lightDir, normal), 0.0, 1.0);

    const float kShininess = 16.0;
    const float kPi = 3.1415926;
    const float kEnergyConservation = ( 2.0 + kShininess ) / ( 2.0 * kPi );

    vec3 reflectV = normalize(reflect(-lightDir, normal));
    float specular = pow(clamp(dot(-rd, reflectV), 0.0, 1.0), kShininess);

    float amb = clamp(0.2+0.5*depthMaterial.y, 0.0, 1.0) * 0.2;
    float shadow = softShadows(surfacePoint, lightPos);
    float ao = ambientOcclusion(surfacePoint, normal);
    vec3 reflected = reflect(rd, normal);

    float dom = smoothstep(-1.50, 0.51, reflected.y);

    float reflection = softShadows(surfacePoint, reflected);

    float lightning =  ao * shadow *( diffuse + diffuse * (kEnergyConservation*specular)) // * (0.04 + 0.96*pow( clamp(1.0+dot(reflectV,rd),0.0,1.0), 5.0 ))) 
                      + amb;

    /*
    vec3 lin = 0.090 * dom * vec3(0.80, 0.7, 1.0) * ao + 0.0031; // this gives objects glossy look 

    col += lin;
    col += depthMaterial.z * 0.01 * reflection * vec3(0.8, 0.7, 1.0) * ao;    
    col *= lightning; 
    */

    vec3 lin = vec3(0.0);

    lin += col * 0.750 * dom * vec3(0.80, 0.7, 1.0) * ao; 

    lin += col * depthMaterial.z * 0.35 * reflection * vec3(0.8, 0.7, 1.0) * ao;    

    col = lin;

    col *= lightning; 

    return vec4(col, 1.0);
}


mat4 lookAt(in vec3 pos, in vec3 target, in vec3 up) 
{
    vec3 forward = normalize(target - pos);
    vec3 right = normalize(cross(forward, up));
    vec3 y = normalize(cross(right, forward));   

    return mat4(vec4(right, 0.0),
                vec4(y, 0.0),
                vec4(-forward, 0.0),
                vec4(0.0, 0.0, 0.0, 0.1));
}

mat3 rotateY(in float alpha) 
{
    return mat3(cos(alpha), 0., sin(alpha),
                0         , 1.,          0, 
                -sin(alpha), 0., cos(alpha));
}

vec3 castVector(in vec3 uv, in vec3 cameraOrigin) 
{
    // orthogonal to origin by setting z = 1
    return  normalize(uv - cameraOrigin);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)    
    float ratio = iResolution.x / iResolution.y;


    vec3 camPos = vec3(0.0, 0.8, 2.7);
    vec3 lightPos = vec3(10.0, 10.0, 10.0);
    mat3 rotM = rotateY(iTime * 0.5);
    lightPos = rotM * lightPos;

    vec4 color = vec4(0.0);
    //mulitsampling
    const int MULTISAMPLE = 2;
    for (int i = 0; i < MULTISAMPLE; ++i) 
    {
        for (int j = 0; j < MULTISAMPLE; ++j)
        {                           
            vec2 shift = (vec2(float(i), float(j)) / float(MULTISAMPLE) - 0.5f);            
            vec2 uv = (fragCoord+shift)/iResolution.xy;            

            uv = uv - 0.5;    
            uv.x *= ratio;

            // focal length from camera is -z
            vec4 uvPos = vec4(uv.xy, -1.0, 1.0);           

            mat4 viewM = lookAt(camPos, vec3(0.0, .2, 0), vec3(0,1,0));

            uvPos = (viewM) * uvPos;    
            uvPos.xyz += camPos.xyz;

            vec3 rd = castVector(uvPos.xyz, camPos);

            vec3 res = rayCast(camPos, rd);

            color += computeBlinnPhongLighting(res, camPos, rd, lightPos * 1.0);
        }
    }

    color /= float(MULTISAMPLE * MULTISAMPLE);
    // gamma
    color = pow(color, vec4(0.4545)); // value 1/2.22 is good for LCD displays

    // Output to screen    
    fragColor = color;
}

float wtan(float x){
    if(x<3.14/2.0){
        return tan(x);
    }
    else{
        return tan(-x);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pTime = iTime*2.0;
    float modT = -1.0*(0.3*sin(pTime*1.2)+pTime*1.3);
    float p = 20.0*wtan(3.14*fragCoord.x/iResolution.x);

    float r = sin(modT+p*1.0);
    float g = sin(modT+p*1.02);
    float b = sin(modT+p*1.03);

    fragColor = vec4(r,g,b,1.0);
}

#define res iResolution
#define ft float

ft hm(vec2 uv, vec2 m){
    ft a = dot(uv,uv);
    ft b = (sin(iTime+uv.x/a/m.x))-cos(uv.y/a/m.y);
return abs(b*1.4)+a;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 10.*(2.*fragCoord.xy-res.xy)/res.y;
    vec2 m = vec2(.9);
    vec2 mouse = iMouse.y < 1.? vec2(.5,.05): iMouse.xy/res.xy;
    ft a = hm(uv, m);
    for(ft i; i < 3.; i++){
    uv = abs(uv/hm(uv,m+i*.2)-.4*mouse);
    }
    fragColor = vec4(1.-1./uv.xyy,1.0);
}

