#define ITERS 12
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    float t=0.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    uv.y += (iTime)/25.0;
    for(int c=0;c<ITERS;c++){
        float scale = 2.6;
        float scale1 = 1.2;
        float s1 = scale1*scale;
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {

            uv= fract(-uv-((vec2(uv.x/scale+uv.y/scale1,uv.y/scale-uv.x/scale1)/(scale))))/scale1;
            uv.x *= scale1;
            uv = fract(-uv.yx/s1)*s1;
            uv.y /= -scale1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}

vec2 hash( vec2 p ) {
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

    //The output must be between 0.0 and 1.0
	return -1.0 + 2.0*fract(sin(p) * 43758.5453123);
}

float noise( in vec2 p ) { //Simplex noise
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y) * K1 );

    vec2 a = p - i + (i.x+i.y) * K2;
    vec2 o = step(a.yx,a.xy);
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );
}

#define OCTAVES 6
float fbm (in vec2 st) {

    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;


    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 2.; //lacunarity: steps in which we increment de frequencies
        amplitude *= .5; //gain: decreases the amplitude
    }
    return value * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float color = 0.;
    //Normalize fragment coordinates
    vec2 uv = fragCoord.xy/iResolution.xy;

    //These variables are used to modify the speed at which the texture moves
    float strength = floor(uv.x+1.);
    float t = max(3.,1.25*strength)*iTime*0.75;

    color += fbm(uv*strength-vec2(0,t));

    float c1=clamp(color,0.,1.);


    float gradient = pow(1.0 - uv.y, 2.0) * 5.;
    float finalNoise = c1 * gradient;


    vec3 col = vec3(c1, c1*c1*c1, c1*c1*c1*c1*c1*c1*c1*c1);

    //define FLAMELIKE to implement a gradient
    #define FLAMELIKE 
#ifdef FLAMELIKE
    col = finalNoise * vec3(c1, c1*c1*c1, c1*c1*c1*c1*c1*c1*c1*c1);
#endif
    fragColor = vec4(vec3(col), 1.0);
}

float res = 10.; // how many sqrs along the x axes
int seed = -5;
int detail = 8;
bool debug = false;


float pixelSize = 0.;

float[] digitsPi = float[](14.159265,35.897932,38.462643,38.327950,28.841971,69.399375,10.582097,49.445923,78.16406,28.620899,86.280348);

float getDigit(float index){
    return digitsPi[int(abs(mod(index, float(digitsPi.length()))))];
}
float getDigit(int index){
    return getDigit(float(index));
}
float getRandom(vec2 index){
    index = mod(index, 65535.);
    float id = length(index) * float(seed) + length(index) + float(seed);
    return fract(sin(dot(index, vec2(getDigit(id+265.), getDigit(id+159.))))* getDigit(id + 314.));
}
float getRandom(vec3 index){
    index = mod(index, 65535.);
    float id = length(index) * float(seed) + length(index) + float(seed);
    return fract(sin(dot(index, vec3(getDigit(id+265.), getDigit(id+159.), getDigit(id+582.))))* getDigit(id + 314.));
}
float smoothMix(vec2 from, vec2 to, vec2 t){
    t = smoothstep(0.,1.,t);
    return mix(
        mix(getRandom(from), getRandom(vec2(to.x, from.y)), t.x),
        mix(getRandom(vec2(from.x, to.y)), getRandom(to), t.x),
    t.y);
}
float smoothMix(vec3 from, vec3 to, vec3 t){
    t = smoothstep(0.,1.,t);
    return mix(
        mix(
            mix(getRandom(from), getRandom(vec3(to.x, from.yz)), t.x),
            mix(getRandom(vec3(from.x, to.y, from.z)), getRandom(vec3(to.xy, from.z)), t.x),
        t.y),

        mix(
            mix(getRandom(vec3(from.xy, to.z)), getRandom(vec3(to.x, from.y, to.z)), t.x),
            mix(getRandom(vec3(from.x, to.yz)), getRandom(to), t.x),
        t.y),

    t.z);
}
float getNoise(vec2 index){
    vec2 low = floor(index);
    float _output = 0.;
    float h = 0.;

    for(float i = 1.; i <= float(detail); i ++){
        vec2 low = floor(index);
        _output += smoothMix(low, low + vec2(1,1), fract(index)) * (float(detail)/i);
        h += (float(detail)/i);
        index += float(seed) + i;
        index *= 2.;
    }
    return _output / h;
}
float getNoise(vec3 index){
    float _output = 0.;
    float h = 0.;

    for(float i = 1.; i <= float(detail); i ++){
        vec3 low = floor(index);
        _output += smoothMix(low, low + vec3(1,1,1), fract(index)) * (float(detail)/i);
        h += (float(detail)/i);
        index += float(seed) + i;
        index *= 2.;
    }
    return _output / h;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord - .5 * iResolution.xy) / (iResolution.x/res));
    pixelSize = res / iResolution.y;

    vec3 col = vec3(getNoise(vec3(uv, iTime/5.)));

    if( debug && (
        fract(uv.x) <= pixelSize || fract(uv.x) >= 1.-pixelSize || 
        fract(uv.y) <= pixelSize || fract(uv.y) >= 1.-pixelSize)
    ) col.r = 1.;

    // Output to screen
    fragColor = vec4(col,1.0);
}

