#define MAX_STEPS (1<<7)
#define MAX_DIST 100.
#define SURF_DIST .001
#define LIGHT_RADIUS 1000.

const float TorusMat = 1.;
const float CapsuleMat = 2.;
const float ShpereMat = 3.;
const float CubeMat = 4.;
const float PlaneMat = 5.;

vec3 GetMatColor(float mat, vec3 currentPoint)
{
    vec3 col = vec3(0);
    if(mat == TorusMat)
    {
        col = vec3(1,sin(iTime),cos(iTime));
    }else if(mat == CapsuleMat)
    {
        col = vec3(0,0.5,cos(iTime));
    }else if(mat == ShpereMat)
    {
        col = vec3(0.4,0.5,0.5);
    }else if(mat == CubeMat)
    {
        col = vec3(.1);
    } else {col = vec3(1);}
    return col;
}

float GetMatSpec(float mat)
{
    if(mat == ShpereMat)
    {
        return 5.;
    } else if(mat == CubeMat)
    {
        return 5.;
    } else if(mat == PlaneMat)
    {
        return 0.05;
    }
    return 0.2;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r)
{
    vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab,ab);
    t = clamp(t,0.,1.);

    vec3 c= a + t * ab * (sin(p.y / 1.) + 0.5);
    return length(p-c)-r;
}

mat2 getRot(float rot)
{
    rot = radians(rot);
    return mat2(cos(rot),sin(rot),-sin(rot),cos(rot));
}

float sdBox(vec3 p, vec3 s,vec3 r) {
    p.zx *= getRot(r.y);
    p.yx *= getRot(r.z);
    p.zy *= getRot(r.x);
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdTorus (vec3 p, vec2 r)
{
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y)) -r.y;
}

float GetPlaneHeight (vec3 point, float lod)
{
    float f = 1.2;
    vec3 texXZ = pow(textureLod(iChannel0, point.xz * .3+.5, lod).rgb, vec3(f));
    return dot(texXZ, vec3(5.,1.,.2) * .003);
}

vec2 GetDist (vec3 p) {
    vec4 s = vec4(sin(iTime), 1,cos(iTime) + 4., 1);

    float sphereDist = length(p-s.xyz ) - s.w;
    float torusDist = sdTorus(p - vec3(0,sin(iTime)/5. + .5,-3), vec2(1,sin(iTime)/6. + 0.5));
    float planeDist = p.y;
    float boxDist = sdBox(p - vec3(-4,1.05,0), vec3(1),vec3(iTime*20.,0,iTime*20.)) - 0.1;

    float cd = sdCapsule(p - vec3(4,0,0), vec3(-cos(iTime),-sin(iTime) + 2.,0), vec3(cos(iTime),sin(iTime) + 2.,0),.6);
    float d = min(cd, planeDist);
    d = min(d, sphereDist);
    d = min(d, torusDist);
    d = min(d, boxDist);

    float mat = 0.;
    if(d == torusDist) {mat = TorusMat;}
    else if(d == cd) {mat = CapsuleMat;}
    else if(d == sphereDist) {mat = ShpereMat;}
    else if(d == planeDist) 
    {

        mat = PlaneMat;
        d -= GetPlaneHeight (p, 2.);
    }
    else if(d == boxDist) {mat = CubeMat;}
    return vec2(d,mat);
}

vec3 GetNormal (vec3 p) {
    float d = GetDist (p).x;
    vec2 e = vec2 (.0003, 0);

    vec3 left = vec3(GetDist(p - e.xyy).x,
                     GetDist(p - e.yxy).x,
                     GetDist(p - e.yyx).x),
        right = vec3(GetDist(p + e.xyy).x,
                     GetDist(p + e.yxy).x,
                     GetDist(p + e.yyx).x);

    vec3 n = normalize(-left + right);
    return normalize(n);
}

vec3 GetPlaneTexture (vec3 point)
{
    vec3 normal = abs(GetNormal(point));
    vec3 texXZ = texture(iChannel0, point.xz * .3+.5).rgb;
    vec3 texYZ = texture(iChannel0, point.yz * .5+.5).rgb;
    vec3 texXY = texture(iChannel0, point.xy * .5+.5).rgb;
    vec3 disp = vec3(0);
    disp += texXY * normal.z;
    disp += texYZ * normal.x;
    disp += texXZ * normal.y;
    return disp;
}

vec3 RayMarch (vec3 ro, vec3 rd) 
{
    float dO=0.;
    float smallestDist = 1.;
    float mat = 0.;
    for(int i=0; i<MAX_STEPS; i++)
    {
        vec3 currentPoint = ro + rd * dO;
        vec2 Distance = GetDist(currentPoint);
        mat = Distance.y;
        float dS = Distance.x;
        dO += dS;
        smallestDist = min(smallestDist,dS/dO);
        if(dS <= 0.01) 
        {
            smallestDist = 0.;
        }
        if(dO>MAX_DIST || dS<SURF_DIST)
        {
         float fadeRatio =
         1.0 - clamp(SURF_DIST / LIGHT_RADIUS, 0., 1.);
         float distanceFactor = pow(fadeRatio, 2.);
         smallestDist *= distanceFactor;
         break;
        }
    }
    smallestDist = clamp(smallestDist,0.,1.);
    return vec3(dO,smallestDist,mat);
}

vec3 GetLight(float material,vec3 rayDir,vec3 p, vec3 pos,float power, vec3 color) 
{
    vec3 lightPos = pos;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal (p);


    float specAmount = GetMatSpec(material);
    float spec = 0.;
    if(specAmount != .0)
    {
        spec = pow(max( dot( reflect(-l, n), -rayDir ), 0.), 8.);
    }
    vec3 d = RayMarch(p+n*SURF_DIST*(material == CapsuleMat ? 400. : 12.),l);
    float dif = d.y *clamp(dot(n, l), 0.,1.);
    return clamp( dif + (vec3(1., .6, .2)*spec*specAmount),0.002,1.) * color * power;
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

vec3 GetColor (float material, vec3 rayDirection, vec3 point)
{
    vec3 col = vec3(1);
    if(material == PlaneMat)
    {
        col = GetPlaneTexture(point);
    } else {col = GetMatColor(material,point);}


    col *= 
       GetLight(material,rayDirection,point,vec3(0,5,-6),1.,vec3(1,1,1))
    + GetLight(material,rayDirection,point,vec3(sin(iTime) * 3.,5,cos(iTime) * 3.),1.,vec3(1,0,1));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec3 pos = vec3(0, 6, -6);
    vec3 ro = pos;
    ro.yz *= Rot(-mouse.y*3.14+1.);
    ro.xz *= Rot(-mouse.x*6.2831);
    ro.y = clamp(ro.y,1.2,10.);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);

    vec3 d = RayMarch(ro, rd);

    vec3 p = ro + rd * d.x;

    vec3 normal = GetNormal(p);

    float Distance = d.x;

    vec3 col = vec3(0);
    if(d.x < MAX_DIST && d.x >= SURF_DIST)
    {
        col = GetColor (d.z, rd, p);
        if(d.z == ShpereMat)
        {
            ro += rd*d.x;
            rd = reflect(rd,normal);
            d = RayMarch(ro+normal*0.0015, rd);
            if(d.x < MAX_DIST)
            {
                p = ro + rd * d.x;
                col += GetColor (d.z, rd, p) / (d.z == PlaneMat ? 1. : 8.);
            }
            normal = GetNormal(p);
        }

    }
    col = pow(col, vec3(.4545));
    //Fade to black
    col *= ((MAX_DIST-Distance)/MAX_DIST);
    // Output to screen
    fragColor = vec4(col,1.0);
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash21(in vec2 p) {
	vec3 p3 = fract(p.xyx * vec3(0.1031, 0.103, 0.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy).x;
}

vec2 sdTruchet(in vec2 p) {
    vec2 cId = floor(p);
    vec2 cUv = fract(p) - 0.5;

    cUv.x *= sign(Hash21(cId) - 0.5);
    cUv -= cUv.x < -cUv.y ? -0.5 : 0.5;

    float t = atan(cUv.y, cUv.x);
    t *= sign(mod(cId.x + cId.y, 2.0) - 0.5);

    return vec2(abs(length(cUv) - 0.5), t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0 + 0.25 * iTime;

    vec2 truchet = sdTruchet(uv);
    float d = truchet.x - 0.1;

    // Nice colors from iq
    vec3 color = 1.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.2 * cos(140.0 * d);
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.015, abs(d) * 0.6));

    if (d < 0.02) {
        truchet.y -= 0.5 * iTime;
        float fade = 0.5 + 0.5 * sin(iTime);
        float vertStripes = abs(mod(d + 0.0333, 0.0666) - 0.0333);
        float horiStripes = abs(mod(truchet.y + 0.1, 0.2) - 0.1) * 0.5;
        color += fade * smoothstep(0.03, 0.0, min(vertStripes, horiStripes));
    }

    fragColor = vec4(color, 1.0);
}

float drawCircle(vec2 pos, float t)
{
    float dist = length(pos);

    float radius = mod(t * .1, .3);
    float fadeDis = smoothstep(.3, .0, radius);

    float intensity = clamp(pow(2./abs(dist-radius), .2), .0, 3.5);

    intensity *= smoothstep(2.2, 3.1, intensity);

    intensity *= fadeDis;

    return intensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float intensity = drawCircle(pos, iTime);
    intensity += drawCircle(pos, iTime + 1.);
    intensity += drawCircle(pos, iTime + 2.);

    fragColor = vec4(.0,.3,.8, 1.) * intensity;
}

