// License CC0: Random sunday fractal
// Result after a bit of random coding on sunday

#define PI              3.141592654
#define TAU             (2.0*PI)
#define TIME            iTime
#define RESOLUTION      iResolution
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))
#define PCOS(x)         (0.5+0.5*cos(x))
#define DOT2(x)         dot(x, x)

const vec3 std_gamma        = vec3(2.2);

float g_cd = 0.0;

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

// From: https://stackoverflow.com/a/17897228/418488
vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/std_gamma);
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

float box(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float torus( vec3 p, vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}


float boxf(vec3 p, vec3 b, float e) {
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

vec3 pmin(vec3 a, vec3 b, float k) {
  vec3 h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 pabs(vec3 a, float k) {
  return -pmin(a, -a, k);
}

float df(vec3 p) {
  float d = 1E6;
  vec3 op = p;

  const float zf = 2.0-0.1;
  const vec3 nz = normalize(vec3(1.0, .0, -1.0));
  const vec3 ny = normalize(vec3(1.0, -1., 0.0));
  float z = 1.0;
  const float rsm = 0.125*0.25;
  float a = 124.7+TIME*TAU/173.0; 
  mat2 rxy = ROT(a);
  mat2 ryz = ROT(a*sqrt(0.5));

  float off = 0.8; 
  vec3 cp = vec3(0.55, 0.5, 0.45);
  float cd = 1E6;

  const int mid   = 0; 
  const int upper = 7; 
  for (int i = 0; i < mid; ++i) {
    cd = min(cd, length(p-cp));
    z *= zf;
    p *= zf;
    p.xy *= rxy;
    p.yz *= ryz;
    p  = pabs(p, rsm);
    p -= nz*pmin(0.0, dot(p, nz), rsm)*2.0;
    p -= ny*pmin(0.0, dot(p, ny), rsm)*2.0;

    p -= vec3(off/zf, 0.0, 0.0);
  }


  for (int i = mid; i < upper; ++i) {
    cd = min(cd, length(p-cp));
    vec3 pp = p;
    float dd4 = torus(pp.zxy, 0.5*vec2(1.0, 0.2));
    float dd5 = boxf(pp, vec3(0.2), 0.00)-0.025;
    float dd  = dd5;
    dd = min(dd5, dd4);
    dd  /= z;

    z *= zf;
    p *= zf;
    p.xy *= rxy;
    p.yz *= ryz;
    p  = pabs(p, rsm);
    p -= nz*pmin(0.0, dot(p, nz), rsm)*2.0;
    p -= ny*pmin(0.0, dot(p, ny), rsm)*2.0;

    p -= vec3(off/zf, 0.0, 0.0);
    d = pmax(d, -(dd-0.1/z), 0.05/z);

    d = min(d, dd);
  }

  g_cd = cd;
  return d;
}

float df(vec2 p) {
  vec3 p3 = vec3(p, mix(0.0, 1.0, PCOS(TAU*TIME/331.0)));
  p3.xz *= ROT(TAU*TIME/127.0);
  p3.yz *= ROT(TAU*TIME/231.0);
  const float z = 0.25;
  p3 *= z;
  return df(p3)/z;
}

float hf(vec2 p) {
  float d = df(p);
  float aa = 0.0125;
  return -0.025*smoothstep(-aa, aa, -d);
}

vec3 normal(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = hf(p + e.xy) - hf(p - e.xy);
  n.y = 2.0*e.x;
  n.z = hf(p + e.yx) - hf(p - e.yx);

  return normalize(n);
}

vec3 color(vec2 p, vec2 q) {
  vec2 ppp = p;
  const float s = 1.0;
  const vec3 lp1 = vec3(1.0, 1.25, 1.0)*vec3(s, 1.0, s);
  const vec3 lp2 = vec3(-1.0, 1.25, 1.0)*vec3(s, 1.0, s);

  float aa = 2.0/RESOLUTION.y;

  float h = hf(p);
  float cd   = g_cd;
  vec3  n = normal(p);

  vec3 ro = vec3(0.0, mix(-3.0, -10.0, PCOS(TIME+PI*p.x)), 0.0);
  vec3 pp = vec3(p.x, 0.0, p.y);

  vec3 po = vec3(p.x, h, p.y);
  vec3 rd = normalize(ro - po);

  vec3 ld1 = normalize(lp1 - po);
  vec3 ld2 = normalize(lp2 - po);

  float diff1 = max(dot(n, ld1), 0.0);
  float diff2 = max(dot(n, ld2), 0.0);

  vec3  rn    = n;
  vec3  ref   = reflect(rd, rn);
  float ref1  = max(dot(ref, ld1), 0.0);
  float ref2  = max(dot(ref, ld2), 0.0);

  const vec3 lcol1 = sqrt(vec3(1.25, 1.35, 2.0).zxy*0.15);
  const vec3 lcol2 = vec3(2.0, 1.55, 1.25).zyx*0.85;
  vec3 lpow1 = 0.15*lcol1/DOT2(ld1);
  vec3 lpow2 = 0.25*lcol2/DOT2(ld2);
  vec3 dm = vec3(1.0)*tanh_approx(-h*50.0+0.125);
  vec3 col = vec3(0.0);
  col += dm*pow(diff1, 4.0)*lpow1;
  col += dm*pow(diff2, 4.0)*lpow2;
  vec3 rm = vec3(1.0)*mix(0.25, 1.0, tanh_approx(-h*1000.0));
  col += rm*pow(ref1, 40.0)*lcol1;
  col += rm*pow(ref2, 40.0)*lcol2;

  float hue  = fract(0.85-0.5*PI*cd);
  float sat  = clamp(0.85*PCOS(10.0*cd), 0.0, 1.0);
  float vue  = 1.0-1.0*PCOS(8.0*cd);
  vec3 hsv   = vec3(hue, sat, vue);
  vec3 color = (1.0*hsv2rgb(hsv));

  return p.x > 0.0 ? color : col ;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = color(p, q);

  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

// Filmic tonemapping curve by milesWaugh

const float epsilon = 0.001;
const float focalLength = 1.;
const float renderDist = 850.;
const float pi = 3.141592653589793;
const int bounces = 5;

vec3 camera;
float nearestDist;
vec3 ray;

vec3 surfCol;
vec3 surfNormal;
float surfRoughness;
int surfType; /* 0=dielectric 1=metallic */
bool updateMat;

float diffuse = 0.;
float specular;
vec3 ambient = 1.*vec3(.3, 0.4, 0.7);

vec3 rayDirection( float focal, vec2 xy ) {
    return (normalize(vec3(xy, focal)));
}

// SDF's

vec4 Sphere( vec3 rayOrigin, vec3 rayDir, vec3 pos, float radius ) {
    vec3 offset = pos - rayOrigin;
    float dot1 = dot(offset, rayDir);
    float dot2 = dot(offset, offset);
    if (dot1 < 0.) {
        return vec4(-1., 0, 0, 0);
    }
    if (dot2 - dot1 * dot1 < radius * radius) {
        float near = sqrt(radius * radius - (dot2 - (dot1 * dot1)));
        return vec4(dot1 - near, (rayOrigin + (rayDir * (dot1 - near))) - pos);
    }
    return vec4(-1., 0, 0, 0);
}

vec4 Plane( vec3 rayOrigin, vec3 rayDir, vec3 normal, float dist ) {
    float dot1 = dot(normal, rayDir);
    float near = (dot(normal, rayOrigin) + dist)/dot1;
    if (!(dot1 == 0.) && near < 0.)
    {
        return vec4(-near, normal);
    }
    return vec4(-1., 0, 0, 0);
}

void addIntersector( vec4 surface, vec3 col, float roughness, int type /* 0=dielectric, 1=metallic */) {
    float dist = surface.x;
    if ( dist < nearestDist && dist > 0. ) {
        nearestDist = dist;
        if (updateMat) {
        	surfCol = col;
            surfRoughness = roughness*roughness;
            surfNormal = normalize(surface.yzw);
            surfType = type;
        }
    }
}

float scene( vec3 rayOrigin, vec3 rayDir)
{

    vec3 sinVector = vec3(1., 0.81, 1.23);

    vec3 colx = vec3(0.25*sin((ray/10.)*sinVector)+0.5);

    addIntersector( Sphere( rayOrigin, rayDir, vec3(0, 0, 18), 1.), vec3(0.4,.2, 0.01), .1, 0);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(3, 1, 22), 2.), vec3(0.05,0.1, 0.6), .1, 0);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(-5, 5, 90), 6.), vec3(0.35,0.35, 1.), .1, 0);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(10, 7, 100), 8.), vec3(.7,0.05, 0.05), .1, 0);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(15, 3, 80), 4.), vec3(.85,0.8, 0.05), .1, 1);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(-3, 3, 60), 4.), vec3(.02,0.3, 0.07), .1, 0);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(1, 1, 55), 2.), vec3(.7,0.7, 0.1), .1, 0);
    addIntersector( Sphere( rayOrigin, rayDir, vec3(10, 2, 50), 3.), vec3(.03,0.05, 0.3), .1, 0);
    addIntersector( Plane( rayOrigin, rayDir, vec3(0, 1, 0), 1.), vec3(0.1, 0.3, 0.1), .1, 0);

    return nearestDist;
}

vec4 shootRay( vec3 rayDir, vec3 start, float renderDist, bool updateMaterial ) {
    updateMat = updateMaterial;

    nearestDist = renderDist;

    scene(start, rayDir);

    if ( nearestDist < renderDist ) {
        return vec4(1., rayDir * nearestDist + start);
    }
    else {
        return vec4(0., rayDir * nearestDist + start);
    }
}

float GGX (float halfDot, float roughness) {
    float r2 = roughness*roughness;
    float a = halfDot * halfDot * (r2 - 1.) + 1.;
    return r2 / (a * a);
}

void directionalLight(vec3 ray, vec3 rayDir, vec3 lightDir, vec3 surfNormal, float intensity, float lightDist) {
    if ( shootRay(lightDir, ray + (surfNormal * epsilon), lightDist, false).x == 0. ) {
        float lambertian = dot(surfNormal, lightDir);
        diffuse += max(intensity * lambertian, 0.);

        vec3 halfway = normalize(lightDir - rayDir);
        float halfDot = dot(surfNormal, halfway);
    	specular += max(lambertian * intensity * GGX(halfDot, surfRoughness), 0.);

    }
}

void pointLight( vec3 ray, vec3 rayDir, vec3 position, float intensity, vec3 surfNormal ) {
    vec3 lightDir = normalize(position - ray);
    directionalLight( ray, rayDir, lightDir, surfNormal, intensity, distance(position, ray) );
}

void sceneLights(vec3 ray, vec3 rayDir, vec3 surfNormal) {
    pointLight( ray, rayDir, vec3(-10, 50, 20), 2.5, surfNormal);
    //directionalLight( ray, rayDir, normalize(vec3(-2,2,-1)), surfNormal, 1., 0. );
}

// My implementation of the tonemapping curve by milesWaugh
vec3 filmic(vec3 color) {
    //return color;
    return 1. - (1./(5. * pow(color, vec3(2.)) + 1.));
}

float fresnel(float IOR, float roughness, vec3 surfNormal, vec3 incoming) {
    float F0 = (1. - IOR)/(1. + IOR);
    F0 *= F0;
    return F0 + (1. - roughness)*((1. - F0) * pow(1. - dot(surfNormal, incoming),5.));
}

vec3 lighting(vec3 ray, vec3 rayDir, vec3 rayOrigin, vec3 surfNormal, float f) {
    diffuse = 0.;
    specular = 0.;

    sceneLights(ray, rayDir, surfNormal);
    /*
    float fog = clamp(length(ray - rayOrigin)/(renderDist), 0., 1.);
    fog = fog*fog*fog;
    //fog = fog*fog;
    fog = smoothstep(0., 1., fog);
    */

    vec3 finalCol = vec3(0.);

    if (surfType == 0) {
        finalCol = mix(surfCol * (diffuse + ambient), vec3(specular), f);
    }
    else if (surfType == 1) {
        finalCol = mix(surfCol * specular, vec3(specular), f);
    }

    finalCol = finalCol;

    return finalCol;
}

vec3 renderFromRay(vec3 rayDir, vec3 rayOrigin) {
    vec3 contribution = vec3(1.0);
    vec3 combinedCol;
    float totalDepth = 0.;
    float foga;
    for (int i = 0; i < bounces; i++) {
        vec4 rayData = shootRay( rayDir, rayOrigin, renderDist, true );

        if (rayData.x == 1.) {
            totalDepth += distance(rayData.yzw, rayOrigin);
            float f = fresnel(1.5, surfRoughness, surfNormal, -rayDir);
            combinedCol += contribution * lighting(rayData.yzw, rayDir, rayOrigin, surfNormal, f);
            if (surfType == 0) {
                contribution *= f;
            }
            else if (surfType == 1) {
                contribution *= mix(surfCol, vec3(1.), vec3(f));
            }

            rayDir = reflect(rayDir, surfNormal);
            rayOrigin = rayData.yzw + epsilon * surfNormal;
        }
        else {
            combinedCol += contribution * ambient;
            break;
        }
    }
    return combinedCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cosTime = cos(iTime/2.);
    cosTime = 6.*-cosTime+6.;
    camera = vec3(.5*cosTime, .6*cosTime+0.3, 5.*cosTime-5.);
    //camera = vec3(0,0,0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy/2.)/iResolution.x;

    vec3 rayDir = rayDirection( focalLength, uv );

    vec3 col = renderFromRay(rayDir, camera);

    // Output to screen, filmic tonemapping, and gamma correction.
    fragColor = vec4(pow(filmic(col),vec3(1./2.2)),1.0);
}

vec2 rotate2d(vec2 uv, float angle) {
    mat2 mat = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)); 
    return (mat*(uv));
}

vec2 hash(vec2 uv){
    uv = vec2( dot(uv,vec2(127.1,311.7)), dot(uv,vec2(269.5,183.3)) );
    vec2 v = -1.0 + 2.0 * fract(sin(uv) * 43758.5453123);  
    v = rotate2d(v, iTime); // rotated random direction
    return v;
}

// noise range [-1, 1]
float snoise2D(vec2 v) {
    const float K1 = 0.366025404; // (sqrt(3)-1)/2
    const float K2 = 0.211324865; // (3-sqrt(3))/6

    // skew each unit square gird into two equilateral triangle (simplex cell)
    vec2 i = floor(v + (v.x+v.y)*K1); 

    // unskew the cell origin i(x,y) back to original space
    vec2 pa = i - (i.x+i.y)*K2;
    // distance from cell origin to current point(v)
    vec2 da = v - pa;

    // calculate point b of the simplex
    vec2 o = vec2(0.0f);
    if(da.x > da.y) {
        o = vec2(1.0f, 0.0f); //  lower triangle, XY order: (0,0)->(1,0)->(1,1)
    } else {
        o = vec2(0.0f, 1.0f); // upper triangle, YX order: (0,0)->(0,1)->(1,1)
    }

    vec2 db = da - o + K2; // offset for point b of simplex
    vec2 dc = da - 1.0 + 2.0 * K2; // offset for point c of simplex


    // calculate contribution of each point, with distance of simplex vertices from current point   
    float ca = max(0.5f - dot(da, da), 0.0f); 
    float cb = max(0.5f - dot(db, db), 0.0f);
    float cc = max(0.5f - dot(dc, dc), 0.0f); 

    // calculate gradient with the contribution
    float n1 = ca*ca*ca*ca*dot(da, hash(i + vec2(0.0f)));
    float n2 = cb*cb*cb*cb*dot(db, hash(i + o));
    float n3 = cc*cc*cc*cc*dot(dc, hash(i + vec2(1.0f)));

    // Add contributions from each corner to get the final noise value.
    // The result is scaled to return values in the interval [-1,1].
    return 70.0f * (n1+n2+n3);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // res
    vec2 uv = fragCoord/iResolution.y;
    uv *= 16.0f;
    float x_max = (iResolution.x/iResolution.y)*16.0f; // max uv.x

    // tile
    vec2 fuv = fract(uv);
    vec2 iuv = floor(uv);

    // noise with creases 
    float len = length(snoise2D(uv+iTime*0.2f));

    // sliding window, between len and inverted len
    float a = mod(iTime*2.5f, 2.0f*x_max); 
    if(a > x_max)
      len = abs(step(uv.x, 2.0f*x_max-a)- len);
    else
      len = abs(step(uv.x, a)- len);

    fragColor = vec4(vec3(len), 1.0f);
}

    vec3 hsv2rgb(vec3 c) //from  yiwenl/hsv2rgb 
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

