#define PI 3.14159
#define TWO_PI (PI*2.0)
#define N 68.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = (fragCoord.xy);
    center.x=-10.12*sin(iTime/200.0);
    center.y=-10.12*cos(iTime/200.0);
    vec2 v = (fragCoord.xy - iResolution.x/20.0) / min(iResolution.y,iResolution.x) * 15.0;
    v.x=v.x-10.0;
    v.y=v.y-200.0;
    float col = 0.0;

    for(float i = 0.0; i < N; i++) {
        float a = i * (TWO_PI/N) * 61.95;
        col += cos(TWO_PI*(v.y * cos(a) + v.x * sin(a) + sin(iTime*0.004)*100.0 ));
    }

    col /= 5.;

    float red = 1.;
    float green = sin(iTime) * 1.25;
    float blue = cos(iTime) * 1.525;
    vec3 rgb = vec3(red, green, blue);
    vec3 color = vec3(rgb);

    fragColor = vec4(vec3(col*color),1.0);

}

// implementation of MurmurHash (https://sites.google.com/site/murmurhash/) for a 
// single unsigned integer.

uint hash(uint x, uint seed) {
    const uint m = 0x5bd1e995U;
    uint hash = seed;
    // process input
    uint k = x;
    k *= m;
    k ^= k >> 24;
    k *= m;
    hash *= m;
    hash ^= k;
    // some final mixing
    hash ^= hash >> 13;
    hash *= m;
    hash ^= hash >> 15;
    return hash;
}

// implementation of MurmurHash (https://sites.google.com/site/murmurhash/) for a  
// 3-dimensional unsigned integer input vector.

uint hash(uvec3 x, uint seed){
    const uint m = 0x5bd1e995U;
    uint hash = seed;
    // process first vector element
    uint k = x.x; 
    k *= m;
    k ^= k >> 24;
    k *= m;
    hash *= m;
    hash ^= k;
    // process second vector element
    k = x.y; 
    k *= m;
    k ^= k >> 24;
    k *= m;
    hash *= m;
    hash ^= k;
    // process third vector element
    k = x.z; 
    k *= m;
    k ^= k >> 24;
    k *= m;
    hash *= m;
    hash ^= k;
	// some final mixing
    hash ^= hash >> 13;
    hash *= m;
    hash ^= hash >> 15;
    return hash;
}


vec3 gradientDirection(uint hash) {
    switch (int(hash) & 15) { // look at the last four bits to pick a gradient direction
    case 0:
        return vec3(1, 1, 0);
    case 1:
        return vec3(-1, 1, 0);
    case 2:
        return vec3(1, -1, 0);
    case 3:
        return vec3(-1, -1, 0);
    case 4:
        return vec3(1, 0, 1);
    case 5:
        return vec3(-1, 0, 1);
    case 6:
        return vec3(1, 0, -1);
    case 7:
        return vec3(-1, 0, -1);
    case 8:
        return vec3(0, 1, 1);
    case 9:
        return vec3(0, -1, 1);
    case 10:
        return vec3(0, 1, -1);
    case 11:
        return vec3(0, -1, -1);
    case 12:
        return vec3(1, 1, 0);
    case 13:
        return vec3(-1, 1, 0);
    case 14:
        return vec3(0, -1, 1);
    case 15:
        return vec3(0, -1, -1);
    }
}

float interpolate(float value1, float value2, float value3, float value4, float value5, float value6, float value7, float value8, vec3 t) {
    return mix(
        mix(mix(value1, value2, t.x), mix(value3, value4, t.x), t.y),
        mix(mix(value5, value6, t.x), mix(value7, value8, t.x), t.y),
        t.z
    );
}

vec3 fade(vec3 t) {
    // 6t^5 - 15t^4 + 10t^3
	return t * t * t * (t * (t * 6.0 - 15.0) + 10.0);
}

float perlinNoise(vec3 position, uint seed) {
    vec3 floorPosition = floor(position);
    vec3 fractPosition = position - floorPosition;
    uvec3 cellCoordinates = uvec3(floorPosition);
    float value1 = dot(gradientDirection(hash(cellCoordinates, seed)), fractPosition);
    float value2 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 0, 0)), seed)), fractPosition - vec3(1, 0, 0));
    float value3 = dot(gradientDirection(hash((cellCoordinates + uvec3(0, 1, 0)), seed)), fractPosition - vec3(0, 1, 0));
    float value4 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 1, 0)), seed)), fractPosition - vec3(1, 1, 0));
    float value5 = dot(gradientDirection(hash((cellCoordinates + uvec3(0, 0, 1)), seed)), fractPosition - vec3(0, 0, 1));
    float value6 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 0, 1)), seed)), fractPosition - vec3(1, 0, 1));
    float value7 = dot(gradientDirection(hash((cellCoordinates + uvec3(0, 1, 1)), seed)), fractPosition - vec3(0, 1, 1));
    float value8 = dot(gradientDirection(hash((cellCoordinates + uvec3(1, 1, 1)), seed)), fractPosition - vec3(1, 1, 1));
    return interpolate(value1, value2, value3, value4, value5, value6, value7, value8, fade(fractPosition));
}

float perlinNoise(vec3 position, int frequency, int octaveCount, float persistence, float lacunarity, uint seed) {
    float value = 0.0;
    float amplitude = 1.0;
    float currentFrequency = float(frequency);
    uint currentSeed = seed;
    for (int i = 0; i < octaveCount; i++) {
        currentSeed = hash(currentSeed, 0x0U); // create a new seed for each octave
        value += perlinNoise(position * currentFrequency, currentSeed) * amplitude;
        amplitude *= persistence;
        currentFrequency *= lacunarity;
    }
    return value;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy;
    position.x *= iResolution.x / iResolution.y;
    uint seed = 0x578437adU; // can be set to something else if you want a different set of random values
    // float frequency = 16.0;
    // float value = perlinNoise(vec3(position, iTime * 0.25) * frequency, seed); // single octave perlin noise
    float value = perlinNoise(vec3(position, iTime * 0.25), 1, 6, 0.5, 2.0, seed); // multiple octaves
    value = (value + 1.0) * 0.5; // convert from range [-1, 1] to range [0, 1]
    fragColor = vec4(vec3(value), 1.0);
}

#define MAX_STEPS 400
#define MAX_DIST 200.0
#define SURF_DIST 0.001
#define AA 2

#define PI 3.1415926538

#define SPHERECOUNT 4
#define SPHERE3 4.0
#define SPHERE2 3.0
#define SPHERE1 2.0
#define PLANE 1.0
float sdSphere(vec3 p, float r)//Signed distance function of sphere
{
    return length(p) - r;
}

float sdTorus( vec3 p, vec2 t )
{
    vec2 q = vec2(length(p.xz)-t.x,p.y); //outer and inner radius 2d
    return length(q)-t.y; //two symetric circles 2d
}

vec2 minSDF(vec2 sdf1, vec2 sdf2)
{
    return sdf1.x<sdf2.x ? sdf1 : sdf2;
}

vec2 maxSDF(vec2 sdf1, vec2 sdf2)
{
    return sdf1.x>sdf2.x ? sdf1 : sdf2;
}

vec2 smax(vec2 sdf1, vec2 sdf2, float k)//smooth
{
    float h = max(k - abs(sdf1.x - sdf2.x), 0.0)/k;
    float bf = h*h*0.5;// blend factor 
    float res = bf*k*(1.0/2.0);

    return (sdf1.x>sdf2.x) ? vec2(sdf1.x+res, sdf1.y/*mix(sdf1.y, sdf2.y, bf)*/) : 
    vec2(sdf2.x+res, sdf2.y/*mix(sdf1.y, sdf2.y, 1.0-bf)*/);
}

//Returns origin of rotating sphere
vec3 calcRotSphereXZ(float ofset, vec3 o, float th)
{
    vec2 o_2d = o.xz;//ignore height
    vec2 p = o_2d+vec2(ofset);

    //translate to origin
    p -= o.xz;

    //calculate rotation
    vec2 rot_p;
    rot_p.x = p.x*cos(th) - p.y*sin(th);
    rot_p.y = p.x*sin(th) + p.y*cos(th);

    //translate back
    vec3 res = o;
    res.xz = rot_p + o_2d;

    return res;
}


vec2 sdBurger(vec3 p, vec3 o, float r, float k, float colourID) // Sphere with torus cut out
{
    //torus modifiers
    float t1 = 0.7; 
    float t2 = 0.3;

    vec3 po = p-o;

    vec2 sdf = vec2(sdSphere(po, r), colourID);
    sdf = smax(sdf, vec2(-sdTorus(po, vec2(r*t1, r*t2)), colourID), k);
    return sdf;
} 


vec2 map(vec3 p) //Scene geometry information
{

    vec2 cur_sdf = vec2(1e10, 0.0);

    //Hollowed sphere
    vec3 sph_o = vec3(0.0, 2.0, 10.0);
    vec3 torus = vec3(0.0, 2.0, 10.0);
    float rp = 2.0;
    float k = 0.4;
    float ratio = 0.287; //ratio
    vec2 combined = vec2(1e10, 0.0);

    cur_sdf = sdBurger(p, sph_o, rp, k,SPHERE1);

    //Rotating spheres
    float r = ratio*rp;
    float rr = ratio*r;
    float kk = ratio*k;
    float kkk = ratio*kk;
    float theta = 1.0*iTime;
    int sph_count = SPHERECOUNT;

    //NOTE: these nested loops are very slow with increasing ammount of spheres. 
    //There are surely better ways to optimize this, but wanted to implement fractalike animation naively
    for(int i = 0; i < sph_count; i++)
    {
        vec3 sph_or = calcRotSphereXZ(1.0, sph_o, theta+(2.0*PI*(float(i)/float(sph_count))));
        cur_sdf = minSDF(cur_sdf,sdBurger(p, sph_or, r, kk,SPHERE2));

        //Rotating spheres
        float theta2 = 4.0*theta;
        for(int j = 0; j < sph_count; j++)
        {
            vec3 sph_orr = calcRotSphereXZ(ratio, sph_or, theta2+(2.0*PI*(float(j)/float(sph_count))));
            cur_sdf = minSDF(cur_sdf,sdBurger(p, sph_orr, rr, kkk,SPHERE3));

        }

    }


    //plane
    cur_sdf = minSDF(cur_sdf, vec2(p.y, PLANE)); //plane
    return cur_sdf;
}

vec2 rayMarch(vec3 ro, vec3 rd)
{
    float dO = 0.001;
    vec2 res;
    for(int i = 0; i<MAX_STEPS; i++)
    {
        vec3 p = ro + dO*rd; //Go along ray
        res = map(p); //Calc distance to surface, res.x
        if(res.x < SURF_DIST || dO>MAX_DIST) break;
        dO += res.x ;
    }
    if (dO > MAX_DIST) dO = -1.0;
    return vec2(dO, res.y);
}

float calcSoftShdw(vec3 ro, vec3 rd, float k)
{
    //more info: https://iquilezles.org/www/articles/rmshadows/rmshadows.htm
    //Circle circle intersection: https://mathworld.wolfram.com/Circle-CircleIntersection.html

    float res = 1.0; //1.0 if hard shadow
    float prev_d = 1e10; //previous closest distance to surface
    for(float t = 0.0; t<MAX_DIST;)
    {
        float d = map(ro + t*rd).x;
        if(d < 0.001){ return 0.0; }
        float y = d*d/(2.0*prev_d); //y: distance from curr pos to ray midpoint 
        float d_ys = sqrt(d*d-y*y); //d_ms: distance from y to closest surface
        float sh =  clamp(k*d_ys/max(0.0, t-y),0.0,1.0);

        res = min(res, sh*sh*(3.0-2.0*sh));//apply smoothstep (3-2x)*x^2
        prev_d = d;
        t += d;
    }

    return res;
}

vec3 calcNormal(vec3 p)
{
    //float d = map(p).x; //get distance to surface
    vec2 e = vec2(0.01, 0);//error bias for difference
    vec3 n = normalize(vec3( //find normal using small difference
            map(p+e.xyy).x-map(p-e.xyy).x,    
            map(p+e.yxy).x-map(p-e.yxy).x,  
            map(p+e.yyx).x-map(p-e.yyx).x
    ));
    return n;
}



vec3 render(vec3 ro, vec3 rd)
{
    //Raymarch pixel
    vec2 res = rayMarch(ro, rd); //distance to point p
    float d = res.x;
    //sky colour
    vec3 col = vec3(0.4, 0.6, 0.8) - 0.7*rd.y;
    if (d > 0.0){

        vec3 p = ro + d*rd;
        //colour and lights
        vec3 mat = vec3(0.0);
        if(res.y<=PLANE){ mat = vec3(0.017, 0.02, 0.03);}
        else if(res.y<=SPHERE1){ mat = vec3(0.15);}
        else if(res.y<=SPHERE2){ mat = vec3(0.05);}
        else if(res.y<=SPHERE3){ mat = vec3(0.11, 0.07, 0.15);}



        vec3 lpos = vec3(1.5, 1.5, -1.0);
        float speed = 1.0;
        //lpos.xz += vec2(sin(iTime*speed), cos(iTime*speed))*2.;

        //light & normal vectors
        vec3 l = normalize(lpos-0.0);
        vec3 n = calcNormal(p);

        //diffuse light
        float dif = clamp(dot(n,l), 0.0, 1.0);
        float sky_dif = clamp(0.5+0.5*dot(n,vec3(0.0, 1.0, 0.0)), 0.0, 1.0);
        float bounce_dif = clamp(0.2+0.2*dot(n,vec3(0.0, -1.0, 0.0)), 0.0, 1.0);
        float shdw = calcSoftShdw(p+n*0.05, l, 16.0); //step(rayMarch((p+n*SURF_DIST), l).x, 0.0);//hard shadows



        //specular light
        vec3 h = normalize(l + normalize((ro-p)));
        float spec = pow(max(0.0, dot(n,h)), 128.0);


        vec3 light_in = vec3(0.0);
        light_in += vec3(7.0, 5.8, 3.6)*dif*shdw;
        light_in += vec3(0.5, 0.8, 0.6)*sky_dif;
        light_in += vec3(0.6, 0.3, 0.1)*bounce_dif;

        col = mat*light_in;

        col += mat*spec*shdw;
    }
    return (col);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Camera/Ray origin
    vec3 ro = vec3(-cos(0.2*iTime), 2.7, -1.2*sin(0.2*iTime));

    vec3 avrg_col = vec3(0.0);
#if AA>1 //Supersampling AA
    for(int i = 0; i < AA; i++)
    for(int j = 0; j < AA; j++){
        //Pixel coordinates
        vec2 of = vec2(float(i),float(j)) / float(AA) - 0.5;
        vec2 uv = ((fragCoord+of)- .5*iResolution.xy)/iResolution.y;
#else

        vec2 uv = (fragCoord- .5*iResolution.xy)/iResolution.y;
#endif

        //Ray direction
        vec3 rd = normalize(vec3(uv.x, uv.y, 1));

        //Render scene
        vec3 col = render(ro,rd);



        avrg_col += col;

#if AA>1      
    }
    avrg_col /= float(AA*AA);//Apply AA
#endif
    //gamma
    avrg_col = pow(avrg_col, vec3(0.4545));

    fragColor = vec4(avrg_col,1.0);
}

