const int MAX_ITERATIONS = 200;

int julia(vec2 zz, vec2 c) 
{
  vec2 z = zz;
  float a = z.x;
  float b = z.y;
  int n = 0;
  while (n < MAX_ITERATIONS) {
    float aa = a*a;
    float bb = b*b;
    float twoab = 2.0 * a * b;

    a = aa - bb + c.x;
    b = twoab + c.y;

    if (a*a*a*b + b*b*b*a > 3.0) break;
    n++;
  }
  return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
  int juliaValue = julia((fragCoord.xy/iResolution.xy*4.0 - vec2(2, 2)) * vec2(iResolution.x / iResolution.y, 1), 
                          vec2(cos(iTime), sin(iTime/1.2)));

  float color = float(juliaValue) / float(MAX_ITERATIONS) * 7.0 + 0.1;
  fragColor = vec4(color*0.2, color-0.3, color, 1.0);
}

/*
    Returns hash in range [-1.0; 1.0].
*/
float signedSample1DHashUI32(uint value)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint enthropy0 = 12345u;
    const uint enthropy1 = 67890u;

    // Calculate hash.
	value += enthropy1;
	value *= 445593459u;
	value ^= enthropy0;

    // 2.0f / 4294967295.0f = 4.6566128730773926e-10

    return float(value * value * value) * 4.6566128730773926e-10 - 1.0f;
}

float PerlinNoise1D(float u)
{
    // Fractial part.
    float fractU = u - floor(u);

    // Integer part.
    u = floor(u);

    // Smoothstep.
    float tU = fractU * fractU * (3.0f - 2.0f * fractU);

    // Random gradient vectors.
    float gradient0 = signedSample1DHashUI32(uint(u));
    float gradient1 = signedSample1DHashUI32(uint(u + 1.0f));     

    // Distance vectors.
    float distance0 = fractU;
    float distance1 = fractU - 1.0f;

    // Dot products.
    float dotProduct0 = gradient0 * distance0;
    float dotProduct1 = gradient1 * distance1;

    // llinear filter.
    return
        dotProduct0 * (1.0f - tU) +
        dotProduct1 * tU;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0f;

    float _u;

    if (uv.x < 1.0f) { _u = uv.x + iMouse.x * 0.1f + iTime * 0.1f; }
    else { _u = uv.x + iMouse.x * 0.1f + iTime * 0.9f; }

    float gray = PerlinNoise1D(_u)      * 6.6f;
    gray += PerlinNoise1D(_u * 2.3f)    * 2.62f;
    gray += PerlinNoise1D(_u * 4.32f)   * 0.47f;
    gray += PerlinNoise1D(_u * 8.672f)  * 0.17f;
    gray += PerlinNoise1D(_u * 16.62f)  * 0.18f;
    gray += PerlinNoise1D(_u * 37.72f)  * 0.17f;
    gray += PerlinNoise1D(_u * 64.72f)  * 0.17f;
    gray += PerlinNoise1D(_u * 128.72f) * 0.07f;
    gray += PerlinNoise1D(_u * 256.72f) * 0.03f;
    gray += 4.6f;
    gray *= 0.20f;

    // Anti-aliasing.
    float lineWidth = 4.0/iResolution.y;
    gray = smoothstep(0.0f, lineWidth, abs(uv.y - gray));

    // Output to screen
    fragColor = vec4(vec3(gray),1.0);
}

//fast vec2 -> vec4 hash function for webgl
//github.com/victor-shepardson

//hash based on Blum, Blum & Shub 1986
//and Sharpe http://briansharpe.wordpress.com/2011/10/01/gpu-texture-free-noise/
float pi = 3.14159265359;
float bbsm = 1739.;
vec2 bbsopt(in vec2 a){
	return fract(a*a*(1./bbsm))*bbsm;
}
vec2 mod1024(in vec2 a){
	return fract(a*(1./1024.))*1024.;	
}
vec4 hash(in vec2 pos){
	vec2 a0 = mod1024(pos*pi);
	vec2 a1 = bbsopt(a0);
	vec2 a2 = a1.yx + bbsopt(a1);
	vec2 a3 = a2.yx + bbsopt(a2);
	return fract((a2.xyxy + a3.xxyy + a1.xyyx)*(1./bbsm));
}

vec4 fbm(in vec2 pos){
    float m = 1.;
    float n = 0.125;
    vec4 r = vec4(0.);
    for (int i=0; i<32; i++){
        r += hash(pos) * n;
        m *= r.x;
        n *= 0.9;
        pos *= 1.5;
    }
    return r;
}

