const float segments = 50.0; // Segments per unit, must be less than (1.0 / thickness), or else will glitch visually, because lines are wider than segments.
const float thickness = 0.01; // Line thickness
const float gridThickness = 0.001; // Grid line thickness
const float scale = 3.0;

float value(float x) {
    return fract(x) * fract(-x) * (fract(x * 0.5) > 0.5 ? -4.0 : 4.0); // I discovered a sine wave polynomial. I doubt it's any faster than sin() though, it's off by a few decimals.
}

mat2 rot(float r) {
    float s = sin(r), c = cos(r);
    return mat2(c, -s, s, c);
}

vec2 closestPointLine(vec2 a, vec2 b, vec2 p) {
    vec2 ab = b - a;
    float dist = clamp(dot(p - a, ab) / dot(ab, ab), 0.0, 1.0);
    if(dist <= 0.0) return a;
    if(dist >= 1.0) return b;
    return mix(a, b, dist);
}

float distToLine(vec2 a, vec2 b, vec2 p) {
    return length(p - closestPointLine(a, b, p));
}

vec2 point(int i, float unit) {
    float x = (float(i) + 0.5) * unit;
    return vec2(x, value(x));
}

void mainImage(out vec4 o, vec2 i) {
    float un = 1.0 / min(iResolution.x, iResolution.y);

    un *= scale;

    vec2 uv = (i - (0.5 * iResolution.xy)) * un;

    uv *= rot(radians(sin(radians(iTime * 22.5)) * 4.0));

    int ip = int(floor(uv.x * segments));
    float unit = 1.0 / segments;

    vec2 p0 = point(ip - 2, unit),
         p1 = point(ip - 1, unit),
         p2 = point(ip    , unit),
         p3 = point(ip + 1, unit),
         p4 = point(ip + 2, unit);

    float dist = min(min(distToLine(p0, p1, uv), distToLine(p1, p2, uv)),
                     min(distToLine(p2, p3, uv), distToLine(p3, p4, uv))) - thickness;

    float d = 1.0;

    vec2 f = fract(uv);
    d = mix(d, 0.75, min(1.0, smoothstep(un, -un, (1.0 - f.x) - gridThickness) + smoothstep(un, -un, f.x - gridThickness) + smoothstep(un, -un, (1.0 - f.y) - gridThickness) + smoothstep(un, -un, f.y - gridThickness)));

    d = mix(d, 0.0, smoothstep(un, -un, dist));

    d = mix(d, 0.0, smoothstep(0.0, 3.0 * scale, length(uv)));

    o = vec4(d, d, d, 1.0);
}

float unity_noise_randomValue (vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898, 78.233)))*43758.5453);
}

float unity_noise_interpolate (float a, float b, float t)
{
    return (1.0-t)*a + (t*b);
}

float unity_valueNoise (vec2 uv)
{
    vec2 i = floor(uv);
    vec2 f = fract(uv);
    f = f * f * (3.0 - 2.0 * f);

    uv = abs(fract(uv) - 0.5);
    vec2 c0 = i + vec2(0.0, 0.0);
    vec2 c1 = i + vec2(1.0, 0.0);
    vec2 c2 = i + vec2(0.0, 1.0);
    vec2 c3 = i + vec2(1.0, 1.0);
    float r0 = unity_noise_randomValue(c0);
    float r1 = unity_noise_randomValue(c1);
    float r2 = unity_noise_randomValue(c2);
    float r3 = unity_noise_randomValue(c3);

    float bottomOfGrid = unity_noise_interpolate(r0, r1, f.x);
    float topOfGrid = unity_noise_interpolate(r2, r3, f.x);
    float t = unity_noise_interpolate(bottomOfGrid, topOfGrid, f.y);
    return t;
}

// https://docs.unity3d.com/Packages/com.unity.shadergraph@10.4/manual/Simple-Noise-Node.html
void Unity_SimpleNoise_float(vec2 UV, float Scale, out float Out)
{
    float t = 0.0;

    float freq = pow(2.0, float(0));
    float amp = pow(0.5, float(3-0));
    t += unity_valueNoise(vec2(UV.x*Scale/freq, UV.y*Scale/freq))*amp;

    freq = pow(2.0, float(1));
    amp = pow(0.5, float(3-1));
    t += unity_valueNoise(vec2(UV.x*Scale/freq, UV.y*Scale/freq))*amp;

    freq = pow(2.0, float(2));
    amp = pow(0.5, float(3-2));
    t += unity_valueNoise(vec2(UV.x*Scale/freq, UV.y*Scale/freq))*amp;

    Out = t;
}

void Unity_Remap_float(float In, vec2 InMinMax, vec2 OutMinMax, out float Out)
{
    Out = OutMinMax.x + (In - InMinMax.x) * (OutMinMax.y - OutMinMax.x) / (InMinMax.y - InMinMax.x);
}

