void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // designed effect: start with a circle on grey background, and dissovle to whole black
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // params
    float threshold = sin(iTime - 3.14 * 1.5); // offset by 3/2 PI
    vec4 borderColor = vec4(1.0, 1.0, 0.0, 1.0);

    vec3 bgColor = vec3(0.0, 0.0, 0.0);
    vec4 fgColor = vec4(rgb(210.0, 222.0, 228.0), 1.0);
    // params - simple shape
    vec2 uv_n = uv;
    float ratio = iResolution.x / iResolution.y;
    uv_n.x *= ratio;
    fgColor = length(vec2(0.5 * ratio, 0.5) - uv_n) < 0.3 ? vec4(rgb(225.0, 95.0, 60.0), 1.0) : fgColor;
    // params - simple shape end

    float noiseScale = 43.44;
    // params end

    float noise;
    vec3 col = fgColor.rgb;
    Unity_SimpleNoise_float(uv, noiseScale, noise); // generate noise
    Unity_Remap_float(threshold, vec2(-1.0, 1.0), vec2(0.0, 1), threshold);
    float alpha = step(noise, threshold); // alpha clip threshold
    col += borderColor.rgb * step(threshold - noise, 0.05); // add border
    fragColor = mix(vec4(bgColor, 1.0), vec4(col, 1.0), alpha); // blend
}

#define M_PI 3.1415926535897932384626433832795

vec3 to_polar(vec3 pos)
{
    float x = atan( pos.x, pos.z );
    float y = atan( pos.y, length(pos.xz));
    return vec3(x, y, length(pos));
}

vec4 quaternion_multiply(vec4 a, vec4 b)
{
    float real_p = a.x*b.x - dot(a.yzw, b.yzw);
    vec3 imag_p = a.x * b.yzw + b.x * a.yzw + cross(a.yzw, b.yzw);
    return vec4(real_p, imag_p);
}

vec4 quaternion_inverse(vec4 a)
{
    float len = length(a);
    return a * vec4(1.0, vec3(-1.0)) / (len * len);
}

vec4 rotation_quaternion(vec3 axis, float angle)
{
    return vec4(cos(angle/2.0), sin(angle/2.0) * axis);
}

vec3 rotate_around_axis(vec3 v, vec3 axis, float angle)
{
    vec4 Q = rotation_quaternion(axis, angle);
    vec4 Q_inv = quaternion_inverse(Q);
    return quaternion_multiply(quaternion_multiply(Q, vec4(0.0, v)), Q_inv).yzw;
}

float noise(float pos)
{
    //Picking the low bits, which are more random
    return mod(sin(pos) * 10000.0, 1.0);
}

float noise(vec3 pos)
{
    float key = pos.x * 111.0 + pos.y * 788.0 + pos.z * 827.0;
    return noise(key);
}


mat3 camera_rotation( vec3 look_at )
{
    //look_at = normalize(look_at);
    vec3 x = vec3( look_at.z, 0, -look_at.x ); 
    vec3 y = cross( look_at, x );
    return mat3( x, y, look_at );
}

#define AccuracyDropoff 10.0
#define MaxCastLen 27.0
#define HitDistance 0.05
#define FocalLength 2.0
#define DistanceMultiplier 1.0
#define DistanceNoise 0.2
#define GlowRadius 0.05
#define InnerRadius 7.95
#define OuterRadius 8.0

#define TimeStep 2.0
#define SpokeCount 5.0

float model_radius_fcn(float time)
{
   return mix(InnerRadius, 0.0, clamp(time  / TimeStep - 5.0, 0.0, 1.0));
} 

float spoke_model(vec3 pos, float my_time)
{
    vec3 polar = to_polar(pos);
    vec2 xy = polar.xy / (M_PI * 2.0);
    //Adjust spoke position away from the poles
    xy.y /= 0.75;
    xy += 0.5;

    vec2 xy2 = fract(xy * SpokeCount) - 0.5;

    float dist_polar = length(xy2) * polar.z;
    float inner_radius = model_radius_fcn(iTime);
    return max(max(dist_polar - 0.25, polar.z - 8.0), inner_radius - polar.z ) * 0.4;  
}

float rotated_model(vec3 pos, float my_time)
{
    float t = floor(my_time / TimeStep);
    float angle = fract(my_time / TimeStep) * 2.0 * M_PI;
    vec3 axis =  t < 1.0  ?
         vec3( 0.0, 1.0, 0.0) :
         vec3(noise(t + 0.2) - 0.5,
              noise(t + 0.7) - 0.5,
              noise(t + 1.1) - 0.5);
    axis = normalize(axis);
    return spoke_model(rotate_around_axis(pos, axis, angle), my_time);
}

float dist_model(vec3 pos, float my_time)
{
    float len = length(pos);
    //Early out
    if( len > (OuterRadius + 0.5))
    {
        return len - OuterRadius;
    }
    return rotated_model(pos, (my_time - 0.3 * len) * 0.25);
}

float castRay( vec3 vStart, vec3 vDir, float my_time )
{
    float fCastLen = 0.0;
    float fMinDist = 100.0;
    vec3 vHit = vStart + vDir * 0.03;
    while( fCastLen < MaxCastLen )
    {
        float fDistance = dist_model( vHit, my_time );
        fMinDist = min(fMinDist, fDistance);

        if( fDistance < HitDistance)
        {
            return fMinDist;
        }

        fDistance *= max(1.0, fCastLen / AccuracyDropoff);
#ifdef DistanceNoise
        fDistance *= ( 1.0 - noise(vHit) * DistanceNoise);
#endif
        fCastLen += fDistance;
        vHit = vDir * fCastLen + vStart;
    }

    return fMinDist;
}

vec3 glow( vec3 dist )
{
    return pow(abs(dist - 0.1) / GlowRadius, vec3(-2.0));
}

vec4 rayTraceMain( vec2 fragCoord )
{
    float dt = 0.1;
    vec2 position = fragCoord * -2.0 + 1.0;
    vec3 cameraPos = vec3( 6.0, 1.0, -21.0 );
    vec3 rayDir = normalize( vec3( position, 2.0 ) );
    float dist_r = castRay( cameraPos, rayDir, iTime );
    float dist_g = castRay( cameraPos, rayDir, iTime + dt);
    float dist_b = castRay( cameraPos, rayDir, iTime + dt * 2.0);

    return vec4(glow(vec3(dist_r, dist_g, dist_b)), 1.0);
}

