#define MOD3 vec3(.1031, .11369, .13787)

vec3 hash33(vec3 p3)
{
    p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz + 19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y) * p3.z, (p3.x + p3.z) * p3.y, (p3.y + p3.z) * p3.x));
}

float snoise(vec3 p)
{
    const float K1 = 0.333333333;
    const float K2 = 0.166666667;

    vec3 i = floor(p + (p.x + p.y + p.z) * K1);
    vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);

    vec3 e = step(vec3(0.0), d0 - d0.yzx);
    vec3 i1 = e * (1.0 - e.zxy);
    vec3 i2 = 1.0 - e.zxy * (1.0 - e);

    vec3 d1 = d0 - (i1 - 1.0 * K2);
    vec3 d2 = d0 - (i2 - 2.0 * K2);
    vec3 d3 = d0 - (1.0 - 3.0 * K2);

    vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
    vec4 n = h * h * h * h * vec4(dot(d0, hash33(i)), dot(d1, hash33(i + i1)), dot(d2, hash33(i + i2)), dot(d3, hash33(i + 1.0)));

    return dot(vec4(31.316), n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 ouv = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.x; 

    float l = length(uv);

    float size = .15;
    float width = .005;

    float v = SS(l,  size) * SS(size -width, l);

    // float v = (.1 / smoothstep(l, l-.1, size));

    float a = sin(atan(uv.y, uv.x)*3.);

    float sv = tx(vec2(0.5,0.75)).r;
    float mf = abs(snoise(vec3(a*.01,a, iTime)))*.1;
    float v2 = SS(l - mf - width,  size) * SS(size-width*2. - mf, l);
    float v22 = SS(l - mf - width,  size); // + SS(size-width*10. - mf, l);
    float vs = 1.025;
    float v3 = SS(l*vs - mf - width,  size) * SS(size-width*2. - mf, l*vs);

    vec3 col = vec3(0.0 + SS(abs(fract((uv.x + uv.y)*iResolution.x*.1)-.5), .15)*.1);

    col = mix(col, vec3(.25).rgb, v2);
    col = mix(col, vec3(.0).rgb, v3);
    col = mix(col, vec3(.7).rgb, v22-v2);
    col = mix(col, vec3(1.), v);            

    // vignetting	
	col *= 0.5 + 0.5*pow( 16.0*ouv.x*ouv.y*(1.0-ouv.x)*(1.0-ouv.y), 0.1 );
    col = clamp(col,0.0,1.0);
    col = col*0.6 + 0.4*col*col*(3.0-2.0*col) + vec3(0.0,0.0,0.04);


    fragColor = vec4(col, 1.);
}

