    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {

            uv.y /= scale;
            uv = (uv+vec2(s1+uv.x-uv.y)/scale);
            //uv.y -= col.x/16.;
            uv.x *= -scale;

            uv = fract(uv.yx);
