#define PI (atan(1.)*4.)

void rot(inout vec3 p,vec3 a,float t)
{
	a=normalize(a);
	p=mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a);
}

// https://www.shadertoy.com/view/MsKGzw
vec3 fold(vec3 p, float t)
{
    vec3 n=vec3(-.5,-cos(PI/t),0);
    n.z=sqrt(1.-dot(n,n));         // normalize
    for(float i=0.; i<t; i++){
        p.xy=abs(p.xy);
        p-=2.*min(0.,dot(p,n))*n;
    }
    return p;
}

float map(vec3 p)
{
	rot(p,vec3(cos(iTime*.3),sin(iTime*.5),.5*sin(iTime*.2)),iTime*.7);
    float t = float[](3.,4.,5.)[int(fract(iTime/8.)*3.)];
    p = fold(p,t);
    p.z-=1.;
    vec3 n=vec3(-.5,-cos(PI/t),0);
    n.z=sqrt(1.-dot(n,n));         // normalize
    return float[](
            dot(p, normalize(vec3(n.z,0,-n.x))),
            dot(p, normalize(vec3(0,n.z,-n.y))),
            length(p.xz)-.1,
            length(p.yz)-.1
        )[int(fract(iTime/2.5)*4.)];
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<70;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

vec3 doColor(vec3 p)
{
    return vec3(.7,.5,.3)+cos(p*2.)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,-5);
    vec3 rd = normalize(vec3(uv,3));
    vec3 col= vec3(0);
	const float maxd=50.;
    float t=march(ro,rd,0.,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(2,5,2);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.1,1.);
        col*=max(dif,0.);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.8;
        col*=max(.5+.5*n.y,.1);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        col+=vec3(.8,.6,.2)*pow(clamp(dot(reflect(rd,n),li),0.,1.),10.);
    }
    col=pow(col,vec3(1./2.2));
    fragColor.xyz=col;
}

vec2 Rotate(vec2 uv,float t){
	return vec2(uv.x*cos(t)-uv.y*sin(t),uv.y*cos(t)+uv.x*sin(t));
}

float R21(vec2 p) 
{
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

float Line(vec2 p, vec2 o, vec2 b, float sw, float ew){
	float d = distance(o, b);
	vec2  n = normalize(b - o);
	vec2 l = vec2( max(abs(dot(p - o, n.yx * vec2(-1.0, 1.0))), 0.0),
	               max(abs(dot(p - o, n) - d * 0.5) - d * 0.5, 0.0));
	return smoothstep( mix(sw, ew, 1.-distance(b,p)/d) , 0., l.x+l.y);
}

vec3 Comet(vec2 p)
{
    float modu = 4.;        
    float endPointY = -.1; 
    vec2 cmtVel = mod(iTime/modu+modu*.5, 2.) > 1. ? vec2(2., 1.4)*.5 : vec2(-2., 1.4)*.5;
    vec2 cmtLen = vec2(.25)*cmtVel;

    vec2 cmtPt = 1. - mod(iTime*cmtVel, modu);
    cmtPt.x +=1.;

    vec2 cmtStartPt, cmtEndPt;

    if(cmtPt.y < endPointY) {
        cmtEndPt   = cmtPt + cmtLen;
        if(cmtEndPt.y > endPointY) 
            cmtStartPt = vec2(cmtPt.x + cmtLen.x*((endPointY - cmtPt.y)/cmtLen.y), endPointY);
        else
            return vec3(.0);
    }
    else {
        cmtStartPt = cmtPt;
        cmtEndPt = cmtStartPt+cmtLen; 
    }

    float bright = clamp(smoothstep(-.2,.65,distance(cmtStartPt, cmtEndPt)),0.,1.);

    vec2 dlt = vec2(.003) * cmtVel;

    float q = clamp( (p.y+.2)*2., 0., 1.);

    return  vec3(bright * .75 * (smoothstep(0.993, 0.999, 1. - length(p - cmtStartPt)) + 
             Line(p, cmtStartPt, cmtStartPt+vec2(.06)*cmtVel,  0.009, 0.003)) +
             vec3(1., .7, .2) * .33 * Line(p, cmtStartPt,         cmtEndPt,        0.003, .0003) +          
             vec3(1., .5, .1) * .33 * Line(p, cmtStartPt+dlt,     cmtEndPt+dlt*2., 0.002 ,.0002) +         
             vec3(1., .3, .0) * .33 * Line(p, cmtStartPt+dlt+dlt, cmtEndPt+dlt*4., 0.001, .0001)) 
             * (bright) * q;
}


float Circle(vec2 uv, vec2 p) {
    float dist = length(uv-p);
    float mask = smoothstep(.3, .29, dist);
    return mask;
}

vec3 Moon(vec2 uv) {
    uv *= 2.;
    vec3 col = vec3(0.);

    vec2 p = vec2(1.2, -.5);

    col += Circle(uv, vec2(0.)-p);
    col -= Circle(uv, vec2(.08)-p);
    return col;
}

float Star(vec2 gv, float dist){
    gv *= 1.9;

    float brigthness = .005;
    brigthness *= sin(fract(iTime)*10.)*.5+.5;

    float star = brigthness/dist;

    float rays = max(0., 1.-abs(gv.x*gv.y*1000.));
    star += rays;

    gv *= Rotate(gv, .8);

    rays = max(0., 1.-abs(gv.x*gv.y*1000000.));
    star += rays;

    star *= smoothstep(.2, .1, dist);

    return star * .3;
}

vec3 Stars(vec2 uv) {
    uv *= 6.;

    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);

    vec3 col = vec3(0.);

    for (float y = -1.; y <= 1.; y ++)
    {
        for(float x = -1.; x <= 1.; x++)
        {
            vec2 offset = vec2(x,y);
            float n = R21(id + offset);

            float dist = length(gv-offset-n);

            //float blink = sin(fract(iTime)*10.+ n)*.5+.5;
            col += Star(gv-offset-n, dist);// * blink;
        }
    }

    return col;
}

