    float lhlw=HLW/2.;//local HLW
    vec3 mesh=vec3(SS(lhlw,lhlw-.005,abs(uv.x-round(uv.x/msp)*msp)));
    mesh+=vec3(SS(lhlw,lhlw-.005,abs(uv.y-round(uv.y/msp)*msp)));
    col+=mesh*.5;
    col=mix(col,vec3(0.,1.,0.),line(uv,vec2(0.),vec2(1.,0.)));
    col=mix(col,vec3(0.,1.,0.),line(uv,vec2(0.),vec2(0.,1.)));
    return col;
}
