struct Intersect {
    vec3 nor;
    vec2 uv;
    float t;
};

// The heart of the truchet pattern...
const vec2 eta = vec2(-0.5, sqrt(0.75));
void updateRoot(inout vec2 old, in vec2 new) { if (abs(new.y) < abs(old.y)) old = new; }
int iTorus(in vec3 ro, in vec3 rd, in float radius, in float thickness, inout vec4 roots) {
    float rr = radius * radius, rr4 = 4.0 * rr;
    float qa = dot(rd, rd), qb = 2.0 * dot(ro, rd), qc = dot(ro, ro) + rr - thickness * thickness;

    float a = qa * qa;
    float b = 2.0 * qa * qb / a;
    float c = (2.0 * qa * qc + qb * qb - rr4 * dot(rd.xz, rd.xz)) / a;
    float d = (2.0 * qb * qc - 2.0 * rr4 * dot(ro.xz, rd.xz)) / a;
    float e = (qc * qc - rr4 * dot(ro.xz, ro.xz)) / a;

    // Quartic solver based on https://www.maa.org/sites/default/files/pdf/upload_library/22/Ford/auckly29.pdf
    float bb = b * b;
    float p = c - 3.0 * bb / 8.0;
    float q = d - 0.5 * b * c + 0.125 * bb * b;
    float r = e - 0.25 * b * d + 0.0625 * bb * c - 3.0 * bb * bb / 256.0;

    // Factoring the quartic into two quadratics using the solution to a cubic
    float ra = 2.0 * p;
    float rb = p * p - 4.0 * r;

    float d0 = ra * ra - 3.0 * rb;
    float d1 = (2.0 * ra * ra - 9.0 * rb) * ra - 27.0 * q * q;
    float h = d1 * d1 - 4.0 * d0 * d0 * d0, j = sqrt(abs(h));

    vec2 C = h < 0.0 ? vec2(d1, j) : vec2(d1 + j, 0.0);
    if (abs(C.x) < 1e-3 && abs(C.y) < 1e-3) C = h < 0.0 ? vec2(d1, -j) : vec2(d1 - j, 0.0); // Switch sign if C=0
    vec2 polar = vec2(atan(C.y, C.x) / 3.0, pow(0.25 * dot(C, C), 1.0 / 6.0)); // ---v
    C = vec2(cos(polar.x), sin(polar.x)) * polar.y; // De Moivre's formula <---------<

    vec2 root = vec2(1e20);
    vec2 m1 = mat2(C, -C.y, C.x) * eta, m2 = mat2(C, C.y, -C.x) * eta; // Complex multiplication using matrices
    updateRoot(root, (vec2(-d0, d0) *  C / dot( C,  C) - vec2( C.x + ra,  C.y)) / 3.0);
    updateRoot(root, (vec2(-d0, d0) * m1 / dot(m1, m1) - vec2(m1.x + ra, m1.y)) / 3.0);
    updateRoot(root, (vec2(-d0, d0) * m2 / dot(m2, m2) - vec2(m2.x + ra, m2.y)) / 3.0);

    // Solve for the roots of the factors
    int n = 0; // Root counter
    float lambda = root.x;
    if (lambda < 0.0) return 0;
    float sqrtLambda = sqrt(lambda);

    float pLambda = p + lambda, qLambda = q / sqrtLambda;
    float offs = 0.25 * b;

    float foo = lambda - 2.0 * (pLambda + qLambda);
    float bar = lambda - 2.0 * (pLambda - qLambda);

    if (foo >= 0.0) {
        roots.xy = (vec2(1.0, -1.0) * sqrt(foo) + sqrtLambda) * 0.5 - offs;
        n += 2;
    }

    if (bar >= 0.0) {
        vec2 others = (vec2(1.0, -1.0) * sqrt(bar) - sqrtLambda) * 0.5 - offs;
        if (n > 0) roots.zw = others;
        else roots.xy = others;
        n += 2;
    }

    return n;
}

// align 0: x aligned, align 1: y aligned, align 2: z aligned
void addTorus(inout Intersect isect, in vec3 ro, in vec3 rd, in float radius, in float thickness, in vec3 pos, in int align) {
    if (align == 0) ro = ro.yxz, rd = rd.yxz, pos = pos.yxz;
    if (align == 2) ro = ro.xzy, rd = rd.xzy, pos = pos.xzy;

    vec4 roots;
    int nroots = iTorus(ro - pos, rd, radius, thickness, roots);

    // Optimizing things to be out of the loop hasn't worked yet...
    for (int idx=0; idx < nroots; idx++) { // All intersections need to be checked against the cell boundaries
        vec3 hitPos = ro + rd * roots[idx];
        if (roots[idx] > 0.0 && (isect.t > 0.0 ? roots[idx] < isect.t : true) &&
            all(lessThan(abs(hitPos), vec3(0.5)))) {
            hitPos -= pos;
            float r = length(hitPos.xz);
            isect.nor = normalize(vec3(hitPos.xz - normalize(hitPos.xz) * radius, hitPos.y)).xzy;
            isect.nor = align == 0 ? isect.nor.yxz : align == 1 ? isect.nor : isect.nor.xzy;
            isect.uv = vec2(atan(hitPos.z, hitPos.x), atan(hitPos.y, r - radius));
            isect.t = roots[idx];
        }
    }
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
vec3 Hash33(in vec3 p) {
	p = fract(p * vec3(0.1031, 0.103, 0.0973));
    p += dot(p, p.yxz + 33.33);
    return fract((p.xxy + p.yxx) * p.zyx);
}

float sign2(in float x) { return x < 0.0 ? -1.0 : 1.0; } // Nonzero sign
Intersect gridCell(in vec3 ro, in vec3 rd, in vec3 pos) {
    ro -= pos;

    vec3 rand = Hash33(pos) - 0.5;
    vec3 flip = vec3(sign2(rand.x), sign2(rand.y), sign2(rand.z));

    // Scramble the cell
    ro *= flip;
    rd *= flip;

    // Arrange three tori connecting the three pairs of faces
    Intersect isect = Intersect(vec3(0.0), vec2(0.0), -1.0);
    addTorus(isect, ro, rd, 0.5, 0.05, vec3( 0.0, -0.5,  0.5), 0);
    addTorus(isect, ro, rd, 0.5, 0.05, vec3( 0.5,  0.0, -0.5), 1);
    addTorus(isect, ro, rd, 0.5, 0.05, vec3(-0.5,  0.5,  0.0), 2);

    // Flip normal back into world space
    isect.nor *= flip;

    return isect;
}

// Learning from https://github.com/cgyurgyik/fast-voxel-traversal-algorithm/blob/master/overview/FastVoxelTraversalOverview.md
Intersect traverse(vec3 ro, vec3 rd) {
    vec3 invRd = 1.0 / rd, octant = sign(rd);
    vec3 voxel = floor(ro) + 0.5;
    vec3 corner = 0.5 * octant - ro;
    for (int iter=0; iter < 15; iter++) {
        Intersect cell = gridCell(ro, rd, voxel);
        if (cell.t > 0.0) return cell;
        vec3 tMax = (voxel + corner) * invRd; // Intersection with the cell boundaries
        voxel += step(0.0, min(tMax.x, min(tMax.y, tMax.z)) - tMax) * octant; // Step in the direction of the nearest one
    }

    return Intersect(vec3(0.0), vec2(0.0), -1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    float time = iTime;

    vec2 mouse = ivec2(iMouse) == ivec2(0) ? vec2(0.0) : (iMouse.xy - center) / iResolution.y * 3.14;
    vec2 uv = (fragCoord - center) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, -iTime);
    vec3 rd = normalize(vec3(uv, -1.0));

    // Rotate with mouse
    float cy = cos(mouse.x), sy = sin(mouse.x);
    float cp = cos(mouse.y), sp = sin(mouse.y);

    rd.yz *= mat2(cp, -sp, sp, cp);
    rd.xz *= mat2(cy, -sy, sy, cy);

    Intersect hit = traverse(ro, rd);
    //Intersect hit = gridCell(ro, rd, vec3(0.0)); // Grid cell debugging

    // Render
    if (hit.t > 0.0) {
        float diff = abs(dot(hit.nor, normalize(mix(normalize(vec3(-1.0, 1.0, 1.0)), hit.nor, 0.5))));
        float checker = 0.5 + 0.5 * mod(floor(hit.uv.x / 6.28 * 32.0) + floor(hit.uv.y / 3.14 * 3.0), 2.0);
        fragColor.rgb = mix(vec3(checker, 0.0, 0.0), vec3(0.0), 1.0 - exp(-hit.t * 0.15)) / max(1.0, 0.125 * hit.t); // Fog
        fragColor.rgb *= diff;
    }
}

