        O = MIX(O, c * CV(vec3(sin(iTime * 0.50 + i / 4.5) * (iResolution.x / 2.0 - 60.0) + iResolution.x / 2.0,
			                   sin(iTime * 0.73 + i / 3.0) * (iResolution.y / 2.0 - 60.0) + iResolution.y / 2.0,
			                   0.0),
